css:
@-moz-document domain(pastebin.com) {
  body {
    background: url(http://fc04.deviantart.net/fs70/f/2014/224/b/7/mega_altaria_by_dashinghero-d7uxwrn.png);
    color: #181818;
    font-family: segoe ui,trebuchet MS,Lucida Sans Unicode,Lucida Sans,sans-serif;
    font-size: 1em;
    line-height: 1.5em;
    margin: 0px;
    padding: 0px;
  }
  
  #monster_frame {
    background-color: #FFF;
    box-shadow: 0px 0px 10px 5px #CCC;
    border: 1px solid #CCC;
    margin: 0px;
    opacity: 0.75;
    border-radius: 10px;
  }
  
 .button1 {
    background: #9c9c9c;
    background: -webkit-gradient(linear, left top, left bottom, from(#f2f2f2), to(#9c9c9c));
    background: -webkit-linear-gradient(top, #f2f2f2, #9c9c9c);
    background: -moz-linear-gradient(top, #f2f2f2, #9c9c9c);
    background: -ms-linear-gradient(top, #f2f2f2, #9c9c9c);
    background: -o-linear-gradient(top, #f2f2f2, #9c9c9c);
    border: 1px solid #BBB;
    border-radius: 3px;
    cursor: pointer;
    font-size: 12px;
    margin: 0px;
    padding: 4px 10px;
  }
  
  .button1:hover {
    background: #adadad;
    border-color: #776EFD;
    color: #000;
  }
  
  input.button1 {
    border: 1px solid #437486;
    background: -webkit-gradient(linear, left top, left bottom, from(#59a7c3), to(#3490b2));
    background: -webkit-linear-gradient(top, #59a7c3, #3490b2);
    background: -moz-linear-gradient(top, #59a7c3, #3490b2);
    background: -ms-linear-gradient(top, #59a7c3, #3490b2);
    background: -o-linear-gradient(top, #59a7c3, #3490b2);
    color: #FFF;
  }
  
  input.button1:hover {
    background: #59a7c3;
    color: #FFF;
    border: 1px solid #437486;
  }
  
  a.button1 {
    border: 1px solid #437486;
    background: -webkit-gradient(linear, left top, left bottom, from(#59a7c3), to(#3490b2));
    background: -webkit-linear-gradient(top, #59a7c3, #3490b2);
    background: -moz-linear-gradient(top, #59a7c3, #3490b2);
    background: -ms-linear-gradient(top, #59a7c3, #3490b2);
    background: -o-linear-gradient(top, #59a7c3, #3490b2);
    color: #FFF;
  }
  
  a.button1:hover {
    background: #59a7c3;
    color: #FFF;
    border: 1px solid #437486;    
  }
  
  a {
    color: #59A7C3;
    text-decoration: none;
  }
  
  a:hover {
    color: #3490B2;
    text-decoration: none;
  }
  
  .search_input {
    background: #BDE0ED;
    border: 1px solid #CCC;
    border-radius: 2px;
    font-family: segoe ui,trebuchet MS,Lucida Sans Unicode,Lucida Sans,sans-serif;
    vertical-align: top;
    width: 180px;
    margin: 0px;
    padding: 6px;
  }
  
  #notice {
    background-color: #BDE0ED;
    color: #666;
  }
  #header a:hover {
    color: #BDE0ED;
  }
  
  .content_right_title a:hover {
    color: #3490B2;
  }
  
  .search_button {
    background: url("http://www.davis.k12.ut.us/cms/lib09/UT01001306/Centricity/Domain/11290/search.png") no-repeat;
    background-size: 30px;
    opacity: 0.7;
  }
  
  .search_button:hover {
    background: url("http://www.davis.k12.ut.us/cms/lib09/UT01001306/Centricity/Domain/11290/search.png") no-repeat;
    background-size: 30px;
    opacity: 1;
  }
  
  #footer_links a:hover, #footer_top a:hover, #footer_right a:hover {
    color: #3490B2;
  }
  
  input.post_input, select.post_select {
    background:#BDE0ED;
    border-radius: 3px;
  }
  
  #code_buttons {
    background:#BDE0ED;
    border-radius: 3px;
  }
  
  .textarea_border {
    background:#59A7C3;
    border: 1px solid #DDD;
    margin: 10px 0px;
    padding: 5px;
  }
  
  textarea.paste_textarea {
    background:#BDE0ED;
  }

  .form_login_frame {
    background: rgba(189, 224, 237, 0.7);
    padding-right: 20px;
  }
  
  .right_menu span {
    font-style: italic;
  }
  
  .footer_top_title {
    background: rgba(189, 224, 237, 0.7);
  }

  .paste_box_line2 {
    font-style: italic;
  }
  
  .textarea.message_textarea {
    background: rgba(189, 224, 237, 0.7);
  }
  
  .fieldset.login_field {
    background: rgba(189, 224, 237, 0.7);
  }
  
  textarea.paste_code {
    background: rgba(189, 224, 237, 0.7);
  }
  
  .html4strict {
    background: rgba(189, 224, 237, 0.4);
    color: #979797;
  }
  
  .css {
    background: rgba(189, 224, 237, 0.4);
    color: #979797;
  }
  
  .javascript {
    background: rgba(189, 224, 237, 0.4);
    color: #979797;
  }
  
  /*javascript*/
  
  .javascript .st0 {
    color: #6AB545;
  }
  
  .javascript .br0 {
    color: #000;
  }
  
  .javascript .nu0 {
    color: #B156C3;
  }
  
  .javascript .me1 {
      color: #298F9F;
  }
  
  .javascript .kw1 {
    color: #FF2BAB;
    font-weight: bold;
  }
  
  .javascript .es0 {
    color: #B156C3;
    font-weight: bold;
  }
  
  
  /*HTML*/
  
  .html4strict .kw2 {
    color: #FF2BAB;
    font-weight: bold;
  }
  
  .html4strict .sc2 {
    color: #000;
  }
  
  .html4strict .sy0 {
    color: #575757;
  }
  
  .html4strict .st0 {
    color: #6AB545;
  }
  
  .html4strict .sc1, .html4strict .sc0 {
    color: #B156C3;
  }
  
  .html4strict .kw3 {
    color: #298F9F;
  }
  
  
  /*CSS*/
  
  .css .re1 {
    color: #FF2BAB;
    font-weight: bold;
  }
  
  .css .co2, .css .st0 {
    color: #6AB545;
  }
  
  .css .sy0, .css .br0 {
    color: #000;
  }
  
  .css .kw1 {
    color: #298F9F;
    font-weight: bold;
  }
  
  .css .kw2, .css .re3, .css .re0 {
    color: #B156C3;
  }
  
  .css .re2 {
    color: #616161;
  }
  
  
  
  #steadfast, #steadfast:hover {
    opacity:0;
    cursor:default;
  }
  
   * {
        cursor: url("http://i.imgur.com/9aYuxXY.png?1"), default !important
    }

}
