css:
@-moz-document domain("8tracks.com") {
/* hides some annoying things */
#mix_interactions_bottom, #share_button, .facebook_like, .mobile_links, #image_controls, .mobile_apps, .bubble.social_suggestions, .sample {display: none;} 

/* white fonts */
a, a:link, #tracks_count, .featherweight, .turquoise, .displaymode, #q, #playlist, .i-search:before, .user_about.profile.clear.favable.vcard, .track.now_playing.open, .description, .header.skip, p, .disc, .gallery, .answer, .placeholder {color: white !important;}

/* link hovering color */
a:hover, a.mix.suggestion.propername:hover, .turquoise:hover, a.follow:hover, a.active {color: white !important; background-color: #2d2d2d !important;}

/* white fonts */
h1, h4, h6, h3, h2 {color: white !important; background: transparent !important; }

/* white fonts and black backgrounds*/
.date, .length, #mix_description, .mix-stats, #logged_in_dropdown, #back_to_top, a.tag, .popup, .add_to_collections, .content, #new_collection_name, #new_collection_description, #create_collection_button, .explanation, .reason, .mixes, .result.mtag, .tag, .edit_user, label, .sidebar, #social_suggestions, .vcard, .black_tooltip, .interactbox, .turquoise_bt {color: white !important; background: black !important;}

/* black fonts and backgrounds */
#thin_header, #wrapper, #content, #belly_container, #search, #header_right, .grid_square, #mix_wrapper, #mix_interactions_bottom {color: black !important; background: black !important;}

/* black background */
.displaymode, #footer, a.follow.active, #page_description, .select2-drop, .i-x {background: black !important;}

/* faved track color */
a.fav.active, a.fav.active.just_clicked:hover, .feed-controllers .active .bullet {color: #0099CC !important; background: transparent !important;}

/* like button color: one beside mix cover photo */
#like_button.active {color: #9f595a !important;}

/* like button color: one at bottom */
.mix_like.player_button.active {color: #9f595a !important;}

.mix_like {background: transparent !important;}

/* make some things black */
#mix_interactions:after, #mix_interactions:before {border-left: black !important; background: black !important;}

/* white fonts and black backgrounds */
.q, input.mediumText, textarea.mediumText, select#user_country {color: white !important; background: black !important;}

/* follow button colors */
a.follow {background: black; border-color: white !important;}

/* transparent things to fix colors */
a.mix.suggestion.propername, a.mix_url, a.feed-filter.active {background: transparent !important;}

/* gray fonts */
.track_metadata, .detail {color: #746E6E !important;}

/* make something black */
#facebox {color: black !important; border: black !important;}

/* hover color */
.my_collection:hover, .mix:hover, .result.mtag.selected:hover, a.suggestion.propername:hover, .menu-item:hover, .inverted_counter {background: #2D2D2D !important;}

.likes_count:hover {color: #2D2D2D !important;}

/* make something black */
.black_tooltip.black_tooltip_up.black_tooltip_solid {color: white !important; background: black !important;}

/* white font, black background */
.mix.with_reason, .white_button, .autocomplete {color: white !important; background: black !important;}

/* gray font */
.mixset_entries, #news_feed {color: #999 !important;}

/* white border somewhere */
#big_search {border: white !important;}

/* colors for search sort options */
#sort_select {color: white !important; background: black !important;}

/* something with a white border */
input.mediumText, textarea.mediumText {border: 1px solid white !important;}

/* something with a black border */
.q {border: 1px solid white !important;}

/* white font in comment writing area */
textarea, .text.prompt:hover, .text.prompt:focus {color: white !important;}

/* enumerate tracks */
li.track {list-style-type: decimal; list-style-position: inside;}

/* black font */
.unread_notifications.notifications_counter {color: black;}

/* scrollbar */
::-webkit-scrollbar-thumb:vertical { 
background-color: #999999;
height:auto;
}
::-webkit-scrollbar-thumb:horizontal { 
background-color: #999999;
height:7px;
}
::-webkit-scrollbar { 
height:7px;
width:7px; 
background-color: black;
}

/* black bg-c */
.errors, body, .result-tags, .result-artist_tags {background-color: black !important;}


}
