css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("voat.co") {

  /*Header*/
  #header,#sr-header-area,#sr-more-link,#header-container{
    background-color: #222222 !important;
  }
  
  /*Header Account*/
  #header-account{
    background: transparent !important;
  }
  
  .width-clip,.sr-list,.whoaSubscriptionMenu,.sr-bar{
    color: white !important;
  }
  
  .md{
    background-color: #373737 !important;
    color: #FFFFFF !important;
  }
  
  /*General Text Colors*/
  .tagline,.title{
    color: #FFFFFF !important;
  }
  
  /*Block Quotes*/
  blockquote{
    background-color: #121212 !important;
    background: #121212 !important;
  }
  
  /*Featured Sub*/
  .alert-featuredsub{
    background-color: #373737 !important;
    background: #373737 !important;
    color: #FFFFFF !important;
  }
  
  /*First Time User Prompt*/
  .alert-firsttimevisitorwelcome{
    background-color: #373737 !important;
    color: #FFFFFF !important;
  }
  
   /*Header Account*/
  element{  
  background-color: #373737 !important;
  }
  
  body{
    background-color: #222222 !important;
  }

  .spacer{
    background-color: #373737 !important;
  }
  
  /*Labels*/
  .label,.label-default{
    color: #FFFFFF !important;
    background-color: #373737 !important;
  }
  
  /*Login Pop-up*/
  .modal-dialog,.modal-content,#UserName,#Password{
    background-color: #373737 !important;
    color: white !important;
  }
  
.content,#container,.sitetable{
  background-color: #161616 !important;
  color: #FFFFFF !important;
}
  
  /*Comment Box*/
  .commenttextarea{
    color: white !important;
    background-color: black !important;
  }
  
  /*Register Page*/
  .well,.panel{
    background-color: #373737 !important;
  }
  .panel-heading{
    background-color: #60B9F0 !important;
  }
  .panel-body{
    background-color: #222222 !important;
  }
  .form-group,.form,.form-horizontal,.well label.control-label{
    color: #FFFFFF !important;
  }
  .form-control,#Password,#UserName{
    background-color: black !important;
  }
  
  /*Alerts*/
  .alert,.alert-notice{
    background-color: #373737 !important;
    color: #FFFFFF !important;
  }
  
  /*New Expand Button = Later*/
  .expando-button{
  }
}
