css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("disqus.com") {
	
    /*****************************************************/
   /*      Basic Disqus dark theme by w35 (2015-02)     */
  /* Inspired by https://userstyles.org/styles/101293  */
 /*             and http://sssscomic.com              */
/*****************************************************/
/*  Theme url: https://userstyles.org/styles/110281  */
/*****************************************************/

/* Page background. */
	#container-main, body {
		background: linear-gradient(to bottom, #2E3438, #1C1D21, #111111) fixed no-repeat !important;
		border: 0px !important;
	}
	#container-page {
		padding-top: 2em !important;
	}
	.alert--brand, .anon-homepage__overlay, div#explore-overlay{
		background-color: rgba(30,30,30,0.8) !important;
	}
	.module__wrapper::before {
		background-color: rgba(40,40,40,0.8) !important;
	}
	/* Most words. */
	p, span.realtime-replies {
		color: #769440 !important;
		text-shadow: 
			0px 0px 3px black
		!important;
	}
	.content__wrap {
		background: none !important;
		border: 0px !important;
	}
	.form {
		background: none !important;
	}
	
    /***********/
   /* Sign up */
  /***********/
	.signup {
  background: none !important;
	border: none !important;
	}
	select, textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
		background: none !important;
		border: none !important;
		box-shadow: 0 0 7px #00D7EA !important;
		color: #769440 !important;
		
	}

		/*********/
	 /* Links */
	/*********/

	a, button {
		background: transparent !important;
		color: #999999 !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 4px black,
			0px 0px 5px red
		!important;
	}
	a:focus, button:focus {
		color: #00D7EA !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 4px black,
			0px 0px 10px red
		!important;
	}
	a:hover, button:hover {
		color: #EE1111 !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 4px black,
			0px 0px 10px #00D7EA
		!important;
	}
	button, a.button, a.button--recommend {
		border: 0px !important;
		box-shadow: 0 0 7px #00D7EA !important;
	}
	/* Comment links */
	a.card-comment-truncated {
		color: #98B662 !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 5px red 
		!important;
	}
	a.card-comment-truncated:hover {
		color: #00D7EA !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 4px orange,
			0px 0px 10px red
		!important;
	}
	/* Native votes */
	a.vote-up {
		color: #00AA00 !important;
	}
	a.vote-up:hover {
		color: #00EE33 !important;
	}
	a.vote-down {
		color: #CC0000 !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 4px darkgreen
		!important;
	}
	a.vote-down:hover {
		color: #EE1111 !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 5px green 
		!important;
	}
	.label--count {
		background-color: transparent !important;
	}

	/* Top Banner. */
	.site-nav--wrapper, .header-navigation{
		background: linear-gradient(to bottom, #300030, #292F33 50%) !important;
		border: 0px !important;
		box-shadow: 0 0 7px #00D7EA !important;
	}

	/* This did something, but I can't remember what. */
	/*
	div.compensation {
		background: transparent !important;
		border: 0px !important;
	}
	*/

	/* Home */
	div.card-wrap, div.hidden-tablet, .aside__wrap, li.nav__item, .card-link__wrapper, .card__additional {
		background: rgba(0,0,0,0.2) !important;
		border: 0px !important;
		box-shadow: 0 0 7px #00D7EA !important;
	}
	.card-link__body {
		background: transparent !important;
		border: 0px !important;
		color: #769440 !important;
	}
	.card-link__wrapper:hover {
		border: 0px !important;
		box-shadow: 
			3px 3px 7px rgba(255,0,0,0.35),
			-3px -3px 7px rgba(255,0,0,0.35),
			3px -3px 7px rgba(255,0,0,0.35),
			-3px 3px 7px rgba(255,0,0,0.35) !important;
	}
	button.btn.btn-light.btn-follow {
		background: transparent !important;
		border: 0px !important;
		box-shadow: 0 0 3px #00D7EA !important;
		color: #999999 !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 4px black,
			0px 0px 5px red
		!important;
	}
	button.btn.btn-light.btn-follow:hover {
		border: 0px !important;
		box-shadow: 0 0 7px #00D7EA !important;
		color: #EE1111 !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 4px black,
			0px 0px 10px #00D7EA
		!important;
	}

	/* Discover tab */
	div.dropdown-menu {
		background: transparent !important;
		box-shadow: 0 0 7px #00D7EA !important;
	}
	div.dropdown__blk {
		background: rgba(30,0,30,0.8) !important;
		border: 0px !important;
		color:  #769440 !important;
	}
	/* Profile / Settings dropdown graphic */
	div.tooltip.fade.bottom.in {
		background: transparent !important;
		box-shadow: none !important;
	}
	div.tooltip-inner {
		background: rgba(30,0,30,0.8) !important;
		border: 0px !important;
		color:  #9999AA !important;
		box-shadow: 0 0 7px #00D7EA !important;
	}
	/* Settings */
	ul.dropdown-menu {
		background: rgba(30,0,30,0.8) !important;
		box-shadow: 0 0 7px #00D7EA !important;
	}
	/* Community forum cover */
	.cover {
		background: transparent !important;
		border: 0px !important;
	}

		/*****************/
	 /* Edit profile. */
	/*****************/

	/* Background. */
	.dsq-popup-content {
		background: linear-gradient(to bottom, #0C0C0C, #1C1D21, #2E3438) fixed no-repeat !important;
	}
	/* Controls some text.*/
	body > div {
		background: transparent !important;
		color: #769440 !important; 
		text-shadow: 
			0px 0px 2px black
		!important;
	}
	/* List background. */
	#dsq-tab-profile-edit, #dsq-tab-profile-avatar, #dsq-tab-profile-services, #dsq-tab-profile-notifications, #dsq-tab-profile-account, #dsq-tab-profile-apps, #dsq-tab-profile-merge {
		background: transparent !important;
		border: 0px !important;
		box-shadow: 0 0 7px #00D7EA !important;
	}
	/* Selected list item */
	#dsq-popup-message.dsq-popup .dsq-modal-tabs li.dsq-active a {
		background: rgba(0,0,0,0.1) !important;
		border: 0px !important;
		box-shadow: 0 0 7px #00D7EA !important;
		color: darkorange !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 10px red
		!important;
	}
	#dsq-popup-message.dsq-popup .dsq-modal-tabs li.dsq-active a:hover {
		border: 0px !important;
		border-bottom: 0px !important;
		background: rgba(0,0,0,0) !important;
		color: darkorange !important;
		margin: 0 !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 10px red
		!important;
	}
	/* Unselected list item. */
	#dsq-popup-message.dsq-popup .dsq-modal-tabs li a {
		border: 0px !important;
		color: #999999 !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 5px red
		!important;
	}
	#dsq-popup-message.dsq-popup .dsq-modal-tabs li a:hover {
		background: none !important;
		color: #00D7EA !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 10px red
		!important;
	}
	/* Things which need to be transparent, unless you want to experiment. */
	#notice.success, #notice.error, #dsq-profile-edit-modal, .dsq-tab-container{
		background: transparent !important;
	}	

		/*********/
	 /* Inbox */
	/*********/

	/* Navigation bar and comment sections. */
	.nav-notifs {
		background: rgba(0,0,0,0.5) !important;
		border: 0px !important;
		box-shadow: 0 0 7px #00D7EA !important;
	}
	.nav-notifs .dropdown-notifs-settings, li.dropdown-notifs-settings-option label {
		background: rgba(30,0,30,0.9) !important;
		color: #9999AA !important;
	}
	.nav-tabs {
		background: transparent !important;
	/*	transform: scaleX(.93) !important; */
	}

		/****************/
	 /* Comment area */
	/****************/

	div.card {
		background: transparent !important;
		border: 0px !important;
		box-shadow: 0 0 7px #00D7EA !important;
	/*	transform: scaleX(.93) translateX(2px) !important; */
	}
	div.comment-body, .community-alert, #logo img, .discussion-image {
		background: rgba(0,0,0,0.2) !important;
		border: 0px !important;
		box-shadow:
			3px 3px 7px rgba(0,215,234,0.35),
			-3px -3px 7px rgba(0,215,234,0.35),
			3px -3px 7px rgba(0,215,234,0.35),
			-3px 3px 7px rgba(0,215,234,0.35) !important;
	}
	div.comment-body:hover, .discussion-image:hover {
		box-shadow: 
			3px 3px 7px rgba(255,0,0,0.35),
			-3px -3px 7px rgba(255,0,0,0.35),
			3px -3px 7px rgba(255,0,0,0.35),
			-3px 3px 7px rgba(255,0,0,0.35) !important;
	}
	div.card-reason, .nav-notifs-title {
		color: #9999AA !important;
		text-shadow: 0 0 5px #000000 !important;
	}
	.discussion-image {
		border: 0px !important;
		box-shadow:
			3px 3px 7px rgba(0,215,234,0.35),
			-3px -3px 7px rgba(0,215,234,0.35),
			3px -3px 7px rgba(0,215,234,0.35),
			-3px 3px 7px rgba(0,215,234,0.35) !important;
	/*	transform: translateX(-30px) !important; */
	}
	.comment-actions  {
		transform: translate(10px, -5px) !important;
	}
	/* Post / Reply */
	.postbox {
		transform: translateY(-10px) !important;
	}
	.post-actions {
		background: transparent !important;
	}
	/* Voters list */
	.tooltip {
		background: rgba(30,0,30,0.8) !important;
		border: 0px !important;
		box-shadow: 0 0 7px #00D7EA !important;
	}
	/* Guest vote */
	.tooltip span.username {
		color: #769440 !important;
	}
	/* Reply, View discussion, Edit, and a btn link */
	a.reply, a.view-comment, a.edit, a.btn {
		box-shadow: 0 0 2px #00D7EA !important;
	}
	a.reply:hover, a.view-comment:hover, a.edit:hover, a.btn:hover {
		box-shadow: 0 0 6px #00D7EA !important;
		text-shadow:
			0px 0px 2px black,
			0px 0px 4px black
		!important;
	}

		/************/
	 /* Profile. */
	/************/

	/* Profile header. */
	div.cover-profile, div.cover-nav-wrapper {
		background: transparent !important;
		border: 0px !important;
	/*	transform: scaleX(.945) translateX(20px) !important; */
	}
	.cover-profile-name, .cover-profile-name.truncate-line {
		color: #769440 !important;
		text-shadow: 0 0 4px black !important;
	}
	div.cover-nav-wrapper {
	/*	transform: scaleX(.95)translateX(-20px) !important; */
	}
	/* Following number graphic and Follow button. */
	span.nav-tab-stat, div.card button {
		background: transparent !important;
		box-shadow: 0 0 2px #00D7EA !important;
	}
	/* Following list */
	.nav-tabs {
		border-bottom: none !important;
	}
	.layout .more-wrapper, .more-wrapper {
		background: transparent !important;
	}
	.aside-list__item {
		border-bottom: none !important;		
	}



		/*******************/
	 /* Discussion page */	
	/*******************/

	/* Discussion background.*/
	div.discussion-page {
		background: linear-gradient(to bottom, #2E3438, #1C1D21, #111111) fixed no-repeat !important;
		border: 0px !important;
	}
	div.modal-content, div.discuss-tile__description {
		background-color: rgba(30,30,30,0.8) !important;
	}
	/* Comments section. */
	.card-link, #disqus_thread, .textarea-wrapper {
		background: transparent !important;
		border: 0px !important;
		box-shadow:
			3px 3px 7px rgba(0,215,234,0.35),
			-3px -3px 7px rgba(0,215,234,0.35),
			3px -3px 7px rgba(0,215,234,0.35),
			-3px 3px 7px rgba(0,215,234,0.35) !important;
	/*	transform: scaleX(.93) translateX(2px) !important; */
	}
	.placeholder{
		color: rgba(0,0,0,0.2) !important;
		font-weight: bold !important;
		text-shadow: none !important;
	}

	div.discussion-cover-wrapper, div.cards-featured {
		background: transparent !important;
		border: none !important;
	}
	/* More from... */
	div.card.card-generic {
		background: rgba(0,0,0,0.3) !important;
		border: 0px !important;
		box-shadow: 0 0 7px #00D7EA !important;
	}
	a.card-wrap {
		border: 0px !important;
	}
	h2.text-brand {
		color: none !important;
	}
	/* Footer */
	div.feed-wrapper {
		background: transparent !important;
	}

		/*********/
	 /* Login */
	/*********/

	body.profile-login {
		background: linear-gradient(to bottom, #2E3438, #1C1D21, #111111) fixed no-repeat !important;
	}
	body.profile-login .global-header {
		background: transparent !important;
		box-shadow: none !important;
	}
	body.profile-login .login {
		background: transparent !important;
		border: 0px !important;
		transform: translateY(70px) !important;
	}
	body.profile-login .login h1 {
		color: #AAAAAA !important;
	}
	body.profile-login .login input {
		background: rgba(0,0,0,0.2) !important;
		border: 0px !important;
		box-shadow:
			3px 3px 7px rgba(200,215,234,0.35),
			-3px -3px 7px rgba(200,215,234,0.35),
			3px -3px 7px rgba(0,215,234,0.35),
			-3px 3px 7px rgba(0,215,234,0.35) !important;
	}
	body.profile-login .login button.submit {
		background: rgba(0,0,0,0.2) !important;
		box-shadow:
			3px 3px 2px rgba(0,215,234,0.2),
			-3px -3px 2px rgba(0,215,234,0.2),
			3px -3px 2px rgba(0,215,234,0.2),
			-3px 3px 2px rgba(0,215,234,0.2) !important;
	}
	body.profile-login .get-disqus {
			transform: translateY(30px) !important;
	} 

		/*~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~/|
	 /              Required CSS for external sites using Disqus.               / |
	/__________________________________________________________________________/  |
	|    For external websites which use Disqus, webcomics, pbs.org, etc...   |   |
	|  this is required, because most websites display on a white background, |   |
	|   causing this style to be hardly ledgible. Comment out #layout to see! |   |
	|     Controlling each chat from this individual style sheet is roughly   |   |
	|  750,000 times easier than creating a style for each page using Disqus. |   |
	|This is, basically, a copy/paste of the dark style to be used externally.|  /
	|    Everything has been commented, so you can easily restyle a default,  | /
	|    black on white comment section for an external, Disqus comment box.  |/
	|________________________________________________________________________*/

	/* Comment box background */
	#layout {
		background: linear-gradient(to bottom, #2E3438, #1C1D21, #111111) fixed no-repeat !important;
		border: 4px double rgba(0,147,151,0.5) !important;
		padding: 5px !important;
	}
	div.textarea {
		color: #769440 !important;
	} 
	/* Links */
	#layout a {
		color: #999999 !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 5px red
		!important;
	}
	#layout a:focus{
		color: #00D7EA !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 10px red
		!important;
	}
	#layout a:hover {
	/*	color: #00D7EA !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 10px red
		!important;
	*/
		color: #EE1111 !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 4px black,
			0px 0px 10px #00D7EA
		!important;
	}
	/* Comments. */
	#layout div.post-content {
		background: rgba(0,0,0,0.2) !important;
		box-shadow:
			3px 3px 7px rgba(0,215,234,0.35),
			-3px -3px 7px rgba(0,215,234,0.35),
			3px -3px 7px rgba(0,215,234,0.35),
			-3px 3px 7px rgba(0,215,234,0.35) !important;

	}
	#layout div.post-content:hover {
		box-shadow: 
			3px 3px 7px rgba(255,0,0,0.35),
			-3px -3px 7px rgba(255,0,0,0.35),
			3px -3px 7px rgba(255,0,0,0.35),
			-3px 3px 7px rgba(255,0,0,0.35) !important;

	}
	/* External comment margin */
	#layout div.post-content {
	margin: 5px !important;
	}
	/* PBS's "Also on" (love my PBS)*/
	#layout .discovery-col-header h2, .discovery-col-header h2 strong {
		color: #769440 !important;
		text-shadow: 
			0px 0px 3px black
		!important;
	}
	/* Disqus' "What's this?" */
	#layout div.discovery-options {
		transform: translateX(-10px) !important;
	}
	#layout button.discovery-help {
		color: #999999 !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 5px red
		!important;
	}
	#layout button.discovery-help:hover{
		color: #00D7EA !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 10px red
		!important;
	}
	/* Additional articles: users, meta, etc...*/
	#layout span.user, ul.meta {
		color: #BE5E00 !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 5px black
		!important;
	}
	#layout span.user:hover, ul.meta:hover {
		color: #00D7EA !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 10px red
		!important;
	}
	/* Comment voting. */
	#layout a.vote-up {
		color: #00AA00 !important;
	}
	#layout a.vote-up:hover {
		color: #00EE33 !important;
	}
	#layout span.vote-down {
		color: #CC0000 !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 4px darkgreen
		!important;
	}
	#layout span.vote-down:hover {
		color: #EE1111 !important;
		text-shadow: 
			0px 0px 2px black,
			0px 0px 5px green 
		!important;
	}
	/* Comment sorting and profile dropdown. */
	#layout li.dropdown ul {
		background: rgba(30,0,30,0.8) !important;
		box-shadow: 0 0 7px #00D7EA !important;
	}
	#layout .user-menu ul.dropdown-menu {
		background: rgba(30,0,30,0.8) !important;
		box-shadow: 0 0 7px #00D7EA !important;
	}
	/* Discusion sidebar. ##Effects native Disqus site background.## */
	#container-main > div, .drawer-footer {
		background: rgba(0,0,0,0.1) !important;
		border: 0px !important;
		box-shadow: 0 0 7px #00D7EA !important;
	}


			/*******************************************/
		 /*     Disqus actually has a website.      */
		/*******************************************/
	 /* After completing the comment restyling, */
	 /*  which was entirely for webcomics and   */
	 /*         other immature things,          */
	 /*      I find out Disqus has a blog,      */
	 /* and a marketing page, and a help section*/
	 /* and... So, being a perfectionist, time  */
	 /*      to make it a complete restyle      */
	 /*   for everyone to use, no matter what.  */
	 /*  I thought I would share that slightly  */
	 /*    amusing story for your enjoyment.    */
	 /*   I will not lie, it will be quick, so  */
	 /* add anything funky to the discussion on */
	 /*  the userstyles page. URL at the top.   */
	/*******************************************/


		/******************************/
	 /* The following is very ugly */
	/******************************/

	/* Backgrounds. */
	#content, #page-header, #local-nav, #meet-disqus, #footer-navigation, .sub-nav, .page-header, .site-registration, body#homePage, .panel, #footer, body.loaded, .question-body, .layout-content html, footer.global-footer, body.labs {
		background: linear-gradient(to bottom, #2E3438, #1C1D21, #111111) fixed no-repeat !important;
		border: 0px !important;
	}
	/* Glowing backgrounds. */
	#main-content.marketing-block, section.marketing-divider, section.marketing-block, .sub-nav.fixed {
		background: linear-gradient(to bottom, #2E3438, #1C1D21, #111111) fixed no-repeat !important;
		border: 0px !important;
		box-shadow: 0 0 7px #00D7EA !important;
	}
	#hellobar, .global-header{
		background: linear-gradient(to bottom, #300030, #292F33 50%) !important;
		border: 0px !important;
		box-shadow: 0 0 7px #00D7EA !important;
	}
	.clearfix {
		background: rgba(30,0,30,0.8) !important;
		box-shadow: 0 0 7px #00D7EA !important;
	}
	/* Using Disqus. */
	.col-offset-7 {
		background: rgba(0,0,0,0.8) !important;
	}
	.col-offset-7 a.btn {
		transform: translateY(-10px) !important;
	}
	/* Whiteish text. */
	.col-6 p, .sectionIntro h2 em, span[id^="article-autocomplete-body"], div.metric div, div.box h2, div.box h3, .follow-main h1 {
		color: #AAAAAA !important;
		text-shadow: none !important;
	}
	/* Blackish text. */
	#our-team a span p  {
		color: #333333 !important;
		text-shadow: none !important;
	}

	/* Transparent things. */
	.sub-nav div ul, .site-registration::before, div.box, .page-controls {
		background: transparent !important;
		border: 0px !important;
		box-shadow: none !important;
	}
	#support-main .support-body {
		background: transparent !important;
	}
	/* Transparent things from the future. */
	.col-5, div.metric {
		background: transparent !important;
		border: 0px !important;
		box-shadow: 0 0 7px #00D7EA !important;
	}

	/* Not so transparent, but still from the future */
	.col-12 #benefits-tiles li div, #press aside, section.content, div#latestNews .wrapper.clear, div.career-box, .article-content, .follow-main {
		background: rgba(0,0,0,0.3) !important;
		border: 0px !important;
		box-shadow: 0 0 7px #00D7EA !important;
	}
	.col-12 #benefits-tiles li.active div, div.panel ul#slider, #slider-wrapper div.container, .table, a#ui-active-menuitem{
		background: rgba(30,0,30,0.3) !important;
		border: 0px !important;
		box-shadow: 0 0 7px red !important;
		color: #AAAAAA
	}
	/* Sort of transparent */
	nav.sectionNav {
		background: rgba(0,0,0,0.8) !important;
		border: 0px !important;
	}
	/* Very opaque things. */
	.col-4 div.panel, .ui-autocomplete {
		background: rgba(0,0,0,0.9) !important;
		border: 0px !important;
		box-shadow: 0 0 7px #00D7EA !important;
	}
	/* Register inputs */
	input#sitename, input#shortname, div.append-url, input#q {
		background: transparent !important;
		border: none !important;
		box-shadow: 0 0 7px #00D7EA !important;
		color: #AAAAAA
	}

	/* Enjoy */

}
