css:
@-moz-document domain("asahi.com") {

/* Block */
#GlobalNav, #TopicsBar, body>#HometopAd, .Section .PrImage,
.PrText, .UtilityNav, .UserNav, .SocialNav, .BreadCrumb,
#Sub, #Footer, .PrTextMod, #FeatureBox,
#google_esf+*+*+div
{ display: none !important; }

/* Layout */
#Contents #ContentsInner {
  margin: 20px auto 80px;
  padding: 40px;
  box-shadow: 0 0 1px #E1E1DA;
}
#Main, #Main #MainInner {
  width: auto;
}
.RelatedListMod {
  clear: both;
}
.Section {
  margin-top: 40px !important;
}
#Main #MainInner .ArticleBody .ExMod {
  margin: 10px auto;
}

/* Styling */
body {
  background: #f5f5f5;
}
#ContentsInner {
  background: #fff;
  margin: 40px auto;
  padding: 40px;
}
.RelatedListMod .Comment {
  display: none !important;
}
.ArticleBody .AdMod:first-child {
  min-height: 300px;
  width: 250px;
  margin: 0 40px 0 10px;
  background: #fafafa;
}
.ArticleBody .AdMod a {
  display: none;
}
.Section .ListSideImage li {
  width: 780px;
}
#Main #MainInner .ArticleBody p a.TopicsLink {
  border-bottom: 2px dotted rgba(150, 150, 143, 0.2);
}

}
