css:
/* waffle.io -- dark simplefied board by viilo [0.1]
*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("waffle.io") {
  .column {
    background-color: #222 !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
    background-attachment: scroll !important;
      
    border-right: 4px solid #000 !important;
  }
  .column-body{
    padding: 0px !important;
  }
  .column-header{
    background: #444;
    color: white;
  }
  .card{
    border: 1px solid #666 !important;
    border-left: 0 !important;
    border-bottom: 0 !important;
    border-right: 0 !important;
    box-shadow: none !important;
    margin: 0 !important;
  }
  .card:last-child{
    border-bottom: 1px !important;
  }
  body, .card, .card .title{
    font-family: 'ubuntu', 'helvetica', arial, sans-serif !important;
  }
  .board .card .card-header .assignee-icon.assigned{
    top: 0 !important;
    right: 0 !important;
  }
  .assigned img{
    box-shadow: none !important;
  }
  .card-body .title{
    color: #aaa !important;
    padding-right: 30px !important;
  }
  .card-pr-list{
    background: white !important;
    margin: 0 !important;
    box-shadow: none !important;
  }
  .card-body {
    background: #333 !important;
  }
  .card-header{
    background: linear-gradient(0deg, #111, #222) !important;
    height: 30px !important;
    padding: 5px !important;
  }
  .card-header:hover{
    background: #343 !important;
  }
  .source-name{
    color: rgba(255,255,255,0.7) !important;
  }
  .assignee-icon.unassigned{
    background: none !important;
  }
  .assignee-icon.unassigned img{
    top: 5px !important;
    right: 5px !important;
    box-shadow: none !important;
  }
  .card-header-size{
    margin-top: 1px !important;
    right: 50px !important;
  }
  .source-name{
    color: #333 !important; 
  }
  .card-number{
    font-weight: bold !important;
    color: #ccc !important;
  }
  .card-body .footer{
    opacity: .5 !important;
  }
  .card-body .footer:hover{
    opacity: 1 !important;
  }
    
  textarea.title {
    border-color: #000 !important;
    padding-top: 4px !important;
    padding-left: 4px !important;
    padding-right: 2px !important;
    background: #444 !important;
    color: #ccc !important;
  }
  
  .milestone-pill, .label-pill {
    margin-top: 2px !important;
    height: 22px !important;
    border: 1px solid #000 !important;
    border-color: #000 !important;
  }
  
  .milestone-pill {
    color: #fff !important;
    background-color: #111 !important;
  }
  
  .label-pill {
    color: #000 !important;
  }
  
  /* Header stuff */
  .header-nav {
    background: linear-gradient(0deg, #000, #333) !important;
    border-color: #333 !important;
  }
  
  li.dropdown {
    border-color: #444 !important;
  }
  
  .project-item {
    background: #222 !important;
  }
  
  a.button {
    background: linear-gradient(0deg, #444, #555) !important;
    border-color: #000 !important;
  }
  .navbar-brand, .ico-logo  {
    background: linear-gradient(0deg, #000, #333) !important;
  }
  input.search-query, .header-action-toggle {
    background: #333 !important;
    border-color: #000 !important;
  }
  .search > .input-group {
    background: #333 !important;
    border: 1px solid #000 !important;
  }
  .search > .input-group .panel-title {
    background: linear-gradient(0deg, #666, #888) !important;
  }
  .search > .input-group .panel {
    border-color: #000 !important;
  }
  .search > .input-group .panel a {
    color: #000 !important;
  }
  
  /* Sidebar */
  .sidebar {
    background: linear-gradient(90deg, #111, #444) !important;
  }
  
  /* Modal view */
  .modal-content {
    background: #000 !important;
  }
  
  .modal-header {
    border-right: 4px solid #000 !important;
    border-left: 4px solid #000 !important;
  }
  
  .modal-body {
    color: #ccc !important;
    
    border: 4px solid #000 !important;
    border-top: 0px !important;
   
    background: #222 !important;    
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
    background-attachment: scroll !important;
  }
  
  .modal-content .card-item-body {
    background: linear-gradient(0deg, #444, #555) !important;
    color: #ccc !important;
  }
  
  .modal-content .divider {
    border-color: #666 !important;
  }
  
  .modal-content .comment-form {
    border: 0px !important;
    background: transparent !important;
  }
  
  .modal-content .tab-content, .modal-content .footer {
    background: #111 !important;
  }
  .modal-content .tab-content textarea {
    background: #222 !important;
    border-color: #444 !important;
    color: #aaa !important;
    box-shadow: #000 0px 0px 0px !important;
  }
  .modal-content .footer .btn {
    border-color: #000 !important;
    background: linear-gradient(0deg, #444, #555) !important;
  }
  
  .modal-content .nav {
    border-color: #000 !important;
  }
  
  .modal-content .ng-isolate-scope .active a {
    background: linear-gradient(0deg, #555, #666) !important;
    color: #ccc !important;
    border-color: #000 !important;
  }
  
  .modal-content .ng-isolate-scope .disabled a:hover {
/*     background: linear-gradient(0deg, #555, #666) !important; */
/*     color: #ccc !important; */
/*     border-color: #000 !important; */
    color: #aaa !important;
    border-color: transparent !important;
  }
  
  .modal-content .ng-isolate-scope a:hover {
    color: #ccc !important;
    background: linear-gradient(0deg, #555, #666) !important;
    border-color: #000 !important;
  }
  
  .modal-content .preview {
    border-color: #444 !important;
  }
  
}
