css:
@-moz-document domain("youtube.com") {
/*setting the upload button.*/
#appbar-onebar-upload-group #upload-btn{
background-color: transparent;
border: none!important;
font-size: 0px;
background: url(https://google.github.io/material-design-icons/file/svg/ic_file_upload_24px.svg);
background-size: auto!important;
opacity: 1!important;
-webkit-filter:invert(100%)!important;
top:1px!important;
}

#yt-masthead-user .yt-uix-button-icon-bell{
opacity:1!important;
-webkit-filter:invert(100%)!important;
}
#yt-masthead-search .search-form .consolidated-form{
max-width:3840px!important;
}
/*Setting the look on video thumbnails to a more readable appearance.*/
.video-time {
font-weight: 500!important;
font-size: 11px!important;
background-color: rgba(0, 0, 0, 0.51)!important;
color: #fff!important;
height: 13px!important;
line-height: 14px!important;
opacity: 1!important;
filter: alpha(opacity=75);
display: -moz-inline-stack;
vertical-align: middle!important;
position: absolute;
bottom: 0px!important;
width: 100%!important;
right: 0px!important;
}
  
#yt-masthead-container {
border:none!important;
background-color:#F44336!important;
}

/*We start changing the text input field for the search.*/
#masthead-search-terms input {
padding:0px 0px 0px 0px!important;
border: none!important;
outline: none!important;
background: transparent!important;
font-size: 16px!important;
color: white!important;
}

/*Then we change the field itself, first the text entry.*/
#masthead-search-terms {
background-color: #B71C1C!important;
border:none!important;
-moz-transition: none!important;
-webkit-transition: none!important;
transition: none!important;
box-shadow:none!important;
}

#search-btn {
  background-color: transparent !important;
  border: none !important;
  width:auto!important;
  -webkit-filter:contrast(500)!important;
  -webkit-filter:invert(100%)!important;
}
#yt-masthead #search-btn .yt-uix-button-content {
margin-top: 0px;
margin-left: 10px;
}
#masthead-appbar {
background-color: #F44336!important;
border-bottom: none!important;
/*-webkit-box-shadow: 0px 3px 13px 0px rgba(0, 0, 0, 0.84)!important;*/
}
a.yt-uix-button-epic-nav-item.selected, a.yt-uix-button-epic-nav-item.yt-uix-button-toggled, button.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading {
color: #FFF!important;
font-weight: 500!important;
font-size: 14px!important;
text-transform: uppercase!important;
border-bottom: 5px solid!important;
border-color:#B71C1C!important;
}
#appbar-nav .yt-uix-button.yt-uix-button-epic-nav-item {
color: #560602!important;
font-weight: 500!important;
font-size: 14px!important;
text-transform: uppercase!important;
}
.feed-header {
display:none!important;
}

.branded-page-v2-primary-col .branded-page-box, .section-list li .item-section .branded-page-box {
-webkit-box-shadow: 0px 7px 18px -6px rgba(0,0,0,0.84)!important;
}

.yt-section-hover-container .yt-uix-button-action-menu, .yt-section-hover-container .yt-uix-menu-top-level-button {
opacity: 1!important;
}
.yt-lockup-title a, .yt-lockup:hover a, .yt-lockup:hover .yt-lockup:hover {
color: #545454!important;
font-weight: 500!important;
font-size: 16px!important;
}

.yt-lockup-byline{
display:none!important;
}
.player-width {
-webkit-box-shadow: 0px 7px 18px -6px rgba(0,0,0,0.84)!important;
}
.yt-card {
-webkit-box-shadow: 0px 7px 18px -6px rgba(0,0,0,0.84)!important;
border-radius:3px!important;
}

.browse-list-item-container.feed-item-container.branded-page-box {
margin-bottom: 16px!important;
border-radius:3px!important;
box-shadow:none!important;
    }

            *{font-family:Roboto!important;}
.go, .ho {
border-radius: 25px!important;
}
.vKa {
border-radius: 25px!important;
}
}
