css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("^http://manhua\.dmzj\.com/.+/[0-9\-]+\.shtml(#.*)?") {
    body {
        background-color: #222 !important;
    }
    .font14black {
        color: #FFF !important;
        font-weight: bold !important;
    }
    .interactive-opinion-text,.interactive-opinion-text-input,#erweima,#demo li p,.loading,.bds_more,.interactive-opinion-friend p,#myvote span {
        color: #FFF !important;
    }
    .inner_img {
        margin-top: 20px !important;
        margin-bottom: 20px !important;
    }
    #sns_cite_vote_list,#erweima {
        margin-top: 200px !important;
    }
    #internal_div {
        position: fixed !important;
        margin-top: 0 !important;
        left: 764px !important;
        top: 280px !important;
    }
    .pop_bbsadmin_mend_back {
        background-repeat: repeat-y !important;
    }
    .part_collection_bnt {
        width: 96% !important;
        height: 70px !important;
        background: none !important;
        font-size: 36px !important;
        background-color: #0088CC !important;
        color: #FFF !important;
        margin: 5px 0 !important;
        font-weight: normal !important;
    }
    .part_collection_bnt:hover {
        background-color: #E23300 !important;
    }
    body > div:nth-child(7) > div, #sns_cite_vote_list > div > div:last-child {
        color: #FFF !important;
    }
    #floatCode {
        display: none !important;
    }
}

@-moz-document regexp("^http://manhua\.dmzj\.com/.+/") {
    .online_anim_debate {
        margin-top: 200px !important;
    }
}

@-moz-document domain("manhua.dmzj.com") {
    html, body, div, h1, h2, h3, h4, h5, h6, p, form, ul, li, dl, dt, dd, ol, table {
        font-family: Arial,Helvetica,sans-serif,SimSun !important;
    }
}

@-moz-document url-prefix("http://www.dmzj.com/view/") {
    body {
        background-color: #222 !important;
    }
    a, .head_wz, .myPoint {
        color: #FFF !important;
    }
    a:hover {
        color: #4A97CD !important;
    }
    .wrap_last_mid, .allN, .comic_gd_fb {
        color: #FFF !important;
    }
    .mainNav a:hover, .lz_btn:hover, .next_btn:hover {
        color: unset !important;
    }
    .page_o a {
        color: #000 !important;
        background-color: #FFF !important;
    }
    .head_title h2, .head_title a {
        color: #FF6600 !important;
    }
    .foot-detail a {
        color: #666 !important;
    }
    .dy_success, .manag a {
        color: #333 !important;
    }
    .qx {
        color: #000 !important;
    }
}

@-moz-document url-prefix("http://user.dmzj.com/cache/oviewpoint/") {
    .comic_gd_con p, .comic_gd_fb {
        color: #FFF !important;
    }
}
