css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://teksyndicate.com/") {

/* !important needed for Chrome */

/* Middle Container Background Color */ 
.main-container .row-fluid {
  background: #0E0E0E !important;
  }

/* Loggin Notification Below Banner */
.alert-success {
  background-color: #222222 !important; border-color: #333333 !important;
  }
.alert {
  text-shadow: none !important;
  }
  
/* Homepage Feed Text & Comment Count */
.comment-icon {
  background: url("https://teksyndicate.com/sites/all/themes/bootstrap/images/comment_32dark.png") no-repeat scroll 0px 0px transparent !important;
  }
.comment-icon-small {
  background: url("https://teksyndicate.com/sites/all/themes/bootstrap/images/comment_24dark.png") no-repeat scroll 0px 0px transparent !important;
  }
.feed-cn {
  color: #D0D0D0 !important;
  }
  
/* The Feed (Homepage) */
.label, .badge {
  background-color: #555555 !important;
  }

/* Add New Comment Area (For Older Post) */
#reply-bar .reply-bar-message, #similar-content h3, .field .field-label {
  color: #FFFFFF !important;
  }
h3 {
  color: #555555 !important;
  }
.well {
  background-color: #222222 !important;
  }
  
  /* Creating a Link Background */
.panel_wrapper {
  background: #222222 !important;
  }
  
/* Links */ 
a:link, a:visited {
  color: #CCCCCC !important;
  }
a:hover {
  color: #CC6C28 !important;
  }

/* Content Nav */
.contentnav ul {
  text-indent: 65px !important;
  }
.contentnav ul li {
  padding: 0px 0px 0px 50px !important;
  }
  
/* Recent Comments (Homepage, bottom right column) */ 
  /* Comment Title Font Color */
#block-views-comments-recent-block .swizzle-title a {
  color: #457CA5 !important;
}
  /* Even Row Background Color */
#block-views-comments-recent-block .views-row-even {
  background: #222222 !important;
  }

/* Right Column Headers */ 
h2.block-title {
  color: #FFFFFF !important;
  }

/* Help us do amazing things */  
strong, .main-container .row-fluid {
  color: #FFFFFF !important;
  }

/* Game Server Info List */
li {
  color: #FFFFFF !important;
  }

/* Thanks TekSupport Members! */
label {
  color: #FFFFFF !important; margin-left: 11px !important;
  } 

/* Reply to Post Bar */
#reply-bar {
  background: #222222 !important; text-shadow: none !important;
  }
#reply-bar a {
  text-shadow: none !important;
  }
  
/* Forum Post Author */
.comment ul.links li.author a, ul.linkset li.author a {
  color: #A3A3A3 !important;
  }

/* Post Guidelines */
.filter-guidelines.form-wrapper.filter-guidelines-processed {
  background: #222222 !important;
  }

/* Post Links */  
.content p a {
  color: #457CA5 !important;
  }
}
