css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ecigtalk.ru") {
html {
  background: #e9e9e9;
}
body {
  margin: 0;
  min-width: 1100px;
}
.above_body {
  padding-top: 26px;
  border-radius: 0;
  min-width: 1100px;
  margin: 0;
  background: #2f4456;
}

#ad_global_header1 {
  z-index: 12;
  position: relative;
  margin: 0;
}
.doc_header>div {
  float: left;
}
iframe[style="background:#ffffff;"] {
  background: #2f4456!important;
}
#ad_global_below_navbar {
  position: absolute;
  top: 0;
  left: 0;
  right: 0px;
  height: 0px;
  width: auto;
  z-index: 11;
  padding-top: 86px;
}
#ad_global_below_navbar:hover {
  /*height: 260px;*/
}
#ad_global_below_navbar center, #ad_global_below_navbar div[style^="width: 900px;"], #ad_global_below_navbar br, .gsc-above-wrapper-area,
a[href^="http://russkivape.ru"],#header a[onclick^="_gaq.push"] {
  display: none;
}
#ad_global_below_navbar center, #ad_global_below_navbar div[style^="width: 900px;"] {
  background: #fff;
  margin: 0!important;
  /*position: absolute;
  left: 0;
  right: 0;
  top: 96px;*/
  width: auto!important;
  z-index: 102;
  box-shadow: 0px 2px 2px #c8c8c8;
  padding-bottom: 20px;
}
#ad_global_below_navbar:hover center, #ad_global_below_navbar:hover div[style^="width: 900px;"] {
  display: block;
}
#ad_global_below_navbar div[style^="width: 900px;"] {
  padding: 10px 15px;
  top: 125px;
  
}
#ad_global_header1>div {
  margin: 0!important;
}
#ad_global_below_navbar>div[align="center"] {
  position: absolute;
  top: 44px;
  left: 234px;
  right: 474px;
  width: auto;
  min-width: 380px;
  min-height: 40px!important;
  z-index: 103;
}
#breadcrumb {

}
#gsc-i-id1, .gsc-search-button {
  background-color: #52687A!important;
  color: #B6C9D9!important;
  border: 0!important;
  height: 20px;
  line-height: 20px;
}
#gsc-i-id1[style*='watermark'] {
  background:#52687A  url('data:image/png;base64,') no-repeat scroll left center!important;
}
#ad_global_below_navbar #cse {
  width: 100%!important;
}
.gsc-control-cse {
  padding: 0!important;
  background: transparent!important;
  border: 0!important;
}
.gsc-results-wrapper-visible {
  background: #fff;
  box-shadow: 0 0 6px rgba(0,0,0,0.5);
}
input.gsc-input {
  border: 0;
}
#header {
  height: 60px;
  overflow: hidden;
}
.logo-image {
  position: relative;
  z-index: 104;
  top: -30px!important;
  float: left;
}
.logo-image img {
  height: 60px;
  width: auto;
  margin: 0!important;
}
.toplinks form input {
  margin: 0;
}
.navbar_search input.textbox, #logindetails input.textbox {
  background: #363636;
  color: #cbcbcb!important;
  padding: 2px 4px;
  border: #6b6b6b 0px solid;
  margin-top: 2px;
}
#thread_controls {
  position: relative;
  z-index: 10;
}
#toplinks {
  position: fixed;
  top: 0;
  right: 0;
  left: 0;
  background: #000;
  z-index: 1004;
  padding: 0;
  width: 100%;
  min-width: 1100px;
}
#toplinks a {
  height: 26px!important;
  font: bold 12px/26px Arial,Calibri,Verdana,Geneva,sans-serif!important;
  padding: 0 10px!important;
}
#toplinks .nonotifications a.popupctrl {
  padding: 0 15px 0 10px!important;
}
.welcomelink a {
  float: right!important;
  display: block!important;
}
#toplinks a:hover, #toplinks a.active {
  background-color: #2f4456;
  color: #fff;
}
#toplinks li {
  padding: 0;
}
#navbar {
  position: fixed;
  top: 0;
  margin: 0;
  left: 0;
  right: 455px;
  z-index: 1005;
  height: 26px;
  background: #000;
  border-radius: 0;
  width: 690px;
}
.welcomelink {
  color: #000!important;
}
.navbar_advanced_search, #ad_global_header1>br {
  display: none;
}
#navtabs .floatcontainer {
  display: none;
  background: #2f4456;
  width: 200px;
  border: 0;
  border-radius: 0;
}
#navtabs .floatcontainer li {
  margin: 0;
  float: none;
}
#navtabs .floatcontainer a {
  display: block;
  height: 26px!important;
  font: bold 12px/26px Arial,Calibri,Verdana,Geneva,sans-serif!important;
  padding: 0 10px!important;
}
#navtabs .floatcontainer a:hover {
  background-color: #3B556B;
  text-decoration: none;
}
#navbar li.selected, #navbar li.selected li {
  position: relative;
}
#navbar li:hover>ul, #navbar li .popupmenu:hover ul {
  display: block;
}
#navbar li .popupmenu:hover ul {
  left: 200px;
  top: 0;
}
#navbar li .popupmenu:hover ul a:hover {
  background-color: #7192a8;
}
#navtabs, .navtabs li a.navtab, #logindetails, .toplinks ul.nouser li a {
  background: transparent;
  border-radius: 0;
}
.navtabs li a {
  height: 26px;
}
.globalsearch .textboxcontainer, .globalsearch .textboxcontainer span, .globalsearch .buttoncontainer, .globalsearch .buttoncontainer span, .navtabs li, .navtabs li a.navtab, .navtabs li:first-child {
  border: 0;
}
.navtabs li.selected a.navtab, .navtabs li.selected a.navtab:hover, .navtabs li a.navtab:hover, .nouser a:hover {
    background: #2f4456;
    color: rgb(255, 255, 255);
}
.nodecontrols a[name], #header a[name] {
  position: relative;
  top: 0px;
  padding-top: 30px!important;
  display: inline-block;
}
li[id^="post"] {
  position: relative;
  top: -30px!important;
  padding-top: 30px!important;
  display: inline-block;
}
.cke_colorblock table {
  width: 140px!important;
}
.cke_toolbox {
  overflow: hidden;
}
.cke_button_textcolor_panel {
  width: 152px!important;
  min-width: 152px!important;
}
.threadbit .threadinfo {
    width: 58%;
}
.threadbit .threadstats {
  width: 14%;
}
.threadbit .threadlastpost, .threadbit .threaddeleted {
  width: 14%;
}
.threadbit .threadpostedin {
  max-width: 14%;
}

@media (max-width: 1000px) {

}
}
