css:
@-moz-document url("https://calendar.sunrise.am/") {
.week__allday,.week__birthdays {
max-height:74px;
-webkit-transition:all .8s ease-in-out;
-moz-transition:all .8s ease-in-out;
-o-transition:all .8s ease-in-out;
transition:all .8s ease-in-out;
}

.week__allday:hover,.week__birthdays:hover {
max-height:600px;
}

div[data-id^="todoist"] a,a[data-id^="todoist"] {
background-color:rgba(0,230,236,0.39)!important;
color:rgba(0,130,136,1)!important;
}

div[data-id^="todoist"] a .occurrence__title:before,a[data-id^="todoist"] .occurrence__title:before {
content:"✓ ";
}

.occurrence--birthday .occurrence__title {
opacity:0.3;
}

a[data-id^="todoist"] .iconic img {
display:none;
}

.navbar__wrapper {
border:none;
height:28px!important;
}

.occurrence {
border-left:0!important;
padding-left:10px;
}

#week-column-events .occurrence {
border:1px solid rgba(0,0,0,0.14)!important;
}

.occurrence__title {
font-weight:700;
opacity:0.7;
margin-right:10px;
}

.occurrence__time {
font-size:10px;
}

.occurrence--passed {
background-color:#EEE!important;
color:#666!important;
opacity:0.5;
-webkit-transition:all .3s ease-in-out;
-moz-transition:all .3s ease-in-out;
-o-transition:all .3s ease-in-out;
transition:all .3s ease-in-out;
}

.week__timed__day--today {
background:rgba(220,247,255,0.1);
}

.daynames__item--is-today {
color:#000;
background:rgba(220,247,255,0);
box-shadow:0 0 124px rgba(0,0,0,0.1) inset;
border-bottom:6px #DDD solid;
}

.navbar__item > a,.navbar__item > span {
line-height:30px!important;
max-height:20px!important;
font-size:13px!important;
}

.app-navbar {
height:28px!important;
}

.invitations {
display:none!important;
}

.week .column-events .col1,.week .column-events .col2,.week .column-events .col3,.week .column-events .col4,.week .column-events .col5,.week .column-events .col6,.week .column-events .col7 {
width:14%!important;
}

.week__birthdays {
box-shadow:0 10px 14px -11px rgba(96,96,96,1);
z-index:11;
}

.now-marker {
z-index:999;
}

.daynames {
height:31px;
}

.daynames__item {
line-height:34px;
}

.timezone-picker--week .timezone-picker__action {
height:32px;
line-height:32px;
}

a.occurrence[title^="✓"] {
background-color:#AAA!important;
color:#000!important;
opacity:0.3;
-webkit-transition:all .3s ease-in-out;
-moz-transition:all .3s ease-in-out;
-o-transition:all .3s ease-in-out;
transition:all .3s ease-in-out;
}

a[data-id^="todoist"][title^="✓"] .occurrence__title:before {
content:"DONE ";
}

.occurrence--birthday .occurrence__title:hover,.occurrence:hover .occurrence__title,.occurrence--passed:hover,a.occurrence[title^="✓"]:hover {
opacity:1;
}
}
