css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("4chan.org") {

/* sets font */
a,
a:hover,
a.quotejs,
a.quotejs:hover,
a.quotelink,
body,
td.reply,
td.replyhl,
span.filesize,
#navtop,
#navtopr{
	font:1em Arial !important;
}

/* hides ads and so on */
a[href*="http://www.4chan.org/advertise/"],
center+hr,
center > a > img,
div.logo,
div.logo img,
hr,
hr[size="1"][width="90%"],
iframe,
img[src*="http://content.4chan.org/dontblockthis"],
img + br,
td.rules,
#ad{
	display:none !important;
}

#navtop{
	display:block !important;
}

form hr{
	display:block !important;
}

/* rounded corners */
td.reply,
td.replyhl{
	-moz-border-radius:0.4em !important;
}

/* links */
a{
	text-decoration:none !important;
}

a:hover,
a.quotejs:hover{
	font-weight:normal !important;
	text-decoration:underline !important;
}

/* removes borders on page overview [0,1,2...] */
table,
td{
	border:none !important;
}

/* post area */
#header{
	position:static !important;
}

div.postarea > form{
	float:right !important;
}

td.postblock{
	font-size:0.8em !important;
	vertical-align:top !important;
}

input,
textarea{
	border:#aaa 1px solid !important;
	margin-right:2px !important;
}

textarea{
	height:30em !important;
	width:27em !important;
}

.inputtext{
	width:16em;
}

/* blurry thumbnails fix */
img[md5]{
	height:auto !important;
	width:auto !important;
}

}
