css:
@-moz-document domain("zeldauniverse.net") {
.message .messageSidebar {width:120px;}

body, .messageContent, .messageBody, .messageSignature, .messageText{color:#300!important;line-height:23px;}
.messageFooterNote {color:rgba(51, 0, 0, 0.5)!important;}
.quoteBox > header {border-color:rgba(51, 0, 0, 0.3);}
.zuMastheadAvatarWrap > img {
max-width: 197px!important;
  height: auto!important;}

.badge {background-color:#666; margin-right:3px; line-height:15px;}
.badge.blue {background-color:#369!important;}
.badge.userTitleBadge{background:none!important;}

.tabularBoxTitle > header > h2 {font-size:30px; letter-spacing:0px;}
.wbbBoardList .wbbBoard .containerHeadline h3 {font-size:18px;}

.badge.badgeUpdate {display:inline; margin-right:10px;}

.wbbThreadList .table td.zu-topstorybox, .wbbThreadList .table td.zu-topstorybox a {width:150px!important; transition:initial; }
.wbbThreadList .table td.columnSubject h3 a {font-size:20px; font-weight: 400;}
.zu-topstorybox a {
font-size: 16px;
padding-left: 10px;
padding-top: 3px;
font-weight: 500;
line-height:17px;}

.zu-topstorybox a .poster {bottom: 10px;  left: 10px;}
.zu-topstorybox {min-height:100px;}
.wbbBoardList .wbbLastPost {
left: 80%;
width: 20%;
}

.wbbBoardList .wbbBoard .containerHeadline h3 a {font-size:20px;}

.breadcrumbs > ul > li a {font-size:14px; font-weight:500!important;}

.tabularBoxTitle > header {padding:20px;margin:0px 0 1px;
-webkit-box-shadow: inset 0 -3px 10px rgba(161, 116, 29, 0.1);
-moz-box-shadow: inset 0 -3px 10px rgba(161, 116, 29, 0.1);
box-shadow: inset 0 -3px 10px rgba(161, 116, 29, 0.1);
background-color: transparent;
background-image: -webkit-linear-gradient(left, rgba(161, 116, 29, 0.13) 0%, rgba(161, 116, 29, 0.03) 50%);
background-image: -moz-linear-gradient(left, rgba(161, 116, 29, 0.13) 0%, rgba(161, 116, 29, 0.03) 50%);
background-image: -ms-linear-gradient(left, rgba(161, 116, 29, 0.13) 0%, rgba(161, 116, 29, 0.03) 50%);
background-image: -o-linear-gradient(left, rgba(161, 116, 29, 0.13) 0%, rgba(161, 116, 29, 0.03) 50%);
background-image: linear-gradient(left, rgba(161, 116, 29, 0.13) 0%, rgba(161, 116, 29, 0.03) 50%);}

.wbbBoardList .wbbBoard{
-webkit-box-shadow: inset 0 -3px 10px rgba(161, 116, 29, 0.05);
-moz-box-shadow: inset 0 -3px 10px rgba(161, 116, 29, 0.05);
    box-shadow: inset 0 -3px 10px rgba(161, 116, 29, 0.05);}
  
  .table th {color:inherit;}
#main > div > div > .sidebar fieldset .sidebarBoxList li a {min-height:100px;}
}
