css:
/* i really want this to be global */
#header-container {
    background-color: #333;
}

#main-container, #video-container, #add-channel {
    height: 100%;
    max-height: 100%;
    min-height: 0;
}

#toggle-settings {
    opacity: 1;
}

#video-view {
    margin-bottom: 420px;
    background-image: none;
}

#toggle-settings, #settings:not(.open) #toggle-settings:not(:hover):not(:active) {
    background-color: #2B2828;
    opacity: .8;
}

#video-actions, #sponsored-actions {
    opacity: .3;
}

#video-actions:hover, #sponsored-actions:hover {
    opacity: .8;
}

#video-embed {
    width: auto;
    position: relative;
    display: block;
    height: auto;
    padding: 0;
    overflow: hidden;
    padding-bottom: 56.25%;
}

#video-embed > iframe {
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border: 0;
}

#video-title, #video-title a {
    color: gray;
    font-size: 19px;
}

.grid-5 > *, .mobile-grid-5 > *, .grid-10 > *, .mobile-grid-10 > *, .grid-15 > *, .mobile-grid-15 > *, .grid-20 > *, .mobile-grid-20 > *, .grid-25 > *, .mobile-grid-25 > *, .grid-30 > *, .mobile-grid-30 > *, .grid-35 > *, .mobile-grid-35 > *, .grid-40 > *, .mobile-grid-40 > *, .grid-45 > *, .mobile-grid-45 > *, .grid-50 > *, .mobile-grid-50 > *, .grid-55 > *, .mobile-grid-55 > *, .grid-60 > *, .mobile-grid-60 > *, .grid-65 > *, .mobile-grid-65 > *, .grid-70 > *, .mobile-grid-70 > *, .grid-75 > *, .mobile-grid-75 > *, .grid-80 > *, .mobile-grid-80 > *, .grid-85 > *, .mobile-grid-85 > *, .grid-90 > *, .mobile-grid-90 > *, .grid-95 > *, .mobile-grid-95 > *, .grid-100 > *, .mobile-grid-100 > *, .grid-33 > *, .mobile-grid-33 > *, .grid-66 > *, .mobile-grid-66 > * {
    width: 100%;
}

.grid-container {
    max-width: 70%;
}

.icon-equalizer:before {
    color: red;
}

body {
    background-color: #252525;
}

header {
    border-bottom: none;
    margin-bottom: 100px;
}
