css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fark.com") {
  
  /* remove ads and other crap */
    .new_to_fark_p,
    #abPleaBar, 
    .tryBareFark,
    .headlineTable .abbu,
    .headlineTable .spau,
    #rightCol,
    #logo,
    img.tfbadge,
    #headlineCommentsInfoLineThingy,
    #headerMenuContainerNoScript,
    #footerFarkLinks,
    .submitLinkcontainer,
    #copyrightInfo,
    .comlinkcount,
    .howto,
    .refreshRedisplayComments,
    .cpafootnote,
    .commentPostingTable,
    .ad_container {
       display: none !important;
       visibility: hidden !important;
       margin-left: -10000pt !important;
       margin-top: -10000pt !important;
   }
  
  /* other stuff */
  #header {
    height: 0;
    border: none;
  }
  #alsoOnFarkTable {
    margin: 0;
  }
  #newsContainer {
    padding: 0;
  }
  #footer {
    background: none;
  }
  
  /* center the page */
  #newLayoutTable {
    width: 80%;
    margin: auto;
  }
  #leftCol {
    width: 100%;
  }
}

@-moz-document domain("shop.fark.com") {
  
  /* store styles */
  #header > h1 > a > img,
  #header > div {
    display: none !important;
    visibility: hidden !important;
    margin-left: -10000pt !important;
    margin-top: -10000pt !important;
  }
  
  #maintable .sidebar {
    padding-top: 86px;
    background: none;
  }
}
