css:
@-moz-document url-prefix("https://www.bungie.net/en/Forum/"), url-prefix("http://www.bungie.net/en/Forum/") {
/*Your Background*/
body {
background: #000000 url("http://PASTE YOUR IMAGE'S URL HERE") 50% 0 no-repeat fixed ! important;
  -webkit-background-size: cover ! important;
}

/*Transparency*/
.PublicForum .column.main {
background: none repeat scroll 0 0 rgba(0, 0, 0, 0.3) ! important;
}

/*Font Color*/
.forumPost article .post_body, .forumPost article .post_urlLinkOrImage {
color: #E5E5E5 ! important;
}

/*Side Nav Transparency*/
.PublicForum .column.main:before {
background: none repeat scroll 0 0 rgba(221, 225, 228, 0) ! important;
}
        
/*Poster Name Colour*/
.forumPost article h1 a {
color: #E7E7E7 ! important;
}

/*Post Dividers*/
.forumPost .post {
border-top: 1px solid rgba(168, 168, 168, 0.5) ! important;
}
          
/*OP Background Colour*/
#post > .post {
background: none repeat scroll 0 0 rgba(0, 0, 0, 0.25) ! important;
box-shadow: 0 3px 5px -2px rgba(0, 0, 0, 0) ! important;
}
          
/*OP Name Colour*/
.forumPost article h1 a {
color: #E7E7E7;
}
          
/*OP Title Background*/
.header_post {
background: none repeat scroll 0 0 rgba(0, 0, 0, 0.25) ! important;
clear: both;
}

/*OP Title Colour*/
.forumPost .post_title {
color: #E7E7E7! important;
text-shadow: 0 1px 0 rgba(238, 238, 238, 0) ! important;
}

/*Tags Colour-Main Forum*/
.topics .tags, .topics .tags a {
color: #C8C8C8 ! important;
}


/*Thread Title-Main Forum*/
.topics .title {
color: #E7E7E7 ! important;
}

/*New Topic*/
.btn_openCreateNewTopic {
background: url(http://null) ! important;
border: 0px solid #c9cbce ! important;
color: #E7E7E7 ! important;
}

/*Current Tag*/
.searchedTag {
border: 0px solid #c9cbce ! important;
color: #E7E7E7 ! important;
}

/*Forum Sort*/
.container_sort .sort a {
color: #E7E7E7 ! important;
}

.container_sort .sort h2 {
color: #E7E7E7 ! important;
}

/*Spoilers*/
.forumPost .spoiler {
border-width: 0px ! important;
color: #E7E7E7 ! important;
background-color: rgba(0, 0, 0, 0.35) ! important;
}

/*Quotes*/
.forumPost blockquote {
border-style: none ! important;
border-width: 0px ! important;
color: #E7E7E7 ! important;
background-color: rgba(0, 0, 0, 0.35) ! important;
}

/*Tag Nav*/
.nav_side > li {
background: rgba(0, 0, 0, 0.35) ! important;
border-top: 0px solid #479ce4 ! important;
}
  
.nav_side a {
color: #E7E7E7 ! important;
}
    
#nav_tags h4 {
color: #E7E7E7 ! important;
}
    
.nav_side .btn_codeOfConduct {
background: url("http://null") no-repeat scroll 12px center rgba(0, 0, 0, 0) ! important;
padding-left: 55px ! important;
}

#nav_tags .container_textbox {
background: none repeat scroll 0 0 rgba(255, 255, 255, 0.55) ! important;
}

/*Avatar Fix*/
.forumPost article .avatar {
border: 0 solid #000000;
box-shadow: 0 3px 3px -1px transparent inset, 0 0px 0 #FFFFFF ! important;
}

/*Thread Title*/
.forumPost .post_title a:link, .forumPost .post_title a:hover, .forumPost .post_title a:visited {
color: #E7E7E7 ! important;
}
}

@-moz-document url-prefix("https://www.bungie.net"), url-prefix("http://www.bungie.net") {
/*Toolbar*/
header {
background: #090909 ! important;
font-family: 'OpenSansRegular', sans-serif ! important;
border-bottom: 1px solid #090909 ! important;
}

/*Toolbar Logo*/
.nav_logo .logo {
background: url("http://s28.postimg.org/4273pul2l/header_nav_bungie_logo_left.png") no-repeat scroll 0 0 transparent ! important;
height: 54px ! important;
vertical-align: middle ! important;
width: 224px ! important;
display: block ! important;
}

/*Left Nav*/
#leftNav .column1 {
background: #090909 ! important;
}

/*Left Nav Active*/
#leftNav a.current:after {
background: #3C3C3C ! important;
}

#leftNav a:hover, #leftNav a.current {
background: #090909 ! important;
}

#leftNav .moreColumn {
background: #090909 ! important;
}
}
