css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("wiki.dfo-world.com") {

   

    body {

        background: none !important;

        background-color: #000009 !important;

        border-radius: 0.5em !important;

        border: none !important;

        color: #999 !important;

    }
        a {
        color: #1C6F9C !important;
        }
        a:visited {
        color: #1A4A63 !important;
    }
        a:active {
        color: #1A4A63 !important;
    }
        a:hover, a:focus {
        text-decoration: underline !important;
    }
        a.stub {
        color: #772233 !important;
    }
        a.new, #p-personal a.new {
        color: #1A4A63 !important;
    }
        a.new:visited, #p-personal a.new:visited {
        color: #1A4A63 !important;
        }
        a.stub, a.new {
        color: #ba0000 !important;
            
        }
    

    div, span, p, h1, h2, h3, table, tr, td, th, ul, li, input, select {

        background: none !important;

        background-color: #081016 !important;

        border: none !important;

        color: #9C9C9C !important;

        box-shadow: none !important;
        
    }
       li, ul, th, td {
        background-color: #1A1A1A !important;
       }

    hr {   

        background-color: #000009 !important;

    }
    #p-logo a {
        background: url("http://i.imgur.com/oIkFjM3.png") no-repeat scroll rgba(0, 0, 0, 0) !important;
        background-position: right 20px bottom 8px !important;
    }
    
}
