css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rustbyexample.com") {
  .book-header, .page-wrapper, .page-inner > section, .book-summary, .body-inner {
    background-color: #272822 !important;
  }
  
  #active-code, #editor {
    background-color: #2d2e2b !important;
  }
  
  .ace_indent-guide {
    background: none !important;
  }
  
  .ace_line, .page-inner > section > h2, .page-inner > section > h1, .page-inner > section > p, .chapter a, li, .ace_line, .page-inner > section > h, .ace_line, .page-inner > section > h4 {
    color: #C7C7C7 !important;
    border: none !important;
  }
  
  .chapter a:hover {
    color: aliceblue !important;
  }
  
  code, pre {
    background-color: #484848 !important;
    color: #C7C7C7 !important;
  }
  
  .ace_active-line {
    background-color: #383838 !important;
  }
}
