css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("betterexplained.com") {

  body {
    background: #333 !important;
    color: #ccc;
  }
  
  /* Headers */
  h1, h2, h3, h4, h5, h6 {
    color: #e11848;
  }
  
  header h1 {
    color: #aaa !important;
  }
  
  /* Links */
  a:visited {
    color: #B659B6;
  }

  /* Images */
  .entry-content > p > img {
    background-color: #ddd !important;
    color: #222 !important;
    border-color: #d7d7d7 !important;
    border-width: 2pt !important;
    box-shadow: 0px 0px 0px 8px #ddd !important;
    border-radius: 2px !important;
  }
  
  .entry-content > p > img:hover {
    height: 100%;
    width: auto;
  }
  
  /* Block code */
  pre {
    background: #171717 !important;
    color: #bbb !important;
  }
  
  /* Inline code */
  p code {
    background: #222 !important;
    color: #b8b8b8 !important;
    border-color: #222 !important;
  }
  
  /* Right sidebar panels */
  #secondary > .panel {
    background: #333 !important;
    color: #777 !important;
    border-color: #333 !important;
    border-radius: 10px !important;
  }
  
  /* Right sidebar panel headers */
  .panel h1, .panel h2, .panel h3, .panel h4, .panel h5, .panel h6, .panel p, .panel li, .panel dl {
    color: #666 !important;
  }
  
  /* Right sidebar panel images */
  #secondary > .panel > img,
  #secondary > .panel > a > img {
    opacity: 0.05 !important;
  }
  
  #secondary > .panel > img:hover,
  #secondary > .panel > a > img:hover {
    opacity: 1.0 !important;
  }
  
}
