css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(4chan.org) {

body {background:transparent}
html,body {background-color:black}

div.boxbar {background:none;
	background-color:black;
	color:white}
	
div.box-inner {background:none;
	background-color:black;
	color:whitesmoke;}

a.boardlink {color:white!important}

#options-container,
a#option-button 

	
.left-box,
.boxbar  
	
	{color:white!important}
	
	
	
#logo { background:url('http://i.imgur.com/0luTi.png') no-repeat }/* logo hasnt made by me picked up on interwebz*/
	h2 {background-color:black}
	h3 {font-size-adjust:unset}
div.boxbar {background-color:black!important}	
#ft li {background:none;background-color:white}
#ft {display:none}
div#boards,
.left-box,
.right-box
	{background:none;color:red}
	
#ft li.fill {visibility:hidden}
#recent-images li {background:none}
	
}

@-moz-document domain(4chan.org) {

#boardNavDesktop   { margin: 10px }
.boardSubtitle    { margin: 12px }

body {color:white}
span.subject {color:#DD1105!important}
span.quote {color:#789922!important}

a:visited,
a:hover,
a,
a.replylink
	
	{color:white!important}
	
#azk53379,
#azk91603,
#azk98887,
#absbot,
#blotter,
hr.abovePostForm,
.ad-plea,
.aboveMidAd,
.topad.center.ad-cnt,
.center:nth-of-type(3),
.center:nth-of-type(4),
.center:nth-of-type(10),
.bottomad.center.ad-cnt

	{display:none!important}

}
