@-moz-document domain("chiasenhac.vn") {
/*Radios*/
.timtheo .vmk > [type="radio"]:checked,
.timtheo .vmk > [type="radio"]:not(:checked) {
  	width: 0;
  	height: 0;
  	display: none;
  	opacity: 0; 
}

.timtheo .vmk > [type="radio"]:checked + label, 
.timtheo .vmk > [type="radio"]:not(:checked) + label {
    display: inline-block;
    margin: 0px 7px;
    padding-left: 22px;
    padding-top: 2px;
    position: relative;
    cursor: pointer; 
}

.timtheo .vmk > [type="radio"]:checked + label:before,
.timtheo .vmk > [type="radio"]:not(:checked) + label:before {
    position: absolute;
    display: inline-block;
    bottom: 1px;
    left: 0;
    width: 8px;
    height: 8px;
    border: 2px solid #2196f3;
    color: #2196f3; 
}

.timtheo .vmk > [type="radio"]:not(:checked) + label:hover:before {
    background-color: #2196f3; 
}

.timtheo .vmk > [type="radio"]:checked:focus + label:before,
.timtheo .vmk > [type="radio"]:not(:checked):focus + label:before {
    border-color: #6fb5fb;
    box-shadow: 0 0 6px 0 #6fb5fb; 
}

.timtheo .vmk > [type="radio"]:checked[disabled] + label,
.timtheo .vmk > [type="radio"]:not(:checked)[disabled] + label {
    cursor: not-allowed;
    opacity: .4; 
}

.timtheo .vmk > [type="radio"]:checked[disabled] + label:before,
.timtheo .vmk > [type="radio"]:not(:checked)[disabled] + label:before {
    opacity: .7; 
}

.timtheo .vmk > [type="radio"]:checked + label:before,
.timtheo .vmk > [type="radio"]:not(:checked) + label:before {
  	content: "";
  	border-radius: 15px;
  	-webkit-transition: border-color .2s ease-in-out, box-shadow .1s ease-in-out, background-color .2s ease-in-out, box-shadow .2s ease-in-out;
  	-moz-transition: border-color .2s ease-in-out, box-shadow .1s ease-in-out, background-color .2s ease-in-out;
  	-o-transition: border-color .2s ease-in-out, box-shadow .1s ease-in-out, background-color .2s ease-in-out;
  	-ms-transition: border-color .2s ease-in-out, box-shadow .1s ease-in-out, background-color .2s ease-in-out;
  	transition: border-color .2s ease-in-out, box-shadow .1s ease-in-out, background-color .2s ease-in-out; 
}

.timtheo .vmk > [type="radio"]:checked + label:before {
  	color: #4CAF50;
  	border-color: #4CAF50;
  	background-color: #4CAF50;
}

}
