css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {

.identity-refresh, .identity {

	background: #000000;}



ui_notes .ui_note.is_friend .part_activity, .ui_notes .date_header, .date_header {

    color: #000000;}



.identity-refresh .l-container.l-container--two-column .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .left_column, .identity-refresh .l-container.l-container--flex .l-content, .identity-refresh .collection_carousel .tumblelog_mask_item, .identity-refresh .search_results_container .tumblelog_mask_item, .identity-refresh .search_landing_inner_full_wrapper:before {

	background-color: #000000;}



.identity-refresh .l-container.l-container--two-column .right_column, .identity-refresh .l-container.l-container--two-column-dashboard .right_column {

	background-color: #000000;

	width: 229px;}



.identity-refresh .post_full .post_permalink:not(.no-transition--dogear) {

	border-color: #000000 #000000 transparent transparent;}



.identity-refresh .post_full .post_permalink {

	border-color: #000000 #000000 transparent transparent;

	background: #888988;}



.identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container {

	border: 2px solid #212121;}



.identity-refresh .ui_peepr_glass, .post-forms-modal .v-center-inner, .post-forms-glass.active {

    background-color: rgba(0,0,0,0.80);}



.identity-refresh .plexi.show.color, .identity-refresh .glass.blue, .identity-refresh #hello_glass.blue, .identity-refresh #glass_overlay.blue, .ui_dialog_lock.opaque, [data-help-popover="inline-controls"] .post-container-inner:after, [data-help-popover="inline-controls"] .post-form--avatar:after {

	background: rgba(0,0,0,0.80);}



.identity-refresh .controls_section li:not(.section_header):hover {

  	background: rgba(225,225,225,0.05);}

    

.identity-refresh .controls_section.user_list li .follow_list_item_blog:before, .identity-refresh .controls_section.user_list li:hover .follow_list_item_blog:before, .identity-refresh .right_column:after {

  	border-color: transparent;

  	background: transparent;}



.identity-refresh .search_landing_inner {

      background: linear-gradient(to bottom,rgba(33,33,33,1) 0,rgba(33,33,33,1) 330px);}



.l-header-container--refresh, .identity-refresh .search_landing_outer {

	background-color: #212121;}                                



.l-header-container--refresh .tab_notice, .identity-refresh .tab_notice .tab_notice_value, .logged-out-header .login-button {

	color: #212121;}



.discover .blog_row:hover {

    background-color: rgba(255,255,255,0.05);}

	

.discover .blog_row .info:before, .discover .blog_row:hover .info:before {

    display:none;}

              

.discover-controls-wrapper {

    background-color: rgba(22,22,22,0.75);

    border-bottom: none;}

                           

.discover .discover-search-term-item:nth-child(6n+1) .discover-search-term,

.discover .discover-search-term-item:nth-child(6n+2) .discover-search-term,

.discover .discover-search-term-item:nth-child(6n+3) .discover-search-term,

.discover .discover-search-term-item:nth-child(6n+4) .discover-search-term,

.discover .discover-search-term-item:nth-child(6n+5) .discover-search-term,

.discover .discover-search-term-item:nth-child(6n+6) .discover-search-term,

.discover .discover-search-term-item:nth-child(6n+7) .discover-search-term{

    background-color: #888988;}

    

.full_page_landing.search_landing .l-footer-container {

	background: #000000;

    border-top: 0px solid rgba(255,255,255,0.07);}

      

.collection_carousel .blogs_wrapper_carousel:before, .identity-refresh .search_results_container .blogs_wrapper_carousel:before, .identity-refresh .search_results_container .blogs_wrapper_carousel:after {

    background-image: -webkit-linear-gradient(left,#161616,rgba(22,22,22,0.00))}



.dashboard_options_form, .tag_psa {

	border: 1px solid #343434;

	background-color: rgba(255,255,255,0.05);

	color: #888988;}



.dashboard_options_form .dashboard_options_form_disclaimer, .dashboard_options_form .dashboard_options_form_disclaimer a {

	color: #212121;}

                                                                                

.post_avatar, .post_avatar_link, .search_form_row {

	background-color: transparent!important;}



.controls_section .item:hover .icon_plus, .identity-legacy .controls_section .item:hover .icon_plus {

	color: #ffffff;}

        

div.ui_follow:hover, .controls_section li .follow:hover, .identity-legacy div.ui_follow:hover, .identity-legacy .controls_section li .follow:hover {

    background: rgba(80,80,80,0.80);}



.right_column:after, a.radar_avatar:before, .controls_section.user_list li a:before {

	content: none!important;}



.notifications_page .section.divider {

	padding-bottom: 8px;

	background: transparent url("http://i96.photobucket.com/albums/l172/jaygold56/Tumblr%20Stuff/Logo%20and%20Stuff/divider_zps2b8e3ac0.png") 0 bottom no-repeat;}



.post_full .post_avatar .queue .publish_info.day, .post_full .post_avatar .queue .publish_info.time {

    color: #888988}



.compose-button, .compose-button:active, .compose-button:before, .compose-button:focus {

  	color: #212121;

  	background: #888988;

  	border-color: #888988;}



.chrome, .chrome.blue, .logged-out-header .signup-button {

	border-color: #888988;

	background: #888988;}



.split .chrome.blue[disabled], .split .chrome.blue[disabled]:active, .split .chrome.blue.ui_disabled, .split .chrome.blue.ui_disabled:active, .identity-refresh .send_fan_mail, .bubbles>div.active, .paper.active, .font.active, .submit_controls button, .chrome.blue:active, .identity .send_fan_mail:active, .split .create_post_button:active, .identity .controls_section li:not(.section_header):active {

	background: #888988;}      



.button.blue, .button.disabled.blue:active, .button.disabled.blue, .button.dark_gray, .flat-button, .flat-button.blue, .flat-button.blue[disabled], .flat-button.blue.disable, .flat-button.blue[disabled]:active, .flat-button.blue.disabled.active, .post .dock_video_button {

    background-color: #888988;

    border-color: #888988;}



.ui_dialog .chrome, .ui_dialog .chrome.blue, .ui_dialog .buttons .tab_frame.focus .ui_button.blue, .ui_dialog .chrome.ui_button:focus {

	background-color: #888988;

	border-color: #888988;}



.chrome.blue[disabled], .chrome.blue.disabled, .chrome.blue[disabled]:active, .chrome.blue.disabled:active {

    background: #888988;

    border: #888988;}



.plus-follow-button, .l-header-container--refresh .tab_notice, .identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container, .identity-refresh .tab_notice .tab_notice_value {

  	color: #212121;

  	border-color: #212121;}



.plus-follow-button:hover, .identity-refresh .controls_section .item:hover .follow:hover {

    background-color: transparent;}



.ui_notes .ui_note.is_friend .part_main .activity, .ui_notes .ui_note.is_friend .part_main .activity a, .ui_notes .ui_note.is_friend .part_main .summary, .chrome_nav .chrome.sidebar {

    color: #444;}

          

.ui_notes .ui_note.is_friend:hover {

	background-color: #F5F5F5;}



.ui_notes .ui_note.is_friend {

	color: #000000;

	background-color: #EFEFEF;

	border-bottom: 1px solid #D3D3D3;

    border-top: 1px solid #D3D3D3;}



.ui_notes .ui_note {

	border-bottom: 1px solid #D3D3D3;}

                          

.follower {

	border-bottom: 1px solid #D3D3D3;

	background: #EFEFEF;}



#dashboard_controls a, #dashboard_controls .control {

  	color: #888988;

  	text-shadow: #212121 1px 1px 1px;}



#search_actions_search .elevator, #discover_actions_discover .elevator, .elevator-wrapper {

    background-color: transparent;}

    

#auto_pagination_loader {

	text-shadow: #000000 2px 2px 3px;}



#toaster {

    color: transparent;}

}
