css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com")
/* experemntal support Comments: , url-prefix("https://apis.google.com/u/0/wm/4/_/widget/render/comments?usegapi=1&first_party_property=YOUTUBE") */
 {

/* ------ All Around ------ */

.exp-css-ellipsis .yt-ui-ellipsis, .exp-css-ellipsis .yt-uix-expander-ellipsis, .yt-ui-ellipsis {background: transparent !important;}

/* red links: */ .yt-lockup-title a, .feed-item-container:hover .feed-item-header a, .feed-item-container:hover a.yt-user-name, .not-exp-new-metadata-style .yt-lockup:hover .yt-lockup-description a, #watch7-sidebar .video-list-item a:hover .title, .branded-page-module-title a.branded-page-module-title-link:hover, .not-exp-new-metadata-style .yt-lockup:hover a, .not-exp-new-metadata-style .yt-lockup:hover .yt-lockup-meta a, #masthead-expanded .masthead-expanded-menu-item a, #masthead-expanded .masthead-expanded-menu-item .yt-uix-button-link, .not-exp-new-metadata-style .lohp-video-link, .feed-header .feed-manage-link:hover, .branded-page-v2-secondary-col .branded-page-related-channels-see-more a:hover {color: #d2aaeb !important;}


/* ------ Header ---------- */

/*#yt-masthead-container {background: linear-gradient(#f1f1f1, rgba(181, 132, 205, 0.33)) !important;}*/
/* translucive header: */ #yt-masthead-container {background: linear-gradient(#f1f1f1, rgba(181, 132, 205, 0.33)) !important; border-bottom: 0px !important;}
#masthead-expanded-container {background: linear-gradient(rgba(241, 241, 241, 0.9) , rgba(181, 132, 205, 0.33) 20%) !important;border: 0px !important; box-shadow: 0px -25px 40px -45px #888888 inset !important;}
#masthead-appbar {border-bottom: 0px !important; background: rgba(181, 132, 205, 0.33) !important;}
#yt-masthead-container .content-region {display: none !important;}

#yt-masthead #search-btn {background: #d2aaeb !important; border: none !important;}
#masthead-search .search-btn-component .yt-uix-button-content {background: url(
data:image/png;base64,

) no-repeat !important; opacity: 1 !important;}

/* ------ Feed ------------ */

.feed-header {border-bottom: 0px !important; background: linear-gradient(#f1f1f1 60%, rgba(181, 132, 205, 0.33)), radial-gradient(farthest-side at 50% 100%, rgba(0,0,0,.1), rgba(0,0,0,0)) 0 100% !important;}

.site-center-aligned .yt-card {background: transparent !important; box-shadow: none !important;}

.feed-item-container:not(:last-of-type) {background: linear-gradient(#f1f1f1 90%, rgba(181, 132, 205, 0.33)), radial-gradient(farthest-side at 50% 100%, rgba(0,0,0,.1), rgba(0,0,0,0)) 0 100% !important;}
.feed-item-container.legacy-style .feed-item-main {border-bottom: 0px !important;}
.branded-page-v2-secondary-col {background: linear-gradient(270deg, #f1f1f1 90%, rgba(181, 132, 205, 0.33)), radial-gradient(farthest-side at 0% 30%, rgba(0,0,0,.06) 40%, rgba(0,0,0,0)) 0 100% !important;}

.site-center-aligned .branded-page-v2-subnav-container {border-bottom: 0px !important;}

.compact-shelf .yt-uix-button-shelf-slider-pager  {background: #d2aaeb !important;}

/* ------ Guide ----------- */

#guide-container, #appbar-guide-menu, .guide-flyout {background: #d2aaeb !important; border: 0px !important;}
#appbar-guide-menu {top: -1px !important;}
.guide-item, .guide-item-subtitle, .guide-view-more , .guide-count {color: white !important;}
#guide-container li.guide-section h3 a {color: white !important;}
.guide-item.guide-item-selected {background: #f3cffd !important; text-shadow: none !important;}
.guide-item:hover, .guide-item:hover .guide-item-subtitle , .guide-item:hover .guide-count{background: #f1f1f1 !important; color: #d2aaeb !important;}
.guide-view-more:hover {background: #f1f1f1 !important; color: #d2aaeb !important;}

/* optional white shaddow: .guide-section:not(:last-of-type) {border-bottom: 0px !important; background: linear-gradient(#d2aaeb 95%, rgba(181, 132, 205, 0.33)), radial-gradient(farthest-side at 50% 100%, rgba(255,255,255,0.8), rgba(255,255,255,0)) 0 100% !important;}
.guide-section-separator {border-bottom: 0px !important;}*/

.guide-section.signup-promo {color: white !important;}

/* ------ Guide icons ----- */

.guide-item .guide-what-to-watch-icon {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -352px  !important;}
.guide-item .guide-my-channel-icon {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -631px  !important;}
.guide-item .guide-my-subscriptions-icon {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -304px  !important;}
.guide-item .guide-social-icon {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -559px  !important;}
.guide-item .guide-history-icon {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -583px  !important;}
.guide-item .guide-watch-later-icon {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -144px  !important;}
.guide-item .guide-playlists-icon {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -328px  !important;}
.guide-item .guide-likes-playlist-icon {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -24px  !important;}
.guide-item .guide-collection-icon {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -376px  !important;}
.guide-item .guide-builder-icon {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -839px  !important;}
.guide-item .guide-subscription-manager-icon {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -815px  !important;}
.guide-flyout-view-more-icon {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -186px  !important;}


/* ------ Footer ---------- */

#footer-container {background: #f1f1f1 !important; border-top: 0px !important;box-shadow: 0px -25px 40px -45px #888888 !important;}


/* ------ Branded page ---- */

.branded-page-v2-top-row {background: linear-gradient(#f1f1f1 95%, rgba(241,241,241,0)), radial-gradient(farthest-side at 50% 100%, rgba(0,0,0,.1), rgba(0,0,0,0)) 0 100% !important;}
.branded-page-v2-primary-col .branded-page-box:not(:last-of-type), .branded-page-v2-primary-col .branded-page-box.video-player-view-component:last-child, .pl-header.branded-page-boxm, .c4-spotlight-module {border-bottom: 0px !important; background: linear-gradient(#f1f1f1 90%, rgba(181, 132, 205, 0.33)), radial-gradient(farthest-side at 50% 100%, rgba(0,0,0,.1), rgba(0,0,0,0)) 0 100% !important;}
.c4-welcome-primary-col {border-right: 0px !important;}
.c4-welcome-secondary-col {border-left: 0px !important;}
.video-detail a {color: #d2aaeb !important;}
.yt-uix-expander-ellipsis {background: transparent !important;}


/* ------ Watch page ------ */

.video-extras-sparkbar-likes {background: #d2aaeb !important;}
#watch7-content {box-shadow: 25px 0px 40px -45px #888888 !important; }
/* Experimental support Comments: #root, .yJa, .CKa {background: #f1f1f1 !important;}
.Xa {background: linear-gradient(#f1f1f1 95%, rgba(181, 132, 205, 0.33)), radial-gradient(farthest-side at 50% 100%, rgba(0,0,0,.1), rgba(0,0,0,0)) 0 100% !important;} */
.watch8 #watch7-content {background: #fff !important;}

/* ------ Extras ---------- */

.pl-video:not(:last-of-type) {border-bottom: 0px !important; background: linear-gradient(#f1f1f1 90%, rgba(181, 132, 205, 0.33)), radial-gradient(farthest-side at 50% 100%, rgba(0,0,0,.1), rgba(0,0,0,0)) 0 100% !important;}
.yt-uix-button:focus, .yt-uix-button:focus:hover, .yt-uix-button-focused, .yt-uix-button-focused:hover {box-shadow: none !important;}


/* ------ Ads ------------- */

#results .pyv-promoted-videos {background: #eee !important;}
#results .pyv-promoted-videos a {color: #d2aaeb !important;}

}
