css:
@-moz-document domain("gmot.nl") {
/* algemeen en het verdwijnen van het logo */
body {
	background-color: black;
}

.topbar {
	height: 0px;
}
  
.topbar img {
	height: 0px;
}

.maindiv {
	border: none;
    background-color: white;
	border: 1px solid white;
	margin-top: 5px;
	margin-bottom: 5px;
}

td {
	color: white;
	border-color: #444;
}

a:link {
	color: #AAA;
}
a:visited {
	color: #AAA;
}

/* De "topbar2" en inhoud */
.topbar2 {
	background-color: #222;
    background-image: none;
	color: white;
}

input {
	background-color: black;
	color: white;
	border: 1px solid #333;
}
input:hover {
	background-color: #111;
}

input[type=submit], input[type=button] {
	border-radius: 5px;
}

textarea.editor {
	background-color: black;
	color: white;
}

select {
	background-color: black;
	color: white;
}

/* de menubalk */
.menubg {
	background-image: none;
	background-color: #111;
}
.menubg:hover {
	background-color: #282828;
}
.menubg a:link {
	color: white;
}
.menubg2, .menubg2:hover {
	background-image: none;
	background-color: #333;
}

/* De mainpage en de memberbar etc. */
.mainpage {
	background-image: none;
    background-color: #222;
}
.memberbardiv, .memberbardivbottom {
	background-image: none;
	background-color: #555;
}
.memberbar a:link, .memberbar a:visited {
	color: #BBB;
}
.memberbar a:hover {
	text-decoration: underline;
}
.nav, .nav:link, .nav:visited {
	color: white;
}
a.nav:hover, a.nav:link:hover, a.nav:visited:hover {
	color: white;
	text-decoration: underline;
}
    
/* het daadwerkelijke forum */
.tborder {
	border-color: #222;
}
.catbg, .catbg2, .catbg3, tr.catbg3 td {
	background-image: none;
	background-color: black;
    border-color: #999;
    border-bottom: none;
}
.titlebg3, .titlebg, .titlebg2 {
	color: white;
	background-image: none;
    background-color: #181818;
    border-color: #080808;
}

.titlebg, tr.titlebg th, tr.titlebg td, .titlebg2, tr.titlebg2 th, tr.titlebg2 td {
	color: white;
	background-image: none;
    background-color: #181818;
    border-color: #080808;
}

.windowbg, .windowbg2, .windowbg3{
	background-image: none;
    color: white;
}
.windowbg {
	background-color: #333;
}
.windowbg2 {
	background-color: #282828;
}
.windowbg3 {
	background-color: #222;
}

/* De knoppen met acties (antwoord e.d.)*/
.mainstrip, .mainstrip_first, .mainstrip_last {
	background-image: none;
    background-color: black;
    border-top: 1px solid white;
    border-bottom: 1px solid white;
}
.mainstrip_first {
	border-left: 1px solid white;
}
.mainstrip_last {
	border-right: 1px solid white;
}
.mainstrip a:hover {
	color: #CCC;
	font-weight: bold;
}

tr.catbg td {
	background-image: none;
	background-color: #111;
}

/* quotes en Pastebin */
.quote {
	background-color: #999;
}
  
/* alle afbeeldingen */
img[title="Nieuw bericht"] {
	display: block;
	-moz-box-sizing: border-box;
	box-sizing: border-box;
	background: black;
	width: 50px; /* Width of new image */
	height: 50px; /* Height of new image */
	padding-left: 50px; /* Equal to width of new image */
  
    border: 3px solid white;
	border-radius: 5px;
}
img[title="Geen nieuw bericht"] {
	display: block;
	-moz-box-sizing: border-box;
	box-sizing: border-box;
	background: white;
	width: 50px; /* Width of new image */
	height: 50px; /* Height of new image */
	padding-left: 50px; /* Equal to width of new image */
  
    border: 3px solid black;
	border-radius: 5px;
}
img[src="http://www.gmot.nl/Themes/dilbermc/images/lightblue/collapse.gif"], img[src="http://www.gmot.nl/Themes/dilbermc/images/lightblue/expand.gif"] {
	display: block;
	-moz-box-sizing: border-box;
	box-sizing: border-box;
	background-color: white;
	width: 13px; /* Width of new image */
	height: 13px; /* Height of new image */
	padding-left: 13px; /* Equal to width of new image */
  
    border: 3px solid black;
	border-radius: 5px;
}

/* Link kleuren */
.post a:link, .post a:visited, .signature a:link, .signature a:visited {
	color: red;
}
span[style="color: #920AC4;"] {
	color: red !important;
}
.topbar2 .smalltext a:link, .topbar2 .smalltext a:visited {
	color: red;
}
#footerarea a:link, #footerarea a:visited {
	color: red;
}
.quoteheader a:link, .quoteheader a:visited {
	color: #AAA;
}
}

@-moz-document regexp("http:\\/\\/w*\\.*gmot\\.nl\\/.*smartphone.*") {
header h1, section h2, footer h1, footer h3 {
	background: none;
	background-color: #181818;
	border:1px solid white;
  
	text-align: center;
}

section h2 {
	width: auto;
}

header nav > a, section nav > a, footer nav > a, section nav > .pagerNav, footer nav > .pagerNav, header nav > .pagerNav, section article > div, header article > div, footer article > div, label.pollOption {
	background-color: #282828;
}
header nav > a:hover, section nav > a:hover, footer nav > a:hover, section nav > .pagerNav:hover, footer nav > .pagerNav:hover, header nav > .pagerNav:hover, section article > div:hover, header article > div:hover, footer article > div:hover, label.pollOption:hover {
	background-color: #333;
}
section article > div.message, header article > div.message, footer article > div.message {
	background-color: #444;
}

nav a.sticky {
	background-color: #222;
}
nav a.sticky:hover {
	background-color: #252525;
}

section article > div.message a, header article > div.message a, footer article > div.message a {
	color: red;
}

/* de blokkken */
section nav a div, section nav a > a:first-child, header nav a div, header nav a > a:first-child {
	background-color: #999;
}
section nav a div.new, header nav a div.new {
	background-color: black;
	box-shadow: 0px 0px 4px #333;
	-webkit-box-shadow: 0px 0px 4px #333;
}

header nav > a, section nav > a, footer nav > a, section nav > .pagerNav, footer nav > .pagerNav, header nav > .pagerNav, section article > div, header article > div, footer article > div, label.pollOption {
	color: white;
}

/* Textarea e.d. */
textarea, form input[type="submit"] {
	background-color: black;
    color: white;
	border-color: white;
}
form input[type="submit"]:hover {
	background-color: #111;
}

/* En alles weer witten: */
.nav, .nav:link, .nav:visited, a:visited, a:link {
	color: white;
}
footer, footer > a:link, .message {
	color: white;
}
article > div a.icon {
	color: red;
}

/* de pijltjes */
section nav .pager, footer nav .pager, header nav .pager {
	background-color: #AAA;
}
section nav .pager:hover, footer nav .pager:hover, header nav .pager:hover {
	background-color: #bbb !important;
}
}
