css:
@-moz-document url(http://www.w3schools.com/html/tryit.asp){
}

@-moz-document domain( "w3schools.com" ) {
  
  
  body {
    background-color: #060606;
    color: #FFF;
  }
  
  html {
    background-color: #060606;
    color: #FFF;
  }
  
  h1, h2, h3, h4, h5, h6 {
    color: #CCC;
  }
  
  .highVAL {
    color: #50A0FF;
}
  .highELE {
    color: #FF7070;
}
  
  table.reference {
    border-left: 1px solid #222;
    border-right: 1px solid #222;
    border-bottom: 1px solid #222;
}
  
  table.reference tr:nth-child(2n+1) {
    background-color: #090909;
}
  
div.code {
    border: 1px solid #333;
    background-color: #050505;
    color: #BBB;
}
  
  table.reference tr:nth-child(2n) {
    background-color: #000;
}
  
  table.reference > thead > tr > th, table.reference > tbody > tr > th, table.reference > tfoot > tr > th, table.reference > thead > tr > td, table.reference > tbody > tr > td, table.reference > tfoot > tr > td {
    border-top: 1px solid #222;
}
  
  .top10 h3 {
    color: #EEE;
}
  
  #leftcol a {
    color: #808080;
  }
  
  div.boxDemo {
    background-color: #0C0C0C;
    border: 1px solid #1A1A1A;
    color: #BBB;
}
  
  #menyen {
    background-color: #0C0C0C !important;
}
  
  .menu {
    background-color: #0C0C0C !important;
}
  
  .menu a:hover {
    background-color: #333;
}
  
  .footer a {
    color: #AAA;
}
  
  a.btn {
    background-color: #0C0C0C;
    border: 1px solid #1A1A1A;
    color: #EEE;
}
  
  .w3DropdownMenu a {
    color: #EEE;
}
  
  a.btn:hover {
    background-color: #BBB;
    color: #111;
    border: 1px solid #BBB;
}
  
  .menu a{
    color: #EEE;
}
  
  div.boxDemo a.btn:hover {
    background-color: #BBB;
    color: #111;
    border: 1px solid #BBB;
  }
  
  div.boxDemo a.btn {
    background-color: #000;
}
 
  td.gsc-search-button input.gsc-search-button {
    border: medium none #555 !important;;
    background-color: #555 !important;;
}
  
  input {
    background-color: #060606 !important;
    background: none no-repeat scroll left center !important;;
}
  
  input.gsc-input, .gsc-input-box, .gsc-input-box-hover, .gsc-input-box-focus {
    border-color: #303030;
    color: #FFFFFF;
}
  
  .gsc-input-box {
    border: 1px solid #303030;
    background: none repeat scroll 0% 0% #040404;
    color: red;
}
  
  .boxRef a{
    color:#AAA;
  }
  
  div.about a {
    color: #999;
}
  
  .bottom a {
    color: #999;
}
  
  #page {
    background-color: #000;
    box-shadow: 0px 10px 20px 3px #303030;
    color: #FFF;
}
  
  div.image {
    box-shadow: 0px 10px 20px 3px #303030;
  }
  
  .top {
    background-color: #000;
}
  
  .topnav a:hover, .topnav a:active, .topnav a:focus {
    background-color:#121212 !important;
    color:#FFF !important;
    border-top-style:solid;
    border-top-width: 2px;
    border-top-color: #8AC007;
    border-bottom-style: solid;
    border-bottom-width: 2px;
    border-bottom-color: #8AC007;
}
  
  .w3DropdownMenu {
    background-color: #121212;
    border-color: -moz-use-text-color #555 #555;
    box-shadow: 0px 4px 4px -2px #777;
}

  .w3DropdownClose:hover {
    background-color: #EEE !important;;
}
  
  .close {
    color: #FFF;
    text-shadow: 0px 1px 0px #000;
}
  
  p.intro {
    color: #C0C0C0;
}
  
  .sidesection a {
    color: #FFF;
}
  
  hr {
    border-color: #222 -moz-use-text-color -moz-use-text-color;
}
  
  #divpreview {
    border-color: #363636 #363636 -moz-use-text-color !important;
}
  
  button, input, optgroup, select, textarea {
    border-style: solid;
    border-left-color: #222;
    border-top-color: #222;
    border-bottom-color: #111;
    border-right-color: #111;
}
  
  a.tryitbtn, a.tryitbtn:link, a.tryitbtn:visited, a.showbtn, a.showbtn:link, a.showbtn:visited {
    color: #000;
}
  
  a.tryitbtn:hover, a.tryitbtn:active, a.showbtn:hover, a.showbtn:active {
    background-color: #000;
    color:#FFF
}
  
  table.lamp td, table.lamp th {
    background-color: #000;
}
  
  table.lamp {
    border: 1px solid #282828;
}
  
  .main a {
    color: #FFF;
}
  
  div.example {
    color: #FFF;
    background-color: #0E0E0E;
    border: 1px solid #141414;
}
  
  div.example_code {
    background-color: #000;
    border-left: 4px solid #8AC007;
}
  
}
