css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("steamgifts.com") {



.giveaway__row-outer-wrap {
    position: static;
    display: inline-block;
  	margin: 0; padding: 0;
}
.giveaway__row-outer-wrap .giveaway__row-inner-wrap {
  	position: relative;
    height: 95px;
    width: 196px;
  	padding:3px; margin:0px;
  	left:0;top:0;
}
.giveaway__row-outer-wrap .giveaway__column--width-fill,
.giveaway__row-outer-wrap .global__image-outer-wrap--avatar-small,
.giveaway__row-outer-wrap .fa-tag ~ span,
.giveaway__row-outer-wrap .fa-comment ~ span,
.giveaway__row-outer-wrap .esg-win-chance {
    display: none!important;
}
.giveaway__row-outer-wrap .giveaway__heading__name {
    text-align: center;
    position: absolute;
    left: 0; top:0;
    width: 100%;
    z-index: 1;
    max-height: 44px;
    overflow: hidden;
    background-color: rgba(255,255,255,0.95);
    padding: 0;
    visibility: hidden;
}
.giveaway__row-outer-wrap:hover .giveaway__heading__name {
    visibility: visible;
}
.giveaway__row-outer-wrap .global__image-outer-wrap--game-medium {
    position: absolute;
    z-index: 0;
  	left:3px; top:3px;
}
.giveaway__row-outer-wrap .giveaway__heading__thin {
    position: absolute;
}
.giveaway__row-outer-wrap .giveaway__heading__thin:not(:last-of-type) {
    z-index:1;
    background: rgba(255,255,255,1);
    padding: 0 2px 2px 2px;
    border-radius: 2px;
    left: 0;
    bottom: 20px;
}
.giveaway__row-outer-wrap .giveaway__heading__thin:last-of-type {
    bottom: 0;
    right: 0;
}
.giveaway__row-outer-wrap .giveaway__column--group {
    position: absolute;
    right: 0;
}
.giveaway__row-outer-wrap i.giveaway__icon {
    position: absolute;
    bottom: 1px;
    left: 37px;
  	opacity: 0.85;
}
.giveaway__row-outer-wrap a.giveaway__icon {
    position: absolute;
    bottom: 1px;
    left: 56px;
  	opacity: 0.85;
}
.giveaway__row-outer-wrap .giveaway__links {
    position: absolute;
    display: inline-block;
    left: 4px; bottom: 1px;
    height: auto;
}
.giveaway__row-outer-wrap .giveaway__links a {
    margin-right: 3px;
}
.giveaway__row-outer-wrap .giveaway__columns {
    position: absolute;
    z-index: 1;
    top: 56px;
    width: 196px;
}
.giveaway__row-outer-wrap .fa-comment {
  	padding-bottom: 2px;
}
.giveaway__row-outer-wrap .fa-eye-slash,
.giveaway__row-outer-wrap .fa-steam {
  	padding-bottom: 1px;
}
.giveaway__row-outer-wrap .giveaway__columns > div {
    position: absolute;
    padding: 0 3px;
    background-color: rgba(255,255,255,0.25);
    color: rgba(107,137,124,0.25)!important;
    text-shadow: 1px 1px rgba(255, 255, 255, 0.25);
    border: 1px solid rgba(210, 214, 224, 0.25);
    background-image: none!important;
  	visibility: hidden;
}
.giveaway__row-outer-wrap .giveaway__columns > div:last-child {
    right: 0;
}
.giveaway__row-outer-wrap:hover .giveaway__columns > div {
    background-color: rgba(255,255,255,0.94);
    color: rgba(107,137,124,0.94)!important;
    text-shadow: 1px 1px rgba(255, 255, 255, 0.94);
    border: 1px solid rgba(210, 214, 224, 0.94);
  	visibility: visible;
}
.giveaway__row-outer-wrap .esg-join {
    position: absolute;
    left:74px; bottom:0;
  	margin:0;
}
.giveaway__row-outer-wrap .esg-join a {
  	padding: 1px 2px 0px 2px; margin: 0;
  	left: 0; top: 2px;
    height: 14px;
  	line-height: 0;
  	color: black;
  	border: 0;
}
.giveaway__row-outer-wrap .esg-join a:before {
  	content: "\f046";
    font: normal normal normal 14px/1 FontAwesome;
    padding-right: 1px;
}



}
