css:
@-moz-document domain("dist.pntu.edu.ua") {
* {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}

  
#headerlogo {
  margin-left: -8px;
} 
  

.loginform label {
    line-height: 3em;
}

.loginpanel label{
	vertical-align: text-bottom;
}

  
/*
a {
    border-bottom: 1px dotted blue;
}

a:hover {
    border: none;
}
*/
.headermenu {
	font-size: 1.1em!important;
}

div#page-header {
    border-radius: 0!important;
    background-color: #4ECDC4!important;
    color: white!important;
}

.navbar {
    background: #556270!important;
    padding: 2px!important;
    top: -1px;
    position: relative;
}

.navbar a {
    color: #C7F464!important;
}

a.collapseexpand {
    float: right!important;
    position: relative!important;
    top: -2.3em!important;
    right: 1em!important;
}

.block .content {
    background: #FFFFFF!important;
}

.block .header .title, th.header, .formtable tbody th, .generalbox th, .generaltable th.header {
    background: #bdc3c7!important;
}

li.listentry {
    line-height: 2.5em!important;
    border-top: 1px solid #DAE4EC!important;
}

.course_category_tree .category>.info {
    background: #C1C6CB!important;
    border: none!important;
    border-top-left-radius: 4px!important;
    padding: 3px!important;
    margin: 5px 0 0 0!important;
}

.courses .coursebox {
    background: transparent!important;
}
.courses .coursebox:hover {
    background: rgba(0, 0, 0, 0.1)!important;
}
.courses .coursebox:not(.first) {
  border-top: 1px solid rgba(0, 0, 0, 0.25)!important;
}

.categoryname > a {
    display: block!important;
}

.category > .content {
    background: rgba(0, 0, 0, 0.03)!important;
}

.coursebox .content {
  background: rgba(232, 232, 232, 1)!important;
  border: 1px dotted rgb(0, 13, 255)!important;
}

.course_category_tree .category>.content {
    padding-left: 2em!important;
}

input[type="submit"] {
    border: 1px solid #2980b9!important;
    padding: .5em!important;
    border-radius: 4px!important;
    cursor: pointer!important;
    background: #3498db!important;
    color: white!important;
    margin: 0 0.2em!important;
}

input[type="submit"]:hover {
    background: #2980b9!important;
}

select {
    border: 1px solid #ecf0f1!important;
    padding: 1.5%!important;
    border-radius: 4px!important;
}

input[type="text"], input[type="password"] {
    border: 1px solid #34495e!important;
    padding: .3em!important;
    border-radius: 4px!important;
    margin: .5em!important;
}

.navbutton input[type="text"] {
    margin: 0 1em!important;
}

input#searchform_search {
    width: 85%!important;
}

.langmenu {
  position: relative;
  left: -11em;
  top: -.9em;
}

.block_navigation .block_tree li.item_with_icon > p img, .block_navigation .block_tree .type_activity > p.tree_item.active_tree_node img, .block_navigation .block_tree li > p.hasicon img {
  top: 0!important;
}

.block_online_users .content .list li.listentry .user {
    max-width: 170px;
}

/* border radius fix */
#frametop, #framebottom, #frametopright, #frameleft, #frameright {
	margin: 0;
	padding: 0;
	background: white;
	height: inherit;
	width: inherit;
}

#wrapper {
    padding: 0;
}


#footerframetop {
    display: none;
}

#framebottomright, #footerframebottomright {
    height: 0!important;
    background: transparent;
    margin: 0;
    border-bottom: 1px solid #c1c6cb;
}

#page-content #region-main-box, #region-post-box {
    border-left: 1px solid #c1c6cb;
}

#region-main {
    border-right: 1px solid #c1c6cb;
}

.userprofile {
    padding: 1em;
}

.userprofile li {
    list-style: none;
}
}

@-moz-document url-prefix("http://dist.pntu.edu.ua/course/index.php?categoryid=") {
.courses.category-browse > .coursebox.clearfix.odd.first.last:hover{
	background: transparent;
}
}

@-moz-document url("http://dist.pntu.edu.ua/login/index.php"), url("http://dist.pntu.edu.ua/"), url("http://dist.pntu.edu.ua/index.php?") {
.langmenu {
  position: relative;
  left:0px;
  top: 1.1em;
}
}

@-moz-document url-prefix("http://dist.pntu.edu.ua/mod/lesson/view.php?id=") {
#region-main {
    padding: 1em;
    font-size: 1.3em;
}
}

@-moz-document regexp("http\\:\\/\\/dist\\.pntu\\.edu\\.ua\\/.+") {
#region-main a {
   /*text-decoration: underline;*/
	color: blue;
}
}
