css:
@-moz-document url-prefix("https://web.whatsapp.com/") {
#compose-input .block-compose .input-container,#compose-input .input,.block-compose #compose-input .input-container{color:black!important}
.app{height:100%;top:0px;width:100%}
.app-wrapper::before{display:none!important}
.chat{border-bottom:1px solid #000}
.chat,.location{background-color:#1C212D}
.chat-meta{color:#838484!important}
.chat-status,.header-secondary,.location-secondary{color:#45A5D1!important}
.chat-title ,.header-title ,.location-main,.dropdown .menu-item a{color:#45A5D1!important;font-family:'mario',cursive !important}
.chat:hover,.location:hover{background-color:#374056}
.chatlist .active.location,.chatlist .chat.active{background-color:#374056;border-bottom:1px solid #000}
.chatlist .active.location:hover,.chatlist .chat.active:hover,.infinite-list-item:hover{background-color:#374056;border-bottom:0px solid #000!important}
.chatlist .chat:after,.chatlist .list-title:after,.chatlist .location:after{border-bottom:0px solid #000}
.chatlist .chat:hover,.chatlist .location:hover{background-color:#374056;border-bottom:1px solid #000}
.cont-input-search{width:calc(100% - 35px)}
.cont-input-search,.input-search,.block-compose .input-container{background-color:rgba(162,173,178,0.5);border:none}
.drawer-body,.dropdown{background:rgba(0,0,0,1)}
.drawer-container-right,.pane-chat,.pane-intro{width:73%!important}
.drawer-info{background:rgba(0,0,0,0.0)}
.drawer-info .drawer-body{background:linear-gradient(175deg,rgba(0,0,0,1) 1%,rgba(69,72,77,1) 50%,rgba(0,0,0,1) 100%)}
.drawer-profile .drawer-header{background:rgba(0,0,0,0.5) !important}
.drawer-section-title .col-main,.drawer-title .drawer-title-body{color:#45A5D1!important;font-family:'mario',cursive !important}
.drawer-value{color:#838484!important}
.dropdown .menu-item a:hover{background:rgba(0,0,0,0.0);color:#A2ADB2!important;font-family:'mario',cursive !important}
.dropdown-right:after{background:black;box-sizing:border-box;color:#45A5D1!important;content:"Style by Pimp My Site";cursor:pointer;display:block;font-family:'mario',cursive !important;font-size:14.5px;left:0;max-width:200px;padding:2px 58px 15px 24px;position:absolute;top:100%;white-space:nowrap;z-index:20px}
.emoji-panel{background:linear-gradient(to bottom,#606C88 0%,#3F4C6B 100%)}
.icon-smiley,.icon-hide,.block-compose .icon-ptt,.block-compose .icon-send,.icon-menu,.icon-chat,.icon-clip,.menu-horizontal>.menu-item,.menu-horizontal>span>.menu-item{background-color:#A2ADB2;border-radius:55px;padding:6px}
.input-search{background-color:rgba(162,173,178,0.0);border:none}
.intro-image{background-image:url(https://dl.dropboxusercontent.com/u/27347691/whatsappwebplus/whatsap55.png) !important;background-size:cover!important;height:500px;width:356px}
.list-title,.drawer-header,.drawer{background:rgba(0,0,0,1);border-radius:none}
.media-panel-header{background:rgba(0,0,0,1) !important}
.media-viewer{background:rgba(0,0,0,0.65) !important}
.menu-tabs-emoji{background:rgba(0,0,0,0.0)}
.message-in{background:linear-gradient(to bottom,#E2E2E2 0%,#DBDBDB 50%,#D1D1D1 51%,#FEFEFE 100%);box-shadow:2px 2px 4px #888}
.message-out{background:linear-gradient(to bottom,#B8E1FC 0%,#A9D2F3 10%,#90BAE4 25%,#90BCEA 37%,#90BFF0 50%,#6BA8E5 51%,#A2DAF5 83%,#BDF3FD 100%);box-shadow:2px 2px 4px #888}
.pane{height:100%!important}
.pane,.block-compose{background-color:#000}
.pane-chat-body{background-image:url(https://dl.dropboxusercontent.com/u/27347691/Siluet-Background.jpg) !important;background-size:cover!important}
.pane-chat-header{background:#000}
.pane-header{background:#000}
.pane-list-body{background-color:#323232}
.subheader-search{background:rgba(0,0,0,0.0);border-radius:none}
.well.animate-enter{background:rgba(0,0,0,0.0)}
@font-face{font-family:mario;src:url(https://dl.dropboxusercontent.com/u/27347691/Super-Mario-64-DS.ttf)}
}
