css:
@-moz-document domain("psim.us"), domain("play.pokemonshowdown.com") {
  body {
    background:url("http://lakevalor.net/public/style_images/LV/custom/bg/background1.jpg") !important;
    background-color: #344B6C !important;
    color: #FFF;
  }
     
  .ps-room.ps-room-light.scrollable, #header {
    font-family: helvetica,arial,sans-serif;
    background: rgba(0, 0, 0, 0.9);
    color: #F2F2F2;
  }
  
  .chatbox label {
    text-shadow: none !important;
  }

  .mainmenufooter small:after{
    content:"Made by: Charles Paroissien"; 
    font-size:9pt; 
    color:#cccccc; 
    text-shadow:0 1px black; 
    position:absolute; 
    size:6px; 
    left:230px; 
    top:21px; 
    width: 300px;
  }
  
  .roomlist {
    font-family: helvetica,arial,sans-serif;
    color: #FFF;
  }
  
  .roomlist a.ilink {
    border: 1px solid #000;
    color: #000;
    background: #FFF;
  }
  
  .roomlist a.ilink:hover {
    border: 1px solid #000;
    color: #1D90FF;
    background: #DBE8EB;
  }
  
  .roomlist a.ilink small {
    color: #000;
  }
  
  
  .mainmenufooter a{
    text-decoration: none;
    color: #FFF;
    text-shadow: 1px 0px #000;
    font-family: helvetica,arial,sans-serif;
  }
  
  .mainmenufooter a:hover {
    color: #1D90FF;
    text-decoration: none;
  }
  
  .header .logo {
    opacity: 0;
  }
  
  .battleform label {
    background: url("http://i.imgur.com/yFWeHx0.png") no-repeat scroll left center transparent !important;
    padding: 4px;
    opacity:0.9;
    color: #FFF;
  }
  
  .menugroup {
    background: rgba(0, 0, 0, 0);
  }
  
  .menugroup .button.mainmenu1, .menugroup .button.mainmenu2, .menugroup .button.mainmenu3, .menugroup .button.mainmenu4, .menugroup .button.mainmenu5 {
    background: none repeat scroll 0% 0% #474747;
    color: #FFF;
    text-shadow: 0px -1px rgba(0, 0, 0, 0.8);
    border-color: #212121;
    font: 13px helvetica,arial,sans-serif;
    width: 230px;
  }
  
  .menugroup .button.mainmenu1:hover, .menugroup .button.mainmenu2:hover, .menugroup .button.mainmenu3:hover, .menugroup .button.mainmenu4:hover, .menugroup .button.mainmenu5:hover {
    background: none repeat scroll 0% 0% #474747;
    color: #D8D8D8;
    border-color: #212121;
  } 
  
  .select, .select:disabled {
    border-color: #212121;
    border-top-left-radius:0px;
  }
  
  .select {
    background: #EEEEEE !important;
    color: black !important;
  }
  
  .select:disabled, .select:disabled:hover {
    background: rgba(220,220,220,.65) !important;
  }
  
  .select:hover {
    color: #000;
  }
  
  .chat-log, .pm-log, .battle-log {
    font: 11px helvetica,arial,sans-serif;
    background:url("http://i.imgur.com/Q8vHT0Y.png") repeat;
  }
  
  .chat-log-add, .battle-log-add {
    background: #3C3C3C;
  }
  
  .pm-log-add {
    background:rgba(0, 0, 0, 0.8);
  }
  
  .userlist li button {
    font: 12px helvetica,arial,sans-serif;
    text-shadow: 1px 1px #000;
  }
  
  .userlist li button:hover {
    background: #1C1C1C;
  }
  
  .userlist {
    background: #3C3C3C;
    border-right: 1px solid #3C3C3C;
  }
  
  .userbar .username {
    font: 15px helvetica,arial,sans-serif;
    font-weight: bold;
  }
  
  .userbar .username:hover {
    border-bottom: 1px solid #1D90FF;
  }
  
  a {
    color:#40A0FF;
    text-decoration:none;
    margin-left: 3px;
    margin-right: 3px;
  }
    
  a:hover {
    color:#0080FF;
    text-decoration: underline;
  }
  
  .ps-popup {
    background:rgba(78, 78, 78, 0.95);
    font: 14px helvetica,arial,sans-serif;
    color: #FFF;
    border-color:#212121;
  }
  
  .popupmenu button {
    box-shadow: none;
    color: #FFF;
  }
  
  .popupmenu button:hover {
    box-shadow: none;
    color: #FFF;
    background:rgba(78, 78, 78, 1);
  }
  
  .popupmenu button.sel:hover {
    padding: 0px 3px;
    border: 1px solid #AAA;
    background:rgba(65, 65, 65, 1);
  }
  
  .ps-overlay {
    background: rgba(0, 0, 0, 0.3);
  }
    
  .chatbox label {
    font: 12px helvetica,arial,sans-serif;
    text-shadow: 1px 1px #000;
  }
  
  a.ilink {
    /*font-style: italic;*/
    text-decoration: none;
    color: #C9C9C9;
  }
  
   a.ilink:hover {
    color: #929292;
  }
  
  .popupmenu h3 {
    color: #1D90FF;
  }
  
  .broadcast-red {
    background: none repeat scroll 0% 0% #EC5C4D;
    color: #000;
    border: 2px solid #000;
    padding: 5px;
    font-family: helvetica,arial,sans-serif;
    font-size: 10pt;
  }
  
  .broadcast-blue {
    background: none repeat scroll 0% 0% #8FA6B3;
    color: #000;
    border: 2px solid #000;
    padding: 5px;
    font-family: helvetica,arial,sans-serif;
    font-size: 10pt;
  }
  
  .broadcast-green {
    background: none repeat scroll 0% 0% #9BB38F;
    color: #000;
    border: 2px solid #000;
    padding: 5px;
    font-family: helvetica,arial,sans-serif;
    font-size: 10pt;
  }
  
  .pm-window h3 {
    border: 1px solid #212121;
    color: #FFF;
    text-shadow: none;
    background:rgba(0, 0, 0, 0.7);
    font-family: helvetica,arial,sans-serif;
  }
  
  .pm-window h3:hover, .pm-window.focused h3, .pm-window.focused h3:hover {
    border: 1px solid #212121;
    color: #1D90FF;
    text-shadow: none;
    background:rgba(0, 0, 0, 0.8);
    font-family: helvetica,arial,sans-serif;
  }
  
  .message-pm {
    color: #000;
    font-style: italic;
    background: #D0D0D0;
  }
  
  .revealed {
    color: #FF0000;
  }
  
  .maintabbarbottom {
    background: #1D90FF;
    border: 1px solid #000;
    box-shadow: none;
  }
  
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    color: #FFF;
    background: #1D90FF;
    border: 1px solid #000;
    font-family: helvetica,arial,sans-serif;
    text-shadow: none;
  }
  
  .tabbar a.button {
    color: #000;
    background:#FFF;
    border: 1px solid #000;
    font-family: helvetica,arial,sans-serif;
    text-shadow: none;
  }
  
  .tabbar a.button:hover {
    color: #000;
    background: #C9C9C9;
    border: 1px solid #000;
    font-family: helvetica,arial,sans-serif;
    text-shadow: none;
  }
  
  button {
    border: 1px solid #212121;
    background: none repeat scroll 0% 0% #474747;
    color: #FFF;
    font: 12px helvetica,arial,sans-serif;
  }
  
  button:hover {
    color: #D8D8D8;
  }
  
    .tournament-bracket {
    font-size: 8pt;
    background:url("http://i.imgur.com/Q8vHT0Y.png");
    opacity:.8;
  }

  .tournament-title {
    background:#FFF;
    color:#000;
  }
  
  .tournament-status, .tournament-toggle {
  color: #FFF;
    background: #464646;
  }
  
  .tournament-tools {
    padding: 10px;
    border-top: 1px solid #AAA;
    background:url("http://i.imgur.com/Q8vHT0Y.png");
  }
  
    .regular-button {
    box-shadow: 0px 1px 0px 0px #FFFFFF inset, 0px 2px 3px rgba(0, 0, 0, 0.4);
    border: 1px solid #A8A8A8;
    background: #EEEEEE;
    color: #000;
  }

  .regular-button:hover {
    box-shadow: 0px 1px 0px 0px #FFFFFF inset, 0px 2px 3px rgba(0, 0, 0, 0.4);
    border: 1px solid #A8A8A8;
    background: #DBDBDB;
    color: #000;
  }

  .blue-button{
    border: 1px solid #474747;
    color: #474747;
    background: #6F9BB5;
    box-shadow: 0px 1px 0px 0px #92B0C1 inset, 0px 2px 3px rgba(0, 0, 0, 0.4);
  }

  .blue-button:hover {
    border: 1px solid #474747;
    color: #474747;
    background: #7CACC7;
    box-shadow: 0px 1px 0px 0px #92B0C1 inset, 0px 2px 3px rgba(0, 0, 0, 0.4);
  }

  .LV-button{
    border: 1px solid #212121;
    color: #FFF;
    background: rgba(0, 0, 0, 0.8);
    box-shadow: 0px 1px 0px 0px #414141 inset, 0px 2px 3px rgba(0, 0, 0, 0.4);
  }

  .LV-button:hover {
    border: 1px solid #212121;
    color: #1D90FF;
    background: rgba(0, 0, 0, 0.8);
    box-shadow: 0px 1px 0px 0px #414141 inset, 0px 2px 3px rgba(0, 0, 0, 0.4);
  }
  
  .closebutton, .minimizebutton {
    color: #F2F2F2;
  }
  
  .minimizebutton:hover {
    color: #FFDD7D;
    text-decoration: none;
  }
  
  .closebutton:hover {
    color:#FF827D;
    text-decoration: none;
  }
  
  .setchart-nickname {
    background: #1D90FF;
  }
  
  .setmenu button {
    background:rgba(29, 144, 255, 0.3);
    color: #FFF;
  }
  
  .setmenu button:hover {
    background:rgba(29, 144, 255, 0.5);
    color: #FFF;
  }
  
  .setchart {
    background:#1D90FF;
  }

  .utilichart .movenamecol {
    color: #FFF;
    font: 12px helvetica,arial,sans-serif;
  }
  
  .teambuilder-results h3, .teambuilder-results .utilichart h3 {
    background: rgba(0, 0, 0, 0.95);
    color: #FFF;
    border: 1px solid 212121;
    box-shadow: none;
  }
  
  .teambar button {
    background: #474747;
    color: #FFF;
  }
  
  .teambar button:hover {
    background: #2F2F2F;
    color: #FFF;
  }
  
  .teambar button:disabled, .teambar button:disabled:hover, .teambar button:disabled:active {
    background: #474747;
    color: #FFF;
  }
  
  .utilichart .namecol {
    color: #FFF;
  }
  
  .switchmenu button, .movemenu button {
    border: 3px solid #222222;
    color: #000;
  }
  
  .switchmenu button:hover, .movemenu button:hover {
    border: 3px solid #222222;
    color: #222222;
  }
  
  .switchselect button:hover {
    color: #458;
  }
  
  .moveselect button:hover {
    color: #842;
  }
  
  .ps-room.ps-room-opaque {
    background: rgba(0, 0, 0, 0.9);
    color: #FFF;
  }
  
  .battle-log h2 {
    background:#3C3C3C;
    color: #FFF;
    border: 1px solid #212121;
  }
  
  .battle-log .chat strong {
    color: #0080FF;
  }
  
  .leftbar {
    background: rgba(0, 0, 0, 0.9);
  }
  
  .rightbar {
    background: rgba(0, 0, 0, 0.9);
  }

  .ps-room .battle-controls {
    background: transparent !important;
  }
   
}
