css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("basilmarket.com") {

/* Background */
.day {background:url("http://s220.photobucket.com/albums/dd121/666smalls666/Basil Army Theme/BasilArmyBackgroundDay.jpg") top repeat-x #783 !important;}
.night {background:url("http://i220.photobucket.com/albums/dd121/666smalls666/Basil Army Theme/BasilArmyBackgroundNight.jpg") top repeat-x #783 !important;}

/* Top Banner */
.innerWrap			{width:800px !important;height:80px !important;padding:0px 0 0 20px !important;margin-left:auto !important;margin-right:auto !important;color:#ccc !important;}
.innerWrap img	{}
.innerWrapday		{background:url("http://i220.photobucket.com/albums/dd121/666smalls666/Basil Army Theme/BasilArmyBackDay.jpg") top repeat-x !important;}
.innerWrapnight		{background:url("http://i220.photobucket.com/albums/dd121/666smalls666/Basil Army Theme/BasilArmyBackNight.jpg") top repeat-x !important;}
.innerWrapPP1	{float:left !important;padding:0 !important;width:249px !important;height:80px !important;}
.innerWrapPP2	{width:128px !important;height:80px !important;padding:0 !important;}

/* Menu */
.innerMenuWrap    {color:#b2b2b2 !important;background:url(http://www.basilmarket.com/img/menu-back.gif) no-repeat !important;padding:5px !important;}
.innerMenuWrap2	{background-color:#333 !important;height:80px !important;}
.bwnight	{background:url(http://www.basilmarket.com/img/bodywrap-night.gif) top no-repeat #b2b2b2 !important;}
.bwday		{background:url(http://www.basilmarket.com/img/bodywrap-day.gif) top no-repeat #b2b2b2 !important;}

/* Search Function Bar and Front Page "What this topic is" Part */
.misc1 {background-color:#dcffdc !important;}

/* Body (2nd to back background) */
#bodyWrap 		{padding:0 10px 10px 10px !important;padding-top:0 !important;width:800px !important;margin-left:auto !important;margin-right:auto !important;text-align:left !important;clear:both !important;background:#e9ffe9 !important;}

/* Headers in Threads */
.right {background:url("http://i220.photobucket.com/albums/dd121/666smalls666/Basil Army Theme/BasilArmyRight2.jpg") repeat-x #5a5900 !important;color:#fff !important;font-size:12px !important;font-family:tahoma !important;font-weight:bold !important;}
.topicBar, .bbTop 		{color:#fff;padding:10px !important;background:url("http://i220.photobucket.com/albums/dd121/666smalls666/Basil Army Theme/BasilArmyTopicbar.jpg") top no-repeat #263f52 !important;}

/* Person Posting Back */
.postSubn 			{width:790px !important;color:#333 !important;font-size:9px !important;background:url("http://www.basilmarket.com/img/post-back.gif") top repeat-x !important;height:100px !important;padding:0 !important;}

/*Transparent menus */
.sub_menu{opacity: 0.9 !important;}

}
