css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("darkwaveradio.net") {
  #supersized, .mbYTP_wrapper,
  .entry p iframe,
  iframe.playerBox {
      display: none !important;
  }
  #supersized {
    opacity: 0 !important;
  }
  .post .entry:after {
     content: '♡♥❤Fánkocska❤♥♡';
     display: block;
     position: absolute;
     color: #050505;
     font-size: 100px;
     width: 100%;
     text-align: center;
     left: 0; 
     bottom: 0;
     text-shadow: 1px 1px 0px #222, 0px 0px 140px rgba(255,255,255,0.15);
    font-weight: 700;
    
  }
}
