css:
@-moz-document domain("neopets.com") {

#main #header {border: none !important; background-image: url(http://i2.photobucket.com/albums/y8/Shadowslilkitten/brightvaleheader.png) !important;}
#main #ban {border: none !important; background-image: url(http://i2.photobucket.com/albums/y8/Shadowslilkitten/brightvalebanner.png) !important;}
#main #footer {background-image: url(http://i2.photobucket.com/albums/y8/Shadowslilkitten/brightvalefooter.png) !important;}
#main #footer td {text-align: center !important;}
.brand-mamabar {display: none !important;}
.footerNifty {display: none !important;}
.copyright .sf {position: relative !important; left: 100px !important;}
.copyright .sf img {display: none !important;}
a:link, a:visited, a:active {color: #000000;}
a:hover {color: #4CBB17;}
.eventIcon, #header td.user.medText {color: #000000 !important;}
.eventIcon a:link, #header td.user.medText a:link {color: #49E20E !important;}
.eventIcon a:visited, #header td.user.medText a:visited {color: #49E20E !important;}
.eventIcon a:active, #header td.user.medText a:active {color: #49E20E !important;}
.eventIcon a:hover, #header td.user.medText a:hover {color: #FFE303 !important;}
#nst {color: #000000 !important;}
.eventIcon {color: #95BAE7;}
#template_nav img {opacity: 0 !important; filter: alpha(opacity=0) !important;}
ul.dropdown {background: #000000 !important; border: none !important; opacity: 0.9 !important; filter: alpha(opacity=90) !important;}
li:hover ul.dropdown a, li.over ul.dropdown a {color: #458B00 !important;}
li:hover ul.dropdown a:Hover, li.over ul.dropdown a:hover {color: #FFE303 !important;}
.marqueeSlot, .marqueeSlot a:link, .marqueeSlot a:visited, .marqueeSlot a:active, .marqueeSlot a:hover {background-color: #6899F4 !important;}
.marqueeSlotOn, .marqueeSlotOn a:link, .marqueeSlotOn a:visited, .marqueeSlotOn a:active, .marqueeSlotOn a:hover {background-color: #000000 !important;}
.sidebarHeader a:link, .contentModuleHeader a:link, .contentModuleHeaderAlt a:link {color: #FFFFFF !important;}
.sidebarHeader a:visited, .contentModuleHeader a:visited, .contentModuleHeaderAlt a:visited {color: #FFFFFF !important;}
.sidebarHeader a:active, .contentModuleHeader a:active, .contentModuleHeaderAlt a:active {color: #FFFFFF !important;}
.sidebarHeader a:hover, .contentModuleHeader a:hover, .contentModuleHeaderAlt a:hover {color: #FFFFFF !important;}
.moreLink, .moreLinkAlt {border: #FFFFFF 1px solid !important;}
.sidebarHeader {background: #000000 !important; color: #458B00 !important; border: none !important;}
.contentModuleHeader, .contentModuleHeaderAlt {background: #000000 !important; color: #458B00 !important; border: none !important;}
.sidebarModule, .contentModule {border: none !important;}
.sidebarTable {border: #4CBB17 2px solid !important;}
.contentModuleTable {border: #FFFFFF 2px solid !important;}
}
