css:
@-moz-document domain("web.whatsapp.com") {
body {
/* i really want this to be global */
  background-image: url(http://static.hdw.eweb4.com/media/wallpapers_1920x1080/cars/1/1/mitsubishi-lancer-evolution-x-car-hd-wallpaper-1920x1080-2263.jpg);  
  background-size: cover;
  background-position:  center;  
}
.app-wrapper::before {
  display: none;
}
.drawer-container-right, .pane-chat, .pane-intro {
  background-image: url(http://gosgsg.nl);
  background-color: rgba(0,0,0,0.0) ;
}
.app {
  background-color: rgba(0, 0, 0, 0.4);
  width: 100%;
  height: 100%;
  top: 0;
  border-radius:0px;
}
.pane-chat, .pane-intro  {
  width: 80%;
}
.drawer-header {
  background-color: #4E718C;
}
.pane-intro:after {
  border-top: 6px solid #4E718C;
}
.app, .app.three {
  width: 100%;
}
.drawer-container-panel, .pane-info {
  width: 385px;
  left: 80%;
}
.three .pane-chat{
  width: 59.8%;
}
.intro-title {
  color: #fff;
  font-size: 36px;
  margin-top: 38px;
  content: "";
}
.intro-text {
  font-size: 14px;
  color: #fff;
}
.intro-image {
  width: 356px;
  height: 355px;
  background-image: url(http://files.softicons.com/download/application-icons/hex-icons-pack-by-martz90/png/512x512/whatsapp.png);
}
#wrapper:before {
display:none;
}
}
