css:
/* REMOVE THE COMMENTS FROM THE LINE BELOW */
  /*@import url('https://fonts.googleapis.com/css?family=Open+Sans:600,700');    */
@-moz-document domain("thepiratebay.org"), domain("thepiratebay.se") {
  
  :root {
    --headbg: #14151F !important;
    --contentbg: #1d1e2a !important;
    --commentbg: #1a1b26 !important;
    --itemhoverbg: #14151F !important;
    --searchbg: #14151F !important;   
    
    
    /* header bg */
    --imgbg: url("http://i.imgur.com/aQg2cs6.jpg") !important;
    
    /* name bg */
    --imgbg2: url("http://i.imgur.com/BclVyMp.jpg") !important;    
    
    /* dl bg */
    --imgbg3: url("http://i.imgur.com/q0riPr9.jpg") !important;    
    
    
    --text1: #ccc !important;
    --text2: #999 !important;
    --text3: #B294BB !important;           /* purple */
    --text4: #ddd !important;           /* red */
    
    --font1: "Open Sans", "Segoe UI" !important;
  }  
  
                                             /* c o l o r s */
  
table#searchResult tr {
        background: var(--contentbg) !important;
  }
  
th, h2 {
    background: var(--contentbg) !important;
    transition: ease-in-out .2s !important;
  }    
  
td:hover, th:hover {
    background: var(--contentbg) !important;
    transition: ease-in-out .2s !important;
  }  
  
  #details h4 {
   color: var(--text2) !important; 
  }  
  
  a, .download a, #detailsframe #title {
   color: var(--text4) !important;
    text-shadow: 3px 3px 5px rgba(0,0,0,0.1) !important;
  }  
  
  a:hover, #comments p.byline, #details dt, table#searchResult td.vertTh {
    color: var(--text3) !important;
  }  

                                          /* b o d y */
  
  body {
    background: var(--contentbg) !important;
    padding: 0px !important;
    color: var(--text2) !important;
   font-family: var(--font1) !important;
  }

                                          /* h e a d */
  
         /* center header */  
  
  #q {
    text-align: center !important;
    padding: 70px !important;    
  }  
  
     /* remove logo */
  
  #TPBlogo {
    display: none !important;
  }  
  
  #q .inputbox, .submitbutton, input {
    text-align: center !important;
    padding: 10px !important;
    background: var(--searchbg) !important;
    border: none !important;
    color: var(--text2) !important;
    width: 20% !important;
    margin-top: 10px !important;
  }
  
                                 /* t o r r e n t s */
  
 
                                                  /* browse */
  
  #main-content, #content {
    margin: 0 !important;
    width: 100% !important;
    max-width: 3000px !important;
    /*min-width: 1400px !important;*/
    margin-top: 10px !important;
  }
  
                                    /* browse head */
  
h2 {
    border: none !important;
    padding: 0px !important;
    margin: 0 !important;
    text-align: center !important;
    font-family: var(--font1) !important;
    font-size: 20px !important;
    width: 100% !important;
    display: none !important;
  }   
  
h2 span {
    margin: 0 auto !important;
    text-align: center !important;
    float: none !important;
  }  
    
table#searchResult th, table#searchResult td, table#searchResult a, th, td {
    border: none !important;
    text-decoration: none !important;
  }  
  
th, td {
    padding: 10px !important;
    text-align: center !important;
  }  
  
.viewswitch {
    font-size: 14px !important;
  }  
  
     /* remove browse header items - .viewswitch */
  
.sortby, .viewswitch {
    display: none !important;    
  }  
  
  

                                    /* change seeders text */
  
.header > th:nth-child(3) > abbr:nth-child(1), #tableHead > tr > th:nth-child(3) > abbr > a {
    font-size: 0px !important;
    text-decoration: none !important;
  }
  
  .header > th:nth-child(3) > abbr:nth-child(1)::after {
    content: "Seeders" !important;
    font-family: var(--font1) !important;
    font-size: 12px !important;
    text-decoration: none !important;
  }  
  
  
                                    /* change leeches text */
  
  .header > th:nth-child(4) > abbr:nth-child(1), #tableHead > tr > th:nth-child(4) > abbr > a {
     font-size: 0px !important;
     text-decoration: none !important;
  }
  
  .header > th:nth-child(4) > abbr:nth-child(1)::after {
    content: "Leeches" !important;
    font-family: var(--font1) !important;
    font-size: 12px !important;
  }    
  
  
                         /* t o r r e n t  i m g s */
  
 
  table#searchResult img, #details dd img {
    height: 13px !important;
    width: 13px !important;
    filter: grayscale(100%) contrast(0%) !important;
  }
  
                                     /* l i n k s */  
  
table#searchResult a {
  transition: ease-in-out .3s !important;
  }    
  
a, a:hover, table#searchResult a:hover {
  text-decoration: none !important;
  transition: ease-in-out .3s !important;
  border: none !important;
  }  
  
  table#searchResult a {
    font-size: 14px !important;
  }  
  
  h2 span, .detDesc, #q {
    cursor: default !important;
  }
  
  
                          /* m a i n  p a g e */
  
  /* remove search buttons */
  #subm > input, #content > div:nth-child(3) > a:nth-child(2) {
    display: none !important;
  }  
  
  #fp {
    margin: 0 auto !important;
    padding: 250px !important;
  }
  
  #fp h1 a, #fp #howdl a, #icons, #fp #inp span, footer {
    display: none !important;
  }
  
  #fp #inp input {
    text-align: center !important;
    padding: 20px !important;
  }  
  
  #subm > input {
    padding: 10px !important;
  }  
  
#chb {
    opacity: 0 !important;
  }  
  
#fp #navlinks {
    font-size: 17px !important;
    background-color: var(--contentbg) !important;
    background: var(--imgbg) !important;
   color: var(--text1) !important;    
    position: fixed !important;  
    top: 0 !important;
    width: 100% !important;
   left: 0 !important;
  padding: 15px !important;
  }  
  
  #q {
    font-size: 12px !important;
    background-color: var(--contentbg) !important;
    background: var(--imgbg) !important;
    background-size: cover !important;
    color: var(--text1) !important;
    max-height: 0px !important;
    margin-top: -50px !important;    
  }  
  
  
  
                                 /* add shadow */
  
#q, #fp #navlinks, #q .inputbox, .submitbutton, input {
    box-shadow: 2px 2px 6px 3px rgba(0,0,0,0.1) !important;
  }  
  
  
  
  
                           /* r a n d o m  s h i t */
  
  /* remove shadows and borders */
#detailsframe #title, #detailsframe, .nfo, #details dd, #details dt {
    border: none !important;
    box-shadow: none !important;
  }
/* change cursor on hover cause idk it looks nice */
#details dt, #detailsframe #title, #details dd, #details pre, #details p, #comments .comment {
    cursor: default !important;
  }    
/* these are obviously comments you don't need a header boo */  
  #commentsheader {
    display: none !important;
  }  
  
  
  
                                      /* t o r r e n t  p a g e */
  
  #detailsouterframe, .nfo {
    width: 70% !important;
    margin-top: 40px !important;
  }
  
  .nfo, #details .col1, #details .col2 {
    width: 99% !important;
  }  
  
  #detailsframe #title {
    background-color: var(--contentbg) !important;
    background: var(--imgbg2) !important;
  }  
  
  #comments .comment {
    background: var(--commentbg) !important;    
  }  
  
  #detailsframe {
  box-shadow: 2px 2px 8px 3px rgba(0,0,0,0.1) !important;      
  }  
  
#details, .nfo {
    background: var(--headbg) !important;     
  } 
  
  #details dd, #comments .comment {
    padding: 10px !important;    
  }  
  
  #detailsframe #title, .nfo, #comments .comment, #details dd, #comments .comment, #details dt, #details pre, #detailsframe, #details dl {
    text-align: center !important;
  }  
  
  #comments .comment, #details, #details pre {
    font-size: 13px !important;
  }  
  
  #details dd {
    font-size: 15px !important;    
  }  
  
  #detailsframe #title {
    font-size: 19px !important;    
    padding: 30px !important;
  }  
  
                                          /* d l  b u t t o n */
  
  .download a {
    font-size: 25px !important;    
    text-align: center !important;
    text-transform: none !important;    
    margin: 0 auto !important;    
    background: transparent !important;
    margin-left: 10px !important;
  }  
  
  div.download {
    width: 100% !important;
    background-color: var(--contentbg) !important;
    background: var(--imgbg3) !important;
    padding: 30px !important;   
    margin-left: -30px !important;
  }  
  
  #details pre {
    font-family: var(--font1) !important;
    line-height: 15px !important;
  }    
  
  
         /* remove second dl button */  
  
  div.download:nth-child(5) {
    display: none !important;
  }  
  
                                   /* p a g e s */
  
  #searchResult > tbody:nth-child(2) > tr:nth-child(31) > td:nth-child(1) {
    padding: 30px !important;
    background: var(--contentbg) !important;
    font-size: 15px !important;
  }  
  
.detDesc {
   opacity: 0 !important;
   transition: ease-in-out .3s !important; 
  }  
.detDesc:hover {
    opacity: 0.9 !important;
    transition: ease-in-out .3s !important;
  }  
  
  .submitbutton, #q > label > *, #q > label, #category {
    display: none !important;
  }  
  
                                      /* f o o t */
  
  /* remove footer */
  
  #foot, #searchResult > tbody:nth-child(2) > tr:nth-child(31) > td:nth-child(1) > a:nth-child(31) > img:nth-child(1) {
    display: none !important;
  }  
  
}
