css:
@-moz-document domain("openclassrooms.com") {
    
    * {
        color:#fdd;
        background-color: black;
    }
    
    a {
        color: #F39539;
    }
    
    a:hover {
        color: #FF7700;
    }

    body, td, pre {
        color:#9cf;
    }

    h1,h2,h3,h4,h5,h6,th{
        color:#f00;
        background-color: black;
    }
    
    input {
        color:#cdf;
        background-color: #025;
    }
    
    ul,ol,dl,li,dt,dd {
        color:#fd6;
        background-color: #222;
    }

    html,.oc-body,.oc-body main,p,strong,span,.course-content p, .course-content strong,.course-content span {
        color:#a9f;
        background-color: black;
    }
    
    .mainHeader {
        background-color: #000;
    }
    
    .main-section.alt-section, section.alt-section {
       background-color:#000;
    }
    
    ul.nav-tabs > li.active {
        background-color:#222;
    }
    
    body.oldsdz header#mainHeader section#headerBottom {
        background-color: #000 !important;
    }
    
    .course-header {
        background-color: #000;
    }
    
    .course-header__details-container {
        background-color: #000;
    }
    
    .topSearchBox__input {
        color: #000;
    }
    
    section#mainContent .comments .row {
        background-color: #111 !important;
    }
    
    section#mainContent section#mainSection {
        background-color: #000 !important;
    }
    
    code-block {
        color:red !important;
    }
    
    .course-content aside[data-claire-semantic="warning"] {
        background-color: #321;
    }
    
    .course-content div[data-claire-semantic="question"] {
        background-color: #222;
    }
    
    .course-content aside[data-claire-semantic="information"] {
        background-color: #123;
    }
    
    .list-msg__item--read h3 {
        color:#ff9;
    }
    
    #popin-time-remaining {
        background-color:#18181c;
    }
    
    .fancybox-skin .fancybox-close {
        background-color:transparent !important;
    }
    
    .course-content h2:not(.foldable__button), .course-content h3:not(.foldable__button), .course-content h4:not(.foldable__button), .course-content h5:not(.foldable__button), .course-content h6:not(.foldable__button) {
        color:#ddd;
        background-color: black;
    }
    
    .alt-section {
        background-color: #000;
    }
    
    .footer {
        background-color: #000;
    }
}
