    background-color:rgba(0, 0, 0, 0.8) !important;
}

div#mini_jmd.table.fl tr.odd { /*索引，二，四*/
    box-shadow: 0px 0px 10px rgba(255, 255, 255,0) !important;  
    background-color:rgba(0, 0, 0, 0) !important;
}

div#mini_jmd.table.fl th {/*索引，周几*/
    background-color:rgba(100, 100, 100, 0) !important;
    box-shadow: 0px 0px 10px rgba(255, 255, 255,0.2) !important;  
