}

table#topic_list.tablesorter{ /*更新列表内容*/
    background-color:rgba(0, 0, 0, 0) !important;
    box-shadow: none !important;
    margin: 0px !important;
    width: auto !important; 
}


table.tablesorter tr th {  /*更新内容列表分类等*/
