}

@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
/* -----------------------------------------

 THIS SECTION CHANGES NOTHING BUT THE FONTS

----------------------------------------- */

/*[[font]]*/
}

@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
/* ------------------------------------

 THIS SECTION CHANGES THE GENERAL BODY

------------------------------------ */

body#dashboard_iframe {background:transparent!important;}

/* --- removes excess backgrounds --- */

#left_column, .l-content, div#right_column, div#right_column:after {
    background:none!important;
	background:transparent!important;
}
  
/* --- replaces some of the backgrounds I just removed (I'm trash sorry) --- */

#settings_actions_tumblelog #left_column, #settings_actions_account #left_column, #settings_actions_dashboard #left_column, #settings_actions_apps #left_column, #settings_actions_notifications #left_column, #dashboard_upload_static_file #left_column, #main_custom_themes, #main_policy, #main_help .l-content, #main_doc_page .l-content, #dashboard_upload_static_file .l-content, #main_about .l-content, #main_apps .l-content, #main_tips .l-content, #pages_logo_index .l-content, #pages_buttons_buttons .l-content,  #main_press .l-content, #main_developers .l-content, #main_whitehat_security_form .l-content, #main_meetups .l-content, #dashboard_account_create_tumblelog .l-content, #themes_actions_themes .l-content, .corp_page .l-content, .l-container.l-container--full-width .l-content {
    background:#fff!important;
}

#dashboard_upload_static_file #content {
	background-color:#fff!important;
	padding:0px 20px 20px 20px;
	border-radius:0px 0px 20px 20px!important;
	margin:10px 20px 0px 20px!important;
}

#dashboard_tumblelog_settings #content {
	background-color:#fff!important;
	margin:10px 0px!important;
}
}

@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
/* ------------------------------

 THIS SECTION CHANGES THE HEADER

------------------------------ */

/* --- header tweaks --- */

.l-header-container--refresh .l-header {
	max-width:none!important;
    padding:0px 12px 0px 0px!important;
}
    
@media screen and (min-width:1600px)
{.l-header-container--refresh .l-header {
    padding:0px 12px 0px 0px!important;
} #discover_actions_discover .l-header-container--refresh .l-header,
  #search_actions_search .l-header-container--refresh .l-header {
    padding:0px 12px 0px 20px!important;}
}

#user_tools {
	margin-top:0px!important;
}

.compose-button {
    fill:#fff!important;
}

/* --- header notification tweaks --- */

.tab_notice {
  	top:-4px!important;
  	margin-left:-1px!important;
  	height:23px!important;
    min-width:23px!important;
    max-width:41px!important;
    padding:5px 5px 1px 5px!important;
    text-align:center!important;
    letter-spacing:1px!important;
    font-size:11px!important;
    line-height:10px!important;
}

/* --- that new "t" logo in the header --- */

.large-shadow {
	opacity:0.1!important;
}

.logo .svg-logo {
	border-right:1px rgba(255,255,255,0.2) solid;
	padding:9px 13px 6px 15px!important;
}

/* --- removes the rainbow animation from the "t" logo --- */

.logo:hover .inner {
	opacity:0!important;
    -moz-animation: none!important;
    -o-animation: none!important;
    -webkit-animation: none!important;
    animation: none!important;
}


/* --- search bar --- */

.ui_search .search_form_field:before {
	display:none;
}

#search_field .popover_inner {
	background-color:rgba(255,255,255,0.9)!important;
	padding:0px!important;
}
  
.explore_topics {
	display:none!important;
}

.search_subheading {
	display:none!important;
}

.popover_gradient>.popover_inner {
	box-shadow:inset 0px 0px 0px #fff!important;
}

a.result_link {
	padding:0px 10px!important;
	border-bottom:1px #fff solid;
}

.result_thumb.thumb {
	height:33px!important;
	width:33px!important;
	top:5px!important;
	right:5px!important;}

.search_popover .result {
	height:37px!important;
}

.result_title {
	margin-top:1px!important;
}

.result_sub_title {
	margin-top:-6px!important;
	margin-bottom:3px!important;
}

.popover_gradient>.popover_inner, .popover.search_popover>.popover_inner {
	padding:0px!important;
}

.search_popover .result_link, .search_popover .more_results_link {
	border-bottom:1px #eee solid!important;
	padding:10px 12px!important;
}

.search_popover .search_results_section.tag {
	padding-top:0px!important;
}

/* --- the information dropdown in the top-right --- */

.popover_header {
	margin-top:-4px!important;
}

.nav-redesign .popover--account-popover .popover .popover_header {
	border-bottom:1px #eee solid;
}

.popover--account-popover .popover_sublist--scrollable {
	min-height:180px!important;
	max-height:280px!important;
}

.popover_header .popover_item_suffix {
	margin-right:-10px!important;
	border-left:1px #fff solid;
	padding:0px 0px 0px 10px;
}

.popover--account-popover .popover .popover_subsection .popover_menu_list {
	margin:0px 0px 3px 0px!important;}

.blog-sub-nav-details .blog-sub-nav-item {
	padding:6px 0px 2px 6px!important;
	margin-left:-4px!important;
}

.blog-list-item .blog-list-item-info .blog-list-item-info-title, .blog-list-item .blog-list-item-info .blog-list-item-info-name {
	max-width:none!important;
}

.search_popover, .popover--activity-popover, .nav-redesign .popover--account-popover {
	margin-top:15px!important;
}

.popover--account-popover .popover .popover_menu_item .popover_menu_item_anchor {
	padding:0px 10px!important;
    height:34px!important;
	line-height:34px!important;
	border-bottom:1px #eee solid;
}

.nav-redesign .popover--account-popover .popover .popover_menu_item_anchor .popover_menu_item_anchor_icon {
	display:none!important;
}

.popover--account-popover .popover .popover_menu_item_blog_details {
	padding-left:10px!important;
  	margin-left:-2px!important;
	margin-right:-20px!important;
}

.blog-sub-nav-details .blog-sub-nav-item .blog-sub-nav-item-link {
	height:18px!important;
	line-height:18px!important;
  	margin:0px 5px 0px 0px!important;
	padding-bottom:5px!important;
}

.popover_menu_item_blog.popover_menu_item_blog {
  	margin-left:0px!important;
}

.nav-redesign .popover--account-popover .popover {
	width:240px!important;
	overflow:hidden!important;
}

.popover--account-popover .tx-scroll {
	width:247px!important;
}

.popover_gradient {
	padding:0px!important;
}

.blog-list-item-button.blog-list-item-button-details.icon_manburger {
	margin-right:-15px!important;
}

/* --- compose button tweaks --- */

.tab-bar-container {
	border:rgba(255,255,255,0.2) solid!important;
    border-width:0px 1px!important;
    right:-6px!important;
    padding-left:10px!important;
    padding-right:15px!important;
}

/* --- gets rid of the rainbow loading bar in the header --- */

.loader-container {
  display: none!important;
}

/* --- this is from their july 13th 2015 update to the search bar --- */

.ui_search {
    margin-right:-10px!important;
}

.search_popover {
	width:300px!important;
}

.ui_search .search_form_field {
	max-width:none!important;
    margin-left:10px!important;
    margin-right:3px!important;
}

.ui_search .search_query {
	padding-left:25px!important;
    background:transparent!important;
    color:#fff!important;
}

.ui_search.active .search_query::-webkit-input-placeholder,.ui_search.selected .search_query::-webkit-input-placeholder, .ui_search.active .search_query::-moz-placeholder,.ui_search.selected .search_query::-moz-placeholder,.ui_search.active .search_query:-moz-placeholder,.ui_search.selected .search_query:-moz-placeholder, .ui_search.active .search_query:-ms-input-placeholder,.ui_search.selected .search_query:-ms-input-placeholder {
	color:rgba(255,255,255,1)!important;
}
}

@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
/* ------------------------------------------

 THIS SECTION IS FOR THE LEFT COLUMN (POSTS)

------------------------------------------ */

/* --- new post buttons above posts --- */

.new_post_label i {
    padding-top:2px!important;
    margin-left:0px!important;
}

/* --- chat posts --- */

.chat_line {
    padding:13px 20px!important;
	border:#eee solid;
	border-width:1px 0px;
  	margin-bottom:-1px;
}

.chat_line:last-child {
	margin-bottom:0px!important;  
}

.conversation_lines {
    margin:-2px -20px 0px -23px!important;
}

/* --- post info --- */

.reblog_icon {
	opacity:0.8!important;
  	margin-left:1px!important;
  	margin-right:5px!important;
}

#indashblog_actions_peepr .reblog_icon {
	margin-left:0px!important;
}

/* --- post tags --- */
 
.post_full .post_tags {
	white-space:normal!important;
}
    
.post_full .post_tags .post_tag {
	display:inline!important;
}

/* --- the inline notifications above and below posts --- */

.notification {
    border:solid rgba(255,255,255,0.1)!important;
    border-width:0px!important;
    margin:-10px 0px 10px 0px!important;
}
 
.notification blockquote {
	border-left:2px solid rgba(255,255,255,0.7)!important;
	opacity:0.6;
}

ol#posts li.notification {
	line-height:21px!important;
}

ol#posts li.notification * {
	color:#fff!important;
}

ol#posts li.notification * a {
	color:#fff!important;
	opacity:1!important;
}

ol#posts li.notification em {
	font-style:italic;
	margin-left:3px;
}

ol#posts li.notification blockquote {
	text-transform:none!important;
}
 
/* --- removes the default blue icon background --- */

.post_avatar, .blog-avatar,
.post_avatar .post_avatar_link,
.post_avatar .blog-avatar-link,
.blog-avatar .post_avatar_link {
	background-color:transparent!important;
}

/* --- removes rounded corners on posts --- */

.post {
	-webkit-border-radius: 0px!important;
	border-radius: 0px!important;
}
  
/* --- removes the follow buttons --- */
  
.post_header_button.follow, .reblog_follow_button, .post_full .post_header_button {
	display:none!important;
}

.post_full .post_action_wrapper {
	margin:0px!important;
}

/* --- this fixes the permalink in the top-right --- */

.post_full .post_permalink {
    border-color:transparent transparent #eee #eee;
}

pre {
	word-break:break-word!important;
	overflow:hidden!important;
}

/* --- this is the editor for creating and editing posts --- */

.post-form {
	margin:0px!important;
	padding-bottom:0px!important;
}

.title-field {
	border:#eee solid;
	border-width:1px 0px;
	margin:0px -20px 20px -20px;
	padding:12px 20px 12px 20px;
	min-height:10px!important;
}

.post .post-content .title-field *, .editor-richtext[contenteditable], .post:not(.new_post_buttons) * {
	font-family:inherit!important;
}

.post-form--footer.post-footer {
	border-top:1px #eee solid;
	margin-top:20px;
}

.controls-container .flat-button {
	padding-right:10px!important;
	padding-left:10px!important;
}

.tab_frame .ui_button {
	padding-top:5px!important;
}

.controls_section a {
	background-image:none!important;
}

.post_body .note_item {
	padding-bottom:0px!important;
}

.post_full .post_info_fence {
	max-width:300px!important;
}

ol#posts li.post.new_post {
	background:#ffffff top center no-repeat!important;
	box-shadow:none!important;
	border:0px solid #FFF!important;
	border-radius:0px!important;
	padding:0px!important;
	margin:0px!important;
}

/* --- this is for the ask posts --- */

.asker, .answerer {
	color:#777!important;
	margin:-11px -14px 10px -14px!important;
	padding:10px 14px!important; 
	border-bottom:1px #ddd solid;
}

.post_full .name {
	color:#777!important;
    margin-right:1px;
}

.post_body .text {
	margin-bottom:15px!important;
}

.post_full.is_note .answer.post_info {
	margin-top:20px;
}

/* --- the mobile blog that appears when a URL is hovered over --- */

.indash_header_wrapper .info h1.title {
	font-size:42px!important;
	line-height:46px!important;
	padding:5px 0px;
}

.poptica_header .indash_header_wrapper .info h1.title {
	font-size:20px!important; 
	line-height:24px!important;
}

.chrome.big.customize_button, .chrome.unfollow, .chrome.follow {
	height:28px!important;
	padding:0px 10px!important;
}

.chrome.big.customize_button {
	width:115px!important;
	padding:0px!important;
}

.poptica_header .navigation .full_url {
	max-width:140px!important;
}

/* --- this gets rid of that new 'embrace your uncomfort zone' ad --- */

.stretchy_kids {
	display:none!important;
}

/* --- this is to edit the division line --- */

.post .post_content hr, .post .post_body hr {
	margin:15px -20px!important;
    min-width:calc(100% + 40px)!important;
}
}

@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
/* ----------------------------------------------

 THIS SECTION IS FOR THE RIGHT COLUMN (SIDEBAR?)

---------------------------------------------- */
    
/* --- rest in pieces u stupid blue shadows --- */

.identity-refresh .controls_section.user_list li .follow_list_item_blog:before, .identity-refresh .controls_section.user_list li:hover .follow_list_item_blog:before, .radar .radar_footer .radar_avatar:before {
	border-color:transparent;
    background:transparent;
}

/* --- right column tweaks --- */

#right_column {margin-top:4px!important;}

.controls_section li:not(.section_header):not(.selected_blog):hover {
	background-color:rgba(255,255,255,0.1)!important;
	cursor:pointer!important;
	color:#fff!important;
}

.controls_section li.selected:not(.section_header) {
	background-color:rgba(255,255,255,0.1)!important;
	cursor:pointer!important;
	color:#fff!important;
}
      
.identity .controls_section .currently_selected_blog, .identity .controls_section li.section_header, .identity .controls_section.user_list li .follow_list_item_blog .tumblelog_title {
  	color:rgba(255,255,255,0.95)!important;
}

.controls_section li:not(.section_header):not(.selected_blog) .count,
.controls_section li:not(.section_header):not(.selected_blog) a,
.identity .controls_section.user_list li .follow_list_item_blog .tumblelog_name,
.radar .radar_tumblelog_label .subheading,
#right_column .hide_overflow {
	color:rgba(255,255,255,0.7)!important;
}

.controls_section_item.no_hover.no_push {
	text-align:right!important;
	margin-top:0px!important;
	padding:0px!important;
}

.controls_section li.controls_section_item.no_hover.no_push:hover {
	background-color:transparent!important;
}
      
#right_column .currently_selected_blog.hide_overflow.blog_title {
  	color:rgba(255,255,255,0.95)!important;
}

.identity .right_column .small_links a {
	padding:10px 10px 0px 10px!important;
	opacity:1!important;
	color:rgba(255,255,255,0.7)!important;
}
    
.identity .right_column .follow, .identity .right_column .follow_icon {
	color:#fff!important;
}
    
.identity .right_column .follow:hover {
	background:rgba(255,255,255,0.3)!important;
}

.radar .radar_tumblelog_label .name {
	color:rgba(255,255,255,0.9)!important;
}

.sidebar_link.avatar_align {
	display:none!important;
}

a#open_blog_link {
	color:rgba(255,255,255,0.7)!important;
}

/* --- that footer below the sidebar --- */

.sidebar_nav {
	width:260px!important;
  	margin:0px -10px 5px 0px!important;
}

.sidebar_nav .sidebar_nav_item {
	margin:0px 0px 0px 10px!important;
}

#sidebar_footer_nav a.sidebar_link {
	color:rgba(255,255,255,0.8)!important;
}
}

@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
/* --------------------------------------------------

 THIS SECTION IS FOR PAGES THAT AREN'T THE DASHBOARD

-------------------------------------------------- */

/* --- tweaks to fanmail messages in the inbox --- */

.message_body, .message_body_truncated, .message {
	font-size:14px!important;
	line-height:20px!important;
    padding-top:5px!important;
}

.message.mono {
	font-family:'consolas'!important;
}
  
.post.fan_mail.lined-white .message {
	background:top left repeat-y url('http://static.tumblr.com/imnherg/B8Unmt7hq/title23464768.jpg') #fdfdfd!important;}
  
.message.mono div {
	margin-top:15px!important;
	margin-bottom:-30px!important;
}
  
#dashboard_messages_inbox .post_controls a {
	font-size:0!important;
}
  
#dashboard_messages_inbox .post_footer {
	margin-top:10px!important;
}

/* --- for the spotlight, people you know, and following pages --- */

#following, #lookup, #staff_picks {
	background-color:#fff!important;
	margin-top:-30px!important;
	margin-left:0px!important;
	margin-right:0px!important;
  	padding:30px!important;
	border:3px #eee solid;
	border-top:none;
	box-shadow: inset 0 0 30px #ddd;
	border-radius: 0px 0px 15px 15px !important;}

#following {
	padding:0px 35px 0px 0px!important;
}

#following #left_column {
	width:595px!important;
	margin-bottom:-3px!important;
	border-right:1px #ddd solid!important;
}

#following .button {
	margin-bottom:30px!important;
	margin-left:20px!important;
	margin-right:20px!important;
}

#following #right_column {
	margin-top:40px!important;
}

#staff_picks {
	padding:29px 0px!important;
}

/* --- I'm pretty sure this is for the settings pages --- */

.tumblr_settings .settings_heading {
	border-bottom:1px #eee solid!important;
	padding:2px 25px 25px 25px!important;
    font-size:24px!important;
	margin-left:-25px;
	margin-right:-25px;
}

/* --- this is for the explore page --- */

.discover-controls-wrapper .types-tabs>.content-control {
	line-height:16px!important;
}

.post_brick .post_tags .post_tag:nth-of-type(1) {
	margin-left:-5px!important;
}

.post_brick.content_under_header .tumblelog_info, .post_brick.content_under_header .tumblelog_info:hover {
	color:#aaa!important;
}

.blogs-drawer {
	padding:0px 40px 20px 20px!important;
}

.post_info_tumblelog {
	color:#aaa!important;
}

.blogs-drawer .tumblelogs-wrapper .drawer-header {
	font-size:24px!important;
    line-height:74px!important;
    border-bottom:none!important;
    margin-bottom:0px!important;
}

/* --- activity page --- */

.notifications_page .section.divider {
	background:transparent!important;
    padding-bottom:0px!important;
    border-bottom:none!important;
}

.notifications_page .section {
	padding-left:0px!important;
    padding-right:0px!important;
}

#range_controls {
	padding:5px 20px 0px 20px;
}

.tops_box.tops_fans {
	padding:0px 20px;
}

#user_graphs {
	padding:5px 20px 20px 20px!important;
}

.post_micro.is_regular .post_content .post_content_inner .post_title {
	font-size:12px!important;
    line-height:14px!important;
    margin-bottom:-10px!important;
}

.notifications_page .section.last {
	margin-top:0px!important;
}

/* --- following blogs page --- */

.follower.invite_someone {
	padding-top:10px!important;
}
}

@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
/* ----------------------------------------------------

 I DON'T KNOW WHAT MOST OF THIS STUFF DOES (SORRY OMG)

---------------------------------------------------- */

/* --- this is for various xkit extensions --- */

#xpeoplenotifier.controls_section {
	padding:0px!important;
}

.xkit-people-notifier-person .people-notifier-avatar {
	left:5px!important;
}

li.no_push.xkit-people-notifier-person {
	height:40px!important;
}

#xpeoplenotifier .hide_overflow {
	margin-left:28px!important;
	line-height:40px!important;
}

#xpeoplenotifier a.members div {
	line-height:40px;
	padding:0px 10px!important;
	text-align:right;
}

#xpeoplenotifier a.members::before {
	display:none!important;
}

#xpeoplenotifier .count {
	line-height:40px!important;
}

