css:
@-moz-document domain("kongregate.com") {
#play.dark_game_page_background-dark_background .gamepage_categories_list a {
color: #FFCC99;
display: inline-block;
font: 11px Arial, sans-serif;
padding: 13px 0 15px;
text-shadow: 0px 1px 10px #2B2B2B;
}
.post .author .edit a {
color: #666666;
text-transform: none;
text-decoration: none;
}
#play.dark_game_page_background-dark_background #global .sitemessage {
width: 922px;
text-align: center;
text-shadow: 0px -1px 1px #FFF;
}
#headerwrap {
min-width: 940px;
height: 100px;
}
#header h2 a {
clear: both;
width: 311px;
height: 32px;
background-position: 0 0;
}
.main_nav_item:hover .main_nav_top_item, .main_nav_item:hover .main_nav_top_item.active {
background: #423f3e;
color: #FC9;
}
form#search dt input {
float: right;
border: 0;
margin: 0;
padding: 2px;
width: 199px;
height: 13px;
background: #f0f0ef;
letter-spacing: 0px;
border-radius: 20px;
text-align: center;
box-shadow: 0px 1px 7px rgba(76, 4, 4, 0.66);
}
form#search dd input {
float: none;
width: 0px;
height: 0px;
background-position: 0 -1536px;
margin: 0;
padding: 0;
text-indent: -9999px;
border: 0;
}
form#search {
background-color: rgba(113, 0, 0, 0);
padding: 6px;
width: 206px;
}
.main_nav_item:hover .main_nav_top_item, .main_nav_item:hover .main_nav_top_item.active {
background: #423f3e;
color: #FFF;
}
.welcome-user>li>a {
color: #FFF;
display: block;
font: 600 14px/27px 'Source Sans Pro', 'Helvetica', Arial, sans-serif;
padding: 0px 20px;
text-decoration: none;
text-transform: inherit !important;
font-size: larger !important;
text-shadow: 0px 1px 2px rgba(0, 0, 0, 0.8);
}
.main_nav_top_item.active {
background: -webkit-linear-gradient(top, rgba(255, 255, 255, 0.2) , rgba(0, 0, 0, 0.2));
color: #FFF;
box-shadow: 0px 1px 5px rgba(60,60,60,.55);
border-radius: 6px 6px 0px 0px;
transition: ease .33s;
}
.main_navigation a {
color: #FFFFFF;
text-shadow: 0px 1px 2px rgba(0, 0, 0, 0.5);
display: block;
text-decoration: none;
text-transform: uppercase;
background: transparent;
}
.welcome-user .profile #mini-profile-level {
display: inline-block;
height: 1.2em;
position: relative;
top: -1px;
vertical-align: middle;
}
.welcome-user .profile img {
float: left;
height: 27px;
margin-right: 12px;
width: auto;
}
.welcome-user>li {
background-color: rgba(203, 95, 95, 0.2);
color: #FFF;
float: left;
height: 28px;
list-style: none;
margin-left: 0px;
text-align: center;
box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.04);
}
#header h3#playing {
font: 10px/15px Verdana,Arial,sans-serif;
color: #FFF;
text-transform: uppercase;
letter-spacing: 0.12em;
text-shadow: 0px 1px 2px rgba(0,0,0,0.45);
}
body#forums {
font-size: 105%;
text-shadow: 0px 1px 2px rgba(50, 0, 0, 0.10);
text-transform: none;
}
hr {
height: 0px;
border: 0;
border-bottom: 2px solid #C8C8C8;
margin: 1em 0;
box-shadow: 0px 1px 1px rgba(128, 64, 64, 0.1);
border-radius: 30px;
}
.post .author a.post_creator {
color: #990000;
text-shadow: 0px 1px 0px rgba(255, 255, 255, 0.6);
}
.mtm, .mvm, .mam {
margin-top: 15px !important;
text-transform: none;
}
.searchbox .searchbox_field {
border: 1px solid #aaa;
float: left;
height: 20px;
line-height: 20px;
padding: 0 6px;
border-radius: 20px 0px 0px 20px;
text-align: center;
margin-right: -6px;
}
.btn_action, #admin-controls .button_to input[value="Delete Account"], #admin-controls .new_silencing input[type="submit"], #admin-controls .new_ban input[type="submit"], body.adjusted .btn_action, body.adjusted #admin-controls .button_to input[value="Delete Account"], #admin-controls .button_to body.adjusted input[value="Delete Account"], body.adjusted #admin-controls .new_silencing input[type="submit"], #admin-controls .new_silencing body.adjusted input[type="submit"], body.adjusted #admin-controls .new_ban input[type="submit"], #admin-controls .new_ban body.adjusted input[type="submit"] {
background-color: #7F1717;
background: -webkit-gradient(linear, left top, left bottom, from(#912929), to(#700808));
background: linear-gradient(to bottom, #912929,#700808);
border-color: #500606 !important;
-webkit-box-shadow: none;
box-shadow: none;
color: #fff !important;
text-shadow: 0 0 2px rgba(0,0,0,0.5);
}
body#forums div#feature table tr th {
background: #333;
color: #FFF;
font-size: 0.8em;
padding: 1px 10px;
border: 5px solid #333;
box-shadow: 0px 5px 10px rgba(0,0,0,.2);
}
body#forums div#feature table tr td {
padding: 10px;
border: 0px solid #FFFFFF;
}
img.grey {
background-color: #868686;
opacity: 0.33;
}
img.green {
background-color: #D02525;
}
.post .author {
background: -webkit-linear-gradient(left, rgba(128, 128, 128, 0.5) , white);
border-top: 2px solid #efefef;
color: #333;
font-size: 0.9em;
font-weight: bold;
text-transform: capitalize;
min-height: 32px;
padding: 5px 10px;
vertical-align: top;
width: 230px;
border-radius: 25px 0px 0px 25px;
}
.posts .post .body blockquote {
border: 1px solid #AAC2DD;
border-left: 3px solid #7793AA;
border-right: 0;
background: #CCE4FF;
padding: 1px 10px;
margin-left: 0;
}
.post .body p img {
display: block;
height: auto;
max-height: 90%;
max-width: 90%;
width: auto;
box-shadow: 0px 1px 3px rgba(128,128,128,0.75);
}
.my_games_block .game_icon img {
display: block;
height: auto;
max-height: 112px;
width: 140px;
border-radius: 2px;
border: 2px solid rgba(0, 0, 0, 0.3);
box-shadow: 0px 2px 10px rgba(0, 0, 0, 0.5);
}
table tr td.inv {
background: rgba(16, 48, 116, 0.10);
}
}
