/*
     • DownThemAll 3.0b6 (DTA) (Dark)
     • For Firefox
     • For DTA v3.0 Beta 6 (but also for DownThemAll! 2.0.18)
     • by Elian Dankiewicz


THIS Style              ► http://userstyles.org/styles/110581/
All Styles (List)       ► http://userstyles.org/users/192976/
*/


/*  MANAGER  */
@-moz-document
    url("chrome://dta/content/dta/manager.xul") {

/*  BACKGROUND  */
window, toolbar, statusbar {
    -moz-appearance: none !important;
    background-color: rgb(45,53,63) !important;
    color: rgb(220,220,220) !important;
}/*  --  */



/*  LOADING  */
#loadingbox {
    opacity: 0.7;
    background-color: rgb(25,25,25) !important;
}
#loading {
	color: rgb(222,174,37) !important;
}/*  --  */



/*  TOP TOOLBAR  */
#tooldonate {
    display: none !important;
}
#tools toolbarbutton {
    margin-top: 2px !important;
    margin-bottom: 3px !important;
}
#tools toolbarbutton .toolbarbutton-icon{
    padding: 1px 0px 1px 0px !important;
}
#tools[mode="icons"] toolbarbutton {
    padding: 0px 5px 0px 5px !important;
}
#tools toolbarbutton {
    -moz-appearance: none !important;
    background-color: rgba(58,68,79,1) !important;
    background-image:
            linear-gradient(to bottom,
                rgba(122,137,152,0.47),
                rgba(129,139,150,0.14)
                ) !important;
    border-width: 0px !important;
    box-shadow:
          inset 0px 0px 0px 1px rgba(0,0,0,0.7),
          inset 0px 1px 0px 1px rgba(140,158,176,0.40),
                   0px 0px 0px 1px rgba(66,75,86,0.6)
          !important;
    color: rgb(215,241,255) !important;
    padding: 2px 2px 1px 4px;
    text-shadow: 0px -1px 0px rgba(11, 12, 13, 0.45) !important;
    transition-property: background-color, box-shadow, color !important;
    transition-duration: 300ms !important;
    transition-timing-function: ease !important;
    transition-delay: 0s !important;
}
#tools toolbarbutton:-moz-any(:hover, :focus, :focus:hover) {
    background-color: rgba(48,58,69,1) !important;
    background-image:
            linear-gradient(to bottom,
                rgba(111,126,140,0.45),
                rgba(93,105,118,0.20)
                ) !important;
    box-shadow:
          inset 0px 0px 0px 1px rgba(0,0,0,0.7),
          inset 0px 0px 3px 2px rgba(10,18,25,0.25),
                   0px 0px 0px 1px rgba(66,75,86,0.9)
          !important;
   color: rgb(152,217,252) !important;
}
#tools toolbarbutton:not([disabled="true"]):-moz-any(:active, :active:hover) {
    background-color: rgba(5,15,26,0.05) !important;
    background-image:
            linear-gradient(to bottom,
                rgba(255,255,255,0.0),
                rgba(0,0,0,0.0)
                ) !important;
    box-shadow:
          inset 0px 0px 4px 1px rgba(0,0,0,0.95),
          inset 0px 0px 0px 1px rgba(5,15,26,0.2),
                   0px 0px 0px 1px rgba(233,244,255,0.10)
          !important;
	  color: rgb(101,191,255) !important;
}
#tools toolbarbutton[disabled="true"] {
    background-color: rgba(247,185,23,0.1) !important;
    background-image:
            linear-gradient(to bottom,
                rgba(255,255,255,0.0),
                rgba(0,0,0,0.0)
                ) !important;
    box-shadow:
          inset 0px 0px 4px 1px rgba(0,0,0,0.95),
          inset 0px 0px 0px 1px rgba(5,15,26,0.2),
                   0px 0px 0px 1px rgba(233,244,255,0.10)
          !important;
	  color: rgb(120,120,120) !important;
}
/*  --  */



/*  LEFT BUTTONS  */
#actions toolbarbutton {
    -moz-appearance: none !important;
	background: none !important;
    margin: 0px 0px 0px 0px !important;
    padding: 3px 5px 3px 5px !important;
    border: 0px solid transparent!important;
}
#actions toolbarbutton:hover:not([disabled="true"]) {
    background-position: left, left, right !important;
	background-size: auto, 1px 100%, 1px 100% !important;
	background-repeat: no-repeat !important;
	background-image:
        linear-gradient(to top, transparent, rgba(255,255,255,.45)),
		linear-gradient(to top, transparent, rgba(255,255,255,.45) 30%),
		linear-gradient(to top, transparent, rgba(255,255,255,.45) 30%) !important;
    margin: 0px 0px 0px 0px !important;
    padding: 3px 5px 3px 5px !important;
    border: 0px solid transparent!important;
}
/*  --  */
#actions  {
	-moz-appearance: none !important;
	border-bottom: 2px solid transparent;
	-moz-border-bottom-colors: rgb(63,74,88)  rgb(17,20,23)!important;
}
#actions > toolbarseparator {
	-moz-appearance: none !important;
	border-top: 3px solid transparent;
	border-bottom: 3px solid transparent;
	border-left: 0px solid transparent !important;
	border-right: 1px solid transparent !important;
	-moz-border-top-colors: transparent transparent rgb(17,20,23) !important;
	-moz-border-bottom-colors: transparent transparent rgb(63,74,88) !important;
}





/*  TREE LIST PROGRESSMETER  */
treechildren::-moz-tree-progressmeter {
    border: 2px solid !important;
    padding: 0px 0px 0px 0px !important;
    -moz-appearance: none !important;
    -moz-border-top-colors: rgb(45,45,45) rgb(0,0,0) !important;
    -moz-border-right-colors: rgb(45,45,45) rgb(0,0,0) !important;
    -moz-border-bottom-colors: rgb(75,75,75) rgb(0,0,0) !important;
    -moz-border-left-colors: rgb(45,45,45) rgb(0,0,0) !important;
}
treechildren::-moz-tree-progressmeter(progressUndetermined),
treechildren::-moz-tree-progressmeter(progressUndetermined, pausedUndetermined),
treechildren::-moz-tree-progressmeter(progressUndetermined, pausedUndetermined, pausedAutoretrying) {
    background-color: rgb(55,55,55) !important;
}
/*  --  */




/*  STATUSBAR  ITEMS  */
#statusSpeed {
    min-width: 70px !important;
}





/*  SEARCH  */
#search {
    -moz-appearance: none !important;
    border-radius: 0px !important;
    padding: 3px !important;
    min-height: 22px !important;
    max-height: 22px !important;
    border: 1px solid rgb(27,31,35) !important;
    color: rgb(200,200,200) !important;
    background-color: rgba(105,114,122,1) !important;
    background-image: linear-gradient(
                                  rgba(66,73,79,1),
                                  rgba(66,73,79,0)
                                  ) !important;
    box-shadow:
            0 1px 10px rgba(66,73,79,1) inset,
            0 1px 0 hsla(210,16%,76%,0.15),
            0px 24px 0px 0px rgba(66,73,79,1) inset;
    transition: all 700ms ease 0s !important;
}
#search:hover {
    -moz-appearance: none !important;
    border: 1px solid rgba(164,167,172,0.5) !important;
    transition: border 500ms ease 0s !important;
}
#search[focused="true"] {
    border-color: rgb(51,159,255) !important;
    color: rgb(255,252,255) !important;
    background-color: rgba(105,114,122,1) !important;
    box-shadow:
            0 1px 10px rgba(66,73,79,1) inset,
            0 1px 0 hsla(210,16%,76%,0.15),
            0px 0px 0px 0px rgba(66,73,79,1) inset;
    transition: all 500ms ease 0s !important;
}
#search > image {
    list-style-image: url(
        data:image/png;base64,
    ) !important;
    -moz-image-region: rect(0px, 16px, 16px, 0px) !important;
    opacity: 0.4 !important;
}
#search:hover > image {
    -moz-image-region: rect(0px, 32px, 16px, 16px) !important;
    opacity: 1 !important;
}/*  --  */







/*  SPEED LIMIT  */
#speedLimit {
    -moz-appearance: none !important;
    min-width: 100px !important;
    height:10px !Important;
    padding: 0px 0px 0px 2px !important;
    margin: 1px 0px 1px 0px !important;
    font-size: 13px !important;
    background-color:rgb(64,72,82) !important;
    border: 1px solid rgba(179,182,186,0.4) !important;
    box-shadow:
              0 0 0 1px rgba(66,79,95,0) inset
              !important;
    transition: all 500ms ease 0s !important;
}
#speedLimit:hover,
#speedLimit:focus {
    -moz-appearance: none !important;
    background-color:rgb(37,44,51) !important;
    border-color: rgb(255,255,255) !important;
    box-shadow:
              0 0 0 1px rgba(66,79,95,0) inset
              !important;
    transition: all 300ms ease 0s !important;
}
#speedLimit .menulist-label-box {
    background-color: transparent !important;
    color: rgb(179,182,186) !important;
    margin: 0px 0px 0px 0px !important;
}

#speedLimit:hover .menulist-label-box ,
#speedLimit:focus .menulist-label-box {
    color: rgb(255,255,255) !important;
}

#speedLimit[open="true"] {
    border-color: rgb(23,185,247) !important;
    transition: all 300ms ease 0s !important;
}
#speedLimit[open="true"] .menulist-label-box {
    color: rgb(23,185,247) !important;
    text-shadow: 0px 0px 2px rgba(8,159,218,0.7) !important;
    transition: all 300ms ease 0s !important;
}

#speedLimit dropmarker{
    -moz-appearance: none !important;
    width: 12px !important;
    height: 12px !important;
    background-color: transparent !important;
    border: 0px solid transparent !important;
    padding: 7px 0px 0px 0px !important;
    opacity: 0.6  !important;
    list-style-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAwAAAAMCAYAAABWdVznAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAMUlEQVQoz8WRyQ0AMAzCQMr+K7srxI+o/C2uAjGaSJ0AJOkWwDhgImE6YEpjV+r/4x7lPgknh6mMxAAAAABJRU5ErkJggg==
                ) !important;
}
#speedLimit:hover dropmarker:not([disabled="true"]) ,
#speedLimit:focus dropmarker:not([disabled="true"]) {
    opacity: 1  !important;
}
#speedLimit dropmarker:hover:active:not([disabled="true"]),
#speedLimit:hover:active > .menulist-dropmarker {
    padding: 7px 0px 0px 0px !important;
}
#speedLimit[open="true"] dropmarker { list-style-image: url(
                data:image/png;base64,
                ) !important;
    opacity: 1 !important; }
#speedLimit dropmarker[disabled="true"] { list-style-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAwAAAAMCAYAAABWdVznAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAMUlEQVQoz8WRyQ0AMAzCQMr+K7srxI+o/C2uAjGaSJ0AJOkWwDhgImE6YEpjV+r/4x7lPgknh6mMxAAAAABJRU5ErkJggg==
                ) !important;
    opacity: 0.05 !important; }
/*  ----  */


/*  SPINS BUTTONS  */
.spinbuttons-button {
    -moz-appearance: none !important;
    min-width: 16px !important;
    min-height: 10px !important;
    margin: 0px !important;
    padding: 0px !important;
    border: 1px solid rgba(179,182,186,0.4) !important;
    background-color:rgb(64,72,82) !important;
    background-repeat: no-repeat;
    transition: all 300ms ease 0s !important;
}
.spinbuttons-button:hover {
    background-color:rgb(37,44,51) !important;
    border-color: rgb(255,255,255) !important;
}
.spinbuttons-button:active {
    border-color: rgb(23,185,247) !important;
}

.spinbuttons-up {
    background-image: url(
      data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAHCAYAAAABIM1CAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAdElEQVQY05WPoQ2AMBBFH54REGyCQCI6AOLUjXDjIZiAFRB4DL6YI2nSNilf/uS9+wfl9EAws8PMDiB415QeCKp6R4+q3q2SDP4jyWARiSLSLCnNXoG18k6WpXIpWwYsH9QlghGYHLiADXiS92Zg8G4HToAXm+N1LheDiTgAAAAASUVORK5CYII=
    ) !important;
    background-position: center 1px !important;
    border-bottom: 0px solid !important;
}
.spinbuttons-up:hover {
    box-shadow: inset 0px -1px 0px 0px rgba(255,255,255,1) !important;
}
.spinbuttons-up:active {
    box-shadow: inset 0px -1px 0px 0px rgba(23,185,247,1) !important;
    background-image: url(
        data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAHCAYAAAABIM1CAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAhElEQVQY04XPIQ6DUBBF0fM9S6joTioqERgcgoSEJbC8iq4AwwIq6jFopoYmTfMBMWrm3vdGRPgfFKh0w6gbRlQosre7cN3PaVojTWuo+3lPcgqfSY6TqzZU7aHkV5Cr3aDJvZMTlLmkbDPKL5c2WErpitsGvPGIiGXbFbjjggXPiHjBBzrNzVWNOU4ZAAAAAElFTkSuQmCC
    ) !important;
}
.spinbuttons-down {
    background-image: url(
        data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAHCAYAAAABIM1CAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAeUlEQVQY05XOIRbCMBBF0YvPEhDsBIGsyAIqorKELA/BCroFBL4GH0w4nELKab/8M+/NHHxywhkBD1zxbLOAC46tu+HuK0NtyTnPiA0MiDnn+T3HoJPFUpOMGDt97Al+LqWUakqprnxmk2QPvCrZAy8kpZSplDL9g18vrHUuqbRmkgAAAABJRU5ErkJggg==
    ) !important;
    background-position: center center;
    border-top: 0px solid !important;
    box-shadow: inset 0px 1px 0px 0px rgba(255,255,255,0) !important;
}
.spinbuttons-down:hover {
    box-shadow: inset 0px 1px 0px 0px rgba(255,255,255,1) !important;
}
.spinbuttons-down:active {
    box-shadow: inset 0px 1px 0px 0px rgba(23,185,247,1) !important;
    background-image: url(
        data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAHCAYAAAABIM1CAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAjUlEQVQY033OIQ6DQBCF4X/8HqGCm1QgEWNwCBISjsDxKnoCDAeoqMegeYhuCGR3a+fN+2ZMEgBmVgFPIABf4CVpi1kAauABbMBb0gcASUSksWWXLbtoxxXwiAXAacf1zKE5exfgvvRDOqDLzD0HpJe8F94r91kClJB/5QQoflIoZ4EbMkwzwzSXypI4AJNNzVX4aYzdAAAAAElFTkSuQmCC
    ) !important;
}/*  --  */


/*  SPEED MENU  */
#listSpeeds {
    -moz-appearance: none !important;
    border-radius: 4px !important;
    font-weight: normal !important;
    border: 1px solid rgb(22,25,28) !important;
    box-shadow: 0 1px 0 rgba(162,184,204,0.25) inset !important;
    background-color: transparent !important;
    background-image:
            linear-gradient(to bottom,
                rgb(37,46,54),
                rgb(36,40,45)
                ) !important;
}
#listSpeeds {
    width: 100px !important;
}
#listSpeeds menuitem {
    color: rgb(204,204,204) !important;
}
#listSpeeds menuitem:hover {
    background-color: rgb(20,20,20) !important;
}
#listSpeeds menuitem[selected="true"] {
	  font-weight: bold;
    color: rgb(51,159,254) !important;
}
#listSpeeds menuseparator {
    -moz-appearance: none !important;
    padding: 0px 1px 0px 1px;
    margin: 2px 0px 2px 0px;
    border-top: 0px solid !important;
    border-bottom: 0px solid !important;
    height: 2px !important;
    background: linear-gradient(to right,
                       rgba(9,13,17,0) 0%,rgba(9,13,17,1) 20%,
                       rgba(9,13,17,1) 80%,rgba(9,13,17,0) 100%) ,
                       linear-gradient(to right,
                       rgba(58,70,80,0) 0%,rgba(58,70,80,1) 20%,
                       rgba(58,70,80,1) 80%,rgba(58,70,80,0) 100%)
                       !important;
    background-repeat: no-repeat, no-repeat  !important;
    background-size: 100% 1px , 100% 1px  !important;
    background-position:  top center,  bottom center  !important;
    background-color:  transparent  !important;
}/*  --  */


#tooltip {
    -moz-appearance: none !important;
    border-radius: 4px !important;
    font-weight: normal !important;
    border: 1px solid rgb(22,25,28) !important;
    box-shadow:
        0 1px 0 rgba(162,184,204,0.25) inset,
        0 0 4px hsla(0,0%,0%,0.95) !important;
    background-color: transparent !important;
    background-image:
            linear-gradient(to bottom,
                rgb(37,46,54),
                rgb(36,40,45)
                ) !important;
    color: rgb(204,204,204) !important;
}
tooltip textbox.plain {
    color: rgb(204,204,204) !important;
}
#timeRemaining[value=Complete] {
    color: rgb(20,204,20) !important;
}


}
/*  --  */




@-moz-document
    url("chrome://dta/content/dta/addurl.xul"),
    url("chrome://dta/content/dta/manager/conflicts.xul"),
    url("chrome://dta/content/dta/select.xul") {


/*  --  */
#DownThemAll, statusbar {
    -moz-appearance: none !important;
    background-color: rgb(45,53,63) !important;
    color: rgb(220,220,220) !important;
}/*  --  */




/*  INPUT TEXT  SINGLE LINE  */
textbox:not([multiline="true"]) {
    -moz-appearance: none !important;
    border-radius: 0px !important;
    padding: 3px !important;
    min-height: 22px !important;
    max-height: 22px !important;
    border: 1px solid rgb(27,31,35) !important;
    color: rgb(200,200,200) !important;
    background-color: rgba(105,114,122,1) !important;
    background-image: linear-gradient(
                                  rgba(66,73,79,1),
                                  rgba(66,73,79,0)
                                  ) !important;
    box-shadow:
            0 1px 10px rgba(66,73,79,1) inset,
            0 1px 0 hsla(210,16%,76%,0.15),
            0px 24px 0px 0px rgba(66,73,79,1) inset;
    transition: all 700ms ease 0s !important;
}
textbox:hover:not([multiline="true"]) {
    -moz-appearance: none !important;
    border: 1px solid rgba(164,167,172,0.5) !important;
    transition: border 500ms ease 0s !important;
}
textbox[focused="true"]:not([multiline="true"]) {
    border-color: rgb(51,159,255) !important;
    color: rgb(255,252,255) !important;
    background-color: rgba(105,114,122,1) !important;
    box-shadow:
            0 1px 10px rgba(66,73,79,1) inset,
            0 1px 0 hsla(210,16%,76%,0.15),
            0px 0px 0px 0px rgba(66,73,79,1) inset;
    transition: all 500ms ease 0s !important;
}/*  --  */





/*  GROUPS MENU  */
menulist {
    -moz-appearance: none !important;
    width:auto !Important;
    max-height: 22px !Important;
    padding: 0px 0px 0px 0px !important;
    font-size: 13px !important;
    background-color:rgb(64,72,82) !important;
    border: 1px solid rgba(179,182,186,0.4) !important;
    box-shadow:
              0 0 0 1px rgba(66,79,95,0) inset
              !important;
    transition: all 500ms ease 0s !important;
}
menulist:hover,
menulist:focus {
    -moz-appearance: none !important;
    background-color:rgb(37,44,51) !important;
    border-color: rgb(255,255,255) !important;
    box-shadow:
              0 0 0 1px rgba(66,79,95,0) inset
              !important;
    transition: all 300ms ease 0s !important;
}
menulist .menulist-label-box,
.menulist-editable-input {
    -moz-appearance: none !important;
    background-color: transparent !important;
    color: rgb(179,182,186) !important;
}

menulist:-moz-any(:hover, focus) .menulist-label-box ,
menulist:-moz-any(:hover, focus) .menulist-editable-input,
menulist:-moz-any(:hover, focus) .menulist-label {
    color: rgb(255,255,255) !important;
}

menulist[open="true"] {
    border-color: rgb(23,185,247) !important;
    transition: all 300ms ease 0s !important;
}
menulist[open="true"] .menulist-label-box {
    color: rgb(23,185,247) !important;
    text-shadow: 0px 0px 2px rgba(8,159,218,0.7) !important;
    transition: all 300ms ease 0s !important;
}

menulist dropmarker{
    -moz-appearance: none !important;
    width: 12px !important;
    height: 12px !important;
    background-color: transparent !important;
    border: 0px solid transparent !important;
    padding: 7px 0px 0px 0px !important;
    opacity: 0.6  !important;
    list-style-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAwAAAAMCAYAAABWdVznAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAMUlEQVQoz8WRyQ0AMAzCQMr+K7srxI+o/C2uAjGaSJ0AJOkWwDhgImE6YEpjV+r/4x7lPgknh6mMxAAAAABJRU5ErkJggg==
                ) !important;
}
menulist:hover dropmarker:not([disabled="true"]) ,
menulist:focus dropmarker:not([disabled="true"]) {
    opacity: 1  !important;
}
menulist dropmarker:hover:active:not([disabled="true"]),
menulist:hover:active > .menulist-dropmarker {
    padding: 7px 0px 0px 0px !important;
}
menulist[open="true"] dropmarker { list-style-image: url(
                data:image/png;base64,
                ) !important;
    opacity: 1 !important; }
menulist dropmarker[disabled="true"] { list-style-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAwAAAAMCAYAAABWdVznAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAMUlEQVQoz8WRyQ0AMAzCQMr+K7srxI+o/C2uAjGaSJ0AJOkWwDhgImE6YEpjV+r/4x7lPgknh6mMxAAAAABJRU5ErkJggg==
                ) !important;
    opacity: 0.05 !important; }
/*  ----  */







/*  BUTTONS BACKGROUND  */
button {
    -moz-appearance: none !important;
    background-color: rgba(58,68,79,1) !important;
    background-image:
            linear-gradient(to bottom,
                rgba(122,137,152,0.47),
                rgba(129,139,150,0.14)
                ) !important;
    border-width: 0px !important;
    border-style: solid !important;
    border-color: rgb(29,37,46) !important;
    border-radius: 3px !important;
    box-shadow:
          inset 0px 0px 0px 1px rgba(0,0,0,0.7),
          inset 0px 1px 0px 1px rgba(140,158,176,0.40),
                   0px 0px 0px 1px rgba(66,75,86,0.6)
          !important;
    color: rgb(215,241,255) !important;
    padding: 2px 2px 1px 4px;
    text-shadow: 0px -1px 0px rgba(11, 12, 13, 0.45) !important;
