css:
/* Windows 8 Dark Theme Text */
/* by Norzman5 */

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* Changes background tab text white */

.tabmail-tab > .tab-stack > .tab-content:not([selected=true]) > .tab-text {

  color: #fff !important;

}

/* Changes menubar text white */

menu[label="File"] {

  color: #fff !important;

}

menu[label="Edit"] {

  color: #fff !important;

}

menu[label="View"] {

  color: #fff !important;

}

menu[label="Go"] {

  color: #fff !important;

}

menu[label="Message"] {

  color: #fff !important;

}

menu[label="Tools"] {

  color: #fff !important;

}

menu[label="Help"] {

  color: #fff !important;

}
