css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cygnus.cc.kuleuven.be") {
   
#globalNavPageNavArea{
    background-color:#F24F11;
        background-image:url(http://i.imgur.com/KrE6Uaa.png);   
    background-repeat:no-repeat;
    border-bottom:1px solid #FFF;
    direction:ltr!important;
    height:46px!important;
    left:0;
    padding-left:195px!important;
    position:fixed;
    top:0;
    width:100%;
    z-index:1001
}
@font-face{
    font-family:tolicons;
    src:url(fonts/tolicons/tolicons.eot);
    src:url(fonts/tolicons/tolicons.eot?#iefix) format('embedded-opentype'),url(fonts/tolicons/tolicons.woff) format('woff'),url(fonts/tolicons/tolicons.ttf) format('truetype'),url(fonts/tolicons/tolicons.svg#Toledowebfont) format('svg')
}

#quick_links_wrap{
    display:none!important
}

body,.contentPaneWide,.container,.contentBox{
    background:0 0;
    background-color:#FFF;
    border:0;
    border-radius:0;
    box-shadow:none;
    margin:0;
    padding:0;
    position:static
}

#globalNavPageNavArea .tol_top_tab[id*=m_],#globalNavPageNavArea .tol_top_tab[id*=x_]{
    display:none!important
}



#globalNavPageNavArea .tol_top_tab[id*=m_],#globalNavPageNavArea .tol_top_tab[id*=x_]{
    display:none!important
}



.tol_top_tab{
    border-right:1px solid #FFF;
    display:inline-block;
    float:left;
    height:46px;
    padding:0 10px;
    position:relative;
    border-radius:5px;
}

.tol_top_tab:first-child{
   
    border-left:1px solid #FFF
}

.tol_top_tab a,.tol_top_tab a:hover{
    color:#FFF;
    display:block;
    height:20px;
    padding:16px 25px 0
}

.tol_top_tab a:after{
    content:'I';
    font-family:tolicons;
    font-size:30px;
    left:10px;
    position:absolute;
    top:0
}

.tol_top_tab[id="My Toledo"] a:after{
    content:'Y'
}

.tol_top_tab[id="Toledo+"] a:after,.tol_top_tab[id="bbcms.label"] a:after{
    content:'U'
}

.tol_top_tab[id="Local Admin"] a:after{
    content:'B'
}

.tol_top_tab[id=Help] a:after{
    content:'H'
}

.tol_top_tab[id=Bibliotheek] a:after{
    content:'@'
}

.tol_top_tab[id="SystemAdmin.label"] a:after{
    content:'A'
}

.tol_top_tab.active{
    background-color:#009CAD
}

.tol_top_tab.active a{
    color:#FFFFFF!important;
    font-weight:700
}

#global-nav,.global-nav-bar-wrap .logout{
    direction:ltr;
    display:block!important;
    position:fixed!important;
    right:0;
    top:0
}

.global-nav-bar-wrap .logout{
    right:0px
}

.global-nav-bar-wrap .logout{
    background-color:transparent;
    border-left:1px solid #FFF;
    height:46px;
    margin:0;
    padding:0;
    width:25px;
    z-index:1001
}

.global-nav-bar-wrap .logout>a,.global-nav-bar-wrap .logout>a:hover{
    background-color:#ed1c24;
    background-image:none!important;
    height:100%;
    border-radius:5px;
    width:auto!important
       
}

.global-nav-bar-wrap .logout>a:before,.global-nav-bar-wrap .logout #topframe\.login\.label:before{
    color:#fff;
    font-family:tolicons;
    font-size:26px;
    position:absolute
}

.global-nav-bar-wrap .logout>a:before{
    content:"X";
    left:10004px;
    top:25%
}

#topframe\.help\.label,#topframe\.home\.label,.topGlobalLinks a .link-icon{
    display:none!important
}

.global-nav-bar-wrap .logout #topframe\.login\.label{
    background-color:#39b54a!important;
    border:0;
    height:0;
    overflow:hidden;
    padding-top:46px;
    width:25px!important
}

.global-nav-bar-wrap .logout #topframe\.login\.label:before{
    content:"W";
    top:0
}

div#global-nav{
    border-left:1px solid #FFF;
    height:46px;
    margin:0;
    padding:0;
    position:absolute;
    right:26px
}

a#global-nav-link.nav-link{
    height:100%;
    margin:0;
    padding:0 26px 0 10px;
    color:#FFF!important;
    vertical-align:baseline!important
}

img#global-avatar,#global-nav .global-top-avatar{
    border:0;
    display:inline-block;
    float:none;
    height:46px;
    left:-10px;
    margin:0;
    padding:0;
    position:relative;
    top:0;
    width:auto
}

#global-nav-link #global-toggle-img{
    display:none
}

.global-nav-bar .badge,.side-nav .menu-icon span.badge{
    background-color:#39B54A;
    border:1px solid #FFF;
    border-right:0;
    border-top:0;
    font-size:12px;
    left:auto!important;
    margin:0;
    right:0;
    top:0
}

#globalNavPageNavArea+#globalNavPageContentArea{
    height:auto!important;
    margin-top:47px;
    overflow:visible!important;
    position:static
}

#global-toggle-img{
    display:none
}

#global-nav-link{
    padding-right:8px
}

#global-nav-link .nav-link .badge{
    position:relative;
    right:0;
    top:0;
    margin-left:10px
}
#moduleTitle,.portlet h2,.portlet h2.dragable
{
    background-color: #51A0D5;
    border-bottom: 1px solid #c7c8c9;
    color: #666;
    line-height: 2em;
    padding-left: 1.5em;

}
}
