css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.f1news.ru") {
  
  /* ================================================================================================ */
  /* Common */
  /* ================================================================================================ */
  
  a {
    color: #99B !important;
  }
  
  a:hover, a:active {
    color: #BBE !important;
  }
  
  img {
    border: 1px solid #555 !important;
  }
  
  div {
    color: #AAA !important;
  }
  
  button.btn {
    color: #999 !important;
    background: linear-gradient(to bottom, #3A3A3A 0%, #333 100%) repeat scroll 0% 0% !important;
    text-shadow: 0px 0px 0px !important;
    border: 1px solid #222 !important;
    box-shadow: 0px 0px 0px !important;
    letter-spacing: 1px !important;
  }
  
  ::-moz-selection {
    background: #77A !important;
    color: black !important;
  }
  
  ::selection {
    background: #77A !important;
    color: black !important;
  }
  
  /* ================================================================================================ */
  /* Background */
  /* ================================================================================================ */
  
  /* body */
  body {
    background: #222 !important;
  }
  
  /* container */
  div.container {
    background: #222 !important;
    box-shadow: unset !important;
  }
  
  /* ================================================================================================ */
  /* Header */
  /* ================================================================================================ */
  
  /* Hide social buttons */
  div.header_social {
    display: none !important;
  }
  
  /* Hide logo */
  div.header_logo {
    display: none !important;
  }
  
  div.header_top {
    height: 10px !important;
  }
  
  div.header_search {
    top: 21px !important;
  }
  
  ul.navbar_head {
    border-top: 4px solid #AAA !important;
  }
  
  ul.navbar_body {
    border-top: 1px solid #AAA !important;
    border-bottom: 1px solid #AAA !important;
  }
  
  /* ================================================================================================ */
  /* Main section */
  /* ================================================================================================ */
  
  ul.list_striped li, table.list_striped tr td {
    background: #353535 !important;
    border-bottom: 1px solid #666 !important;
  }
  
  ul.list_striped li:nth-child(2n), table.list_striped tbody tr:nth-child(2n) td {
    background: #444 !important;
  }
  
  ul.list_striped a:hover {
    color: #BBE !important;
  }
  
  div.widget_body {
    border-top: 1px solid #666 !important;
  }
  
  div.widget_foot {
    border-top: 1px solid #AAA !important;
  }
  
  div.banner_4, banner_Main_page {
    display: none !important;
  }
  
  /* results table */
  
  table.f1Table {
    border-top-color: #666 !important;
  }
  
  table.f1Table tr.firstLine > td {
    background-color: #444 !important;
  }
  
  table.f1Table tr.lineOne > td {
    background-color: #353535 !important;
  }
  
  table.f1Table tr.lineTwo > td {
    background-color: #444 !important;
  }
  
  table.f1Table tr.lineSelected > td {
    background-color: #666 !important;
  }
  
  /* ================================================================================================ */
  /* Right column */
  /* ================================================================================================ */
  
  /* Hide banner */
  div.banner_sidebar {
    display: none !important;
  }
  
  td.test_sidebar-table-city {
    background: #353535 !important;
    border-top: 1px solid #666 !important;
    border-bottom: 1px solid #666 !important;
  }
  
  td.data-gray {
    color: #BBB !important;
  }
  
  table {
    background: #444 !important;
  }
  
  div.presentation_sidebar-table, div.widget_featured {
    background: #444 !important;
  }
  
  div.widget_head {
    border-top: 1px solid #666 !important;
    background-color: #353535 !important;
  }
  
  div.widget_title {
    background-color: #353535 !important;
  }
  
  div.weather_item_image > i {
    filter: invert(65%) !important;
  }
  
  div.weather_item {
    border-left: 1px solid #666 !important;
  }
  
  div.weather_item:first-child {
    border-left: medium none !important;
  }
  
  /* Upcoming events table */
  table.list_featured tr td {
    background-color: #444 !important;
  }
  
  table.list_featured thead th {
    background-color: #353535 !important;
    border-top: 1px solid #666 !important;
    border-bottom: 1px solid #666 !important;
  }
  
  .list_featured span {
    color: #AAA !important;
  }
  
  a.red {
    color: #99B !important;
  }
  
  .list a:hover {
    color: #BBE !important;
  }
  
  div.forecasts_contest_item_slider {
    background-color: #88B !important;
  }
  
  div.select_date {
    background-color: transparent !important;
  }
  
  div.select_date > .widget_head {
    padding: 0 !important;
  }
  
  div.select_date > .widget_body {
    padding-top: 15px !important;
  }
  
  div.datepicker-days > table {
    background-color: transparent !important;
  }
  
  td.day:hover {
    background-color: #666 !important;
    color: #FFF !important;
  }
  
  div.total {
    display: none !important;
  }
  
  /* ================================================================================================ */
  /* Footer */
  /* ================================================================================================ */
  
  div.footer, div.container > a {
    display: none !important;
  }
  
  /* ================================================================================================ */
  /* Ads */
  /* ================================================================================================ */
 
  div[id^="yandex_ad"], div[id^="adfox"], div[id^="AdFox"], div.catfish {
    display: none !important;
  }
  
}
