css:
@-moz-document domain("kongregate.com") {
#play.dark_game_page_background-dark_background .gamepage_categories_list a {
color: #FFCC99;
display: inline-block;
font: 11px Arial, sans-serif;
padding: 13px 0 15px;
text-shadow: 0px 1px 10px #2B2B2B;
}
.post .author .edit a {
color: #666;
text-transform: none;
text-decoration: none;
}
body#new_profile #main .pod_heading {
border-top: 0;
font: 600 18px 'Open Sans','Arial',sans-serif;
margin-bottom: 14px;
text-transform: uppercase;
padding-top: 10px;
}
#play.dark_game_page_background-dark_background #global .sitemessage {
width: 922px;
text-align: center;
text-shadow: 0px -1px 1px #FFF;
}
#headerwrap {
min-width: 940px;
height: 100px;
}
.forum_header {
border-bottom: 0px solid #ccc;
margin: 0 0 14px 0;
padding: 0 0 14px 0;
position: relative;
height: 40px;
}
body#forums #feature label {
color: #B5B5B5;
font-size: 0.95em;
font-weight: normal;
position: relative !important;
}
.mbl, .mvl, .mal {
margin-bottom: 25px !important;
border-bottom: 1px dotted #ccc;
}
.pll, .phl, .pal {
padding-left: 55px !important;
}
.ptl, .pvl, .pal {
padding-top: 35px !important;
}
#headerwrap {
min-width: 922px;
background-color: #900;
border-bottom: 3px dotted #FFFFFF;
}
#footer .kongregate-logo a {
height: 26px;
width: 153px;
background-position: 0 -1616px;
margin: 8px 0px;
box-shadow: 0px 1px 1px #000;
}
.no_subwrap #subwrap {
min-width: 940px;
padding-top: 0;
border-top: 3px dotted #EEE;
}
#header h2 a {
clear: both;
width: 311px;
height: 32px;
background-position: 0 0;
}
.main_nav_item:hover .main_nav_top_item, .main_nav_item:hover .main_nav_top_item.active {
background: #423f3e;
color: #FC9;
}
#forums .forum_header .forum_title {
font-size: 24px;
font-family: arial;
padding: 6px 0px;
}
form#search dt input {
float: right;
border: 0;
margin: 0;
padding: 2px;
width: 199px;
height: 13px;
background: #f0f0ef;
letter-spacing: 0px;
border-radius: 20px;
text-align: center;
box-shadow: 0px 1px 7px rgba(76, 4, 4, 0.66);
}
form#search dd input {
float: none;
width: 0px;
height: 0px;
background-position: 0 -1536px;
margin: 0;
padding: 0;
text-indent: -9999px;
border: 0;
}
form#search {
background-color: rgba(113, 0, 0, 0);
padding: 6px;
width: 206px;
}
.main_nav_item:hover .main_nav_top_item, .main_nav_item:hover .main_nav_top_item.active {
background: #423f3e;
color: #FFF;
}
#profile_masthead {
overflow: visible;
position: relative;
box-shadow: 0px 5px 10px rgba(204, 204, 204, 0.51);
}
#profile_user_level_info #user_level .user_metric_stat a {
color: #A62C2C;
font-size: 115px;
font-weight: 400;
line-height: 110px;
}
#profile_full_user_bio {
border-color: #ccc;
padding: 15px;
width: 460px;
top: 275px;
left: 345px;
z-index: 1000002;
box-shadow: 0px 2px 15px rgba(0, 0, 0, 0.4);
}
#profile_user_level_info #user_points .user_metric_stat a, #profile_user_metrics .user_metric_stat a {
color: #2C6EA6;
font-size: 27px;
font-weight: 500;
line-height: 42px;
}
#new_profile .kpan_cal {
background-color: #f8f8f8;
border: 1px solid rgba(215, 215, 215, 0);
border-top: 2px dotted rgba(215, 215, 215, 1);
border-top-left-radius: 65px;
border-top-right-radius: 6px;
margin: 23px 10px 3px;
padding: 1px;
box-shadow: 0px 5px 10px rgba(204, 204, 204, 0.51);
}
#new_profile .kpan_cal .cur_kpan {
min-height: 100px;
padding: 15px 0 15px 144px;
}
#new_profile .kpan_cal .week {
border: 0;
border-bottom-left-radius: 5px;
border-bottom-right-radius: 5px;
border-top: 1px dotted rgba(222, 217, 217, 0);
color: #000;
}
#new_profile .kpan_cal .botd_msg {
background-color: rgba(253, 243, 243, 0);
border-bottom-left-radius: 0px;
bottom: -63px;
left: 0;
}
.kpan_cal .badge_info .badge_info_inner {
padding: 11px 8px 11px 18px;
margin: 15px;
}
.kpan_cal .botd_msg p {
display: table-cell;
line-height: 20px;
padding: 11px 20px 11px 183px;
position: relative;
vertical-align: middle;
text-align: right;
}
.pod_heading {
border-bottom: 0px dotted #bbb;
border-top: 0px solid #6c6d6a;
}
body#new_profile #profile_aside p, body#new_profile #profile_aside li, body#new_profile #profile_aside dt, body#new_profile #profile_aside dd {
line-height: 40px;
}
.remove_feed_item .remove_arrow {
background-color: #fff;
background-position: 6px 9px;
border: 1px solid #EBF0F0;
border-top: 0px solid #cfcfcf;
cursor: pointer;
display: block;
height: 25px;
position: absolute;
right: 0;
top: 0;
width: 25px;
z-index: 102;
}
.feed_item_header a {
color: #A62C2C;
font-weight: bold;
font-size: 1.1em;
text-transform: none !important;
text-shadow: 0px 1px 5px #EBF0F0;
}
.follower_feed_item_container {
border-top: dotted 1px rgba(208, 208, 208, 0.5);
padding-top: 14px;
}
.feed_item_header {
border-top: solid 1px rgba(208, 208, 208, 0.5);
font: normal 11px/15px Verdana,Arial,sans-serif;
padding: 25px 0 9px 0;
}
.feed_item .feed_info_content, .feed_item .comment_quote {
background-color: rgba(177, 196, 197, 0.25);
padding: 8px;
position: relative;
border-radius: 6px;
}
.tab_index, .game_tab_index {
background: rgba(224, 224, 224, 1);
height: 33px;
list-style: none;
margin: 3px 3px 0 3px;
border-radius: 5px 150px 0px 0px;
border-top: 2px dotted #B1C4C5;
}
#profile_aside .tab_see_all {
background: rgba(168, 168, 168, 0.3);
padding: 2px 15px;
text-align: right;
border: 6px solid #FAFAFA;
border-top-left-radius: 10px;
border-top-right-radius: 10px;
border-bottom-left-radius: 10px;
border-bottom-right-radius: 10px;
}
.welcome-user>li>a {
color: #FFF;
display: block;
font: 600 14px/27px 'Source Sans Pro', 'Helvetica', Arial, sans-serif;
padding: 0px 20px;
text-decoration: none;
text-transform: inherit !important;
font-size: larger !important;
text-shadow: 0px 1px 2px rgba(0, 0, 0, 0.8);
}
.main_nav_top_item.active {
background: #A31313;
color: #FFF;
box-shadow: 0px 1px 5px rgba(60,60,60,.55);
border-radius: 6px 6px 0px 0px;
transition: ease .33s;
}
.main_navigation a {
color: #FFFFFF;
text-shadow: 0px 1px 2px rgba(0, 0, 0, 0.5);
display: block;
text-decoration: none;
text-transform: none;
background: transparent;
}
.welcome-user .profile #mini-profile-level {
display: inline-block;
height: 1.2em;
position: relative;
top: -1px;
vertical-align: middle;
}
.welcome-user .profile img {
float: left;
height: 27px;
margin-right: 12px;
width: auto;
}
.welcome-user>li {
background-color: rgba(203, 95, 95, 0.2);
color: #FFF;
float: left;
height: 28px;
list-style: none;
margin-left: 0px;
text-align: center;
box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.04);
}
#header h3#playing {
font: 10px/15px Verdana,Arial,sans-serif;
color: #FFF;
text-transform: uppercase;
letter-spacing: 0.12em;
text-shadow: 0px 1px 2px rgba(0,0,0,0.45);
}
body#forums {
font-size: 95%;
text-shadow: 0px 1px 2px rgba(50, 0, 0, 0.10);
text-transform: none;
}
hr {
height: 0px;
border: 0;
border-bottom: 2px solid #C8C8C8;
margin: 1em 0;
box-shadow: 0px 1px 1px rgba(128, 64, 64, 0.1);
border-radius: 30px;
}
.post .author a.post_creator {
color: #990000;
text-shadow: 0px 1px 0px rgba(255, 255, 255, 0.6);
}
.mtm, .mvm, .mam {
margin-top: 15px !important;
text-transform: none;
}
.searchbox .searchbox_field {
border: 1px solid #aaa;
float: left;
height: 20px;
line-height: 20px;
padding: 0 6px;
border-radius: 20px 0px 0px 20px;
text-align: center;
margin-right: -6px;
}
.btn_action, #admin-controls .button_to input[value="Delete Account"], #admin-controls .new_silencing input[type="submit"], #admin-controls .new_ban input[type="submit"], body.adjusted .btn_action, body.adjusted #admin-controls .button_to input[value="Delete Account"], #admin-controls .button_to body.adjusted input[value="Delete Account"], body.adjusted #admin-controls .new_silencing input[type="submit"], #admin-controls .new_silencing body.adjusted input[type="submit"], body.adjusted #admin-controls .new_ban input[type="submit"], #admin-controls .new_ban body.adjusted input[type="submit"] {
background-color: #7F1717;
background: -webkit-gradient(linear, left top, left bottom, from(#912929), to(#700808));
background: linear-gradient(to bottom, #912929,#700808);
border-color: #500606 !important;
-webkit-box-shadow: none;
box-shadow: none;
color: #fff !important;
text-shadow: 0 0 2px rgba(0,0,0,0.5);
}
body#forums div#feature table tr th {
background: #333;
color: #FFF;
font-size: 0.8em;
padding: 1px 10px;
border: 5px solid #333;
box-shadow: 0px 5px 10px rgba(0,0,0,.2);
}
body#forums div#feature table tr td {
padding: 15px;
border: 0px solid #fff;
border-top: 1px dotted rgba(192, 192, 192, 0.45);
}
img.grey {
background-color: #868686;
opacity: 0.33;
}
img.green {
background-color: #00cc00;
}
body#searchresults #main ol, ul.pagination {
font: normal 11.5px arial;
list-style: none;
margin: 0px 10px;
text-transform: none;
text-align: center;
}
.post .author {
background: -webkit-linear-gradient(left, rgba(128, 128, 128, 0.5) , white);
border-top: 2px solid #efefef;
color: #333;
font-size: 0.9em;
font-weight: bold;
text-transform: none;
min-height: 32px;
padding: 5px 10px;
vertical-align: top;
width: 230px;
border-radius: 2px;
box-shadow: -3px 0px 2px rgba(0, 148, 255, 0.08);
}
.post .author a {
color: #414141;
margin: 7px;
}
body#forums #forum_posts {
clear: both;
width: 922px;
margin: 10px;
padding: 0;
}
.posts .post .body blockquote {
border: 1px solid #AAC2DD;
border-left: 3px solid #7793AA;
border-right: 0;
background: #CCE4FF;
padding: 1px 10px;
margin-left: 0;
}
.post .body p {
margin: 0.5em 0;
font-family: Arial;
text-transform: none;
text-rendering: optimizeSpeed;
font-size: 100%;
}
.post .body p img {
display: block;
height: auto;
max-height: 90%;
max-width: 90%;
width: auto;
box-shadow: 0px 1px 3px rgba(128,128,128,0.75);
}
.my_games_block .game_icon img {
display: block;
height: auto;
max-height: 112px;
width: 140px;
border-radius: 2px;
border: 2px solid rgba(0, 0, 0, 0.3);
box-shadow: 0px 2px 10px rgba(0, 0, 0, 0.5);
}
table tr td.inv {
background: rgba(16, 48, 116, 0.10);
}
}
