css:
@-moz-document url-prefix("https://web.whatsapp.com/") {
#compose-input .block-compose .input-container,#compose-input .input,.block-compose #compose-input .input-container{color:black!important}
.app{		width: 100%;
		height: 100%;
		top: 0px;
}
.app-wrapper::before{display:none!important}
.chat{border-bottom:1px solid #000}
.chat,.location{background-color:#040603}
.chat-meta{color:#838484!important}
.chat-status,.header-secondary,.location-secondary{color:#A2ADB2!important}
.chat-title ,.header-title ,.location-main, .dropdown .menu-item a{color:#53c500!important;font-size:150%;font-family:'mario',cursive !important}
.chat:hover,.location:hover{background-color:black}
.chatlist .active.location,.chatlist .chat.active{background-color:black;border-bottom:1px solid #000}
.chatlist .active.location:hover,.chatlist .chat.active:hover, .infinite-list-item:hover{background-color:black;border-bottom:0px solid #000!important}
.chatlist .chat:after,.chatlist .list-title:after,.chatlist .location:after{border-bottom:0px solid #000}
.chatlist .chat:hover,.chatlist .location:hover{background-color:black;border-bottom:1px solid #000}
.cont-input-search{width:calc(100% - 35px)}
.cont-input-search,.input-search,.block-compose .input-container{background-color:rgba(162,173,178,0.5);border:none}
.drawer-body, .dropdown{background:rgba(0,0,0,1)}

.drawer-container-right,.pane-chat,.pane-intro{width:73%!important}

.drawer-info{background:rgba(0,0,0,0.0)}
.drawer-info .drawer-body{background:linear-gradient(175deg,rgba(0,0,0,1) 1%,rgba(69,72,77,1) 50%,rgba(0,0,0,1) 100%)}
.drawer-section-title .col-main, .drawer-title .drawer-title-body{color:#53c500!important;font-size:150%;font-family:'mario',cursive !important}
.drawer-value{color:#838484!important}
.dropdown .menu-item a:hover{background:rgba(0,0,0,0.0);color:#A2ADB2!important;font-size:150%;font-family:'mario',cursive !important}
.dropdown-right:after{background:black;box-sizing:border-box;color:#53c500!important;content:"Style by Pimp My Site";cursor:pointer;display:block;font-size:150%;font-family:'mario',cursive !important;font-size:14.5px;left:0;max-width:200px;padding:2px 58px 15px 24px;position:absolute;top:100%;white-space:nowrap;z-index:20}
.emoji-panel{background:linear-gradient(to bottom,#606C88 0%,#3F4C6B 100%)}
.icon-smiley, .icon-hide, .block-compose .icon-ptt,  .block-compose .icon-send, .icon-menu, .icon-chat, .icon-clip, .menu-horizontal>.menu-item,.menu-horizontal>span>.menu-item{background-color:#A2ADB2;border-radius:55px;padding:6px}

.icon-smiley:hover, .icon-hide:hover, .block-compose .icon-ptt:hover,  .block-compose .icon-send:hover, .icon-menu:hover, .icon-chat:hover, .icon-clip:hover, .menu-horizontal>.menu-item:hover,.menu-horizontal>span>.menu-item:hover{background-color:#53c500;border-radius:55px;padding:6px}

.audio-progress,.message-in .audio-state-played .audio-progress,.audio-progress{background-color:#53c500!important;}

.input-search{background-color:rgba(162,173,178,0.0);border:none}
.list-title,.drawer-header,.drawer{background:rgba(0,0,0,1);border-radius:0px;}
.menu-tabs-emoji{background:rgba(0,0,0,0.0)}

.message-in{
  margin:5px !important;
box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;

   background: rgba(0,0,0,0.5) !important;
   border:1px ridge rgba(0,0,0,0.5) !important;
   border-radius: 8px !important;
   text-shadow:none !important;
color:#838484!important;
}
  
 .message .emojitext 
  {color:#bebfbd!important
}
 .message-datetime {color:#bebfbd!important
}
  
  

.message-out,.msg-unread-body{
  margin:5px !important;
box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;

   background: rgba(0,0,0,0.5) !important;
   border:1px ridge rgba(0,0,0,0.5) !important;
   border-radius: 8px !important;
   text-shadow:none !important;
color:#838484!important;
}

.message-in:before,.message-out:before{display:none;}



.drawer-profile .drawer-header {background: rgba(0,0,0,0.5) !important;}

.media-viewer {background: rgba(0,0,0,0.75) !important;}
.media-panel-header{background: rgba(0,0,0,1) !important;}



.pane{height:100%!important}
.pane,.block-compose{background-color:#000}
.pane-chat-body{background-image:url(http://s1.directupload.net/images/140326/m5c5hsfn.jpg) !important;background-size:cover!important}
.pane-chat-header{background:#000}
.pane-header{background:#000}
.subheader-search{background:rgba(0,0,0,0.0);border-radius:0px;}
.well.animate-enter{background:rgba(0,0,0,0.0)}
.pane-list-body {
  background-color: #040603;
}
  .intro-image {
width: 356px;
height: 500px;
  	background-image: url(https://dl.dropboxusercontent.com/u/27347691/whatsappwebplus/whatsap55.png) !important;
	background-size: cover!important;
  
}
.unread-count{background-color:#53c500!important}




@font-face{font-family:mario;src:url(https://dl.dropboxusercontent.com/u/27347691/UNLEASH%20THE%20BEAST.ttf)}
}
