css:
@-moz-document url-prefix("http://www.toodledo.com"), url-prefix("http://lists.toodledo.com/"), url-prefix("http://outline.toodledo.com/") {
/* top nav bar */
#topnav-in {
background: linear-gradient(to bottom,#B4B8BF 0,#B4B8BF 100%) !important;
}

#topnav-in #desktop-setsubsign .userstylewarning a.minico, #sidenav-in .userstylewarning a.minico {
display: none !important;
}

#topnav-in #desktop-setsubsign .userstylewarning, #sidenav-in .userstylewarning {
display: none !important;
}

.expires {
background-color: #B4B8BF;
border: 1px solid #600;
border-radius: 3px;
border: none !important;
border-radius: 0px !important;
color: #173C60 !important;
}

/* task bar */

tlbr_search {
margin-left: 0px !important;
}
#tlbr_search {
float: none;
margin-left: 0px !important;
}

#tlbr_new {
background: rgb(23, 60, 96) !important;
color: #FFFFFF !important;
}

#tlbr_new:hover {
background: #2B6FB0 !important;
}
  

/* Add task screen */


input.button {
font-weight: bold;
background: #173C60 !important;
height: 28px;
color: #FFF;
border-radius: 0px !important;
text-shadow: none !important;
border: none !important;
}

#addtask, #searchtask {
background-color: #D8DCE0 !important;
}

#tlbr_show:hover {
background-color: #2B6FB0 !important;
}

#tlbr_sort .sorted1:hover {
background-blend-mode: overlay !important;
}

/* Subtasks */
subtasks .row {
border-top: 1px dotted #ccc;
border-bottom: 1px dotted #ccc !important;
font-size: .9em;
background-color: rgb(236, 238, 242) !important;
}
.moresubs {
margin-top: -9px !important;
font-size: .1em !important;
}

.subtasks .row:hover {
background-color: #FFFFFF !important;
}

/* tasks */

.row:hover {
background-color: #EJECF8 !important;
}
      
.cols:hover span, .cols:hover .note {
background-color: rgb(230, 230, 230) !important;
color: rgb(43, 96, 147) !important;
}

/* task check mark */
.chd:hover {
background: url(http://static.toodledo.com/images/css/sprites_icons_8.png) no-repeat -57px -57px !important;
}

.chd {
background: url(http://static.toodledo.com/images/css/sprites_icons_8.png) no-repeat -84px -57px !important;
}
  
/* body font */
  html, body {
font-size: .9em;
font-family: 'Avant Garde', Avantgarde, 'Century Gothic', CenturyGothic, AppleGothic, sans-serif !important;
height: 100%;
}

 .tabon {
font-weight: bold;
background-color: #B4B8BF !important;
}

input.button:hover {
font-weight: bold;
background: #2B6FB0 !important;
}

input.button {
font-weight: 300 !important;
font-family: 'Avant Garde', Avantgarde, 'Century Gothic', CenturyGothic, AppleGothic, sans-serif !important;
}
}
