css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("1chan.ru") {
	/* logo, mascot swap */

	.b-header-block_b-logotype a img {
	    display: none;
	}

	.l-footer-wrap.m-mascot-news {
	    background: #fff;
	}

	.b-header-block_b-logotype a {
	    display: block;
	    background: url("http://russia.tv/i/logo/standart-russia1.png");
	    height: 42px;
	    width: 184px;
	    margin: 19px 33px;
	}

	.b-header-block.m-mascot-news {
	    background: url("http://i.imgur.com/PpNSODk.jpg") bottom center no-repeat;
	}

	img[src*="http://1chan.ru/ico/favicons/1chan.ru.gif"] {
	    background: url("http://russia.tv/favicon-russia1.ico") repeat scroll 0% 0% transparent;
            padding-left: 16px;
            box-sizing: border-box;
	}

	/* color swap */

	.b-blog-panel ul li.b-blog-panel_b-add-entry, .b-post-statistics, .b-search-statistics, .b-chat-panel ul li.b-chat-panel_b-add-room, .b-chat_b-message.m-error {
	    background: #FFD0D0;
	}

	.b-ajax-loader-error, .b-header-block .b-header-block_b-sharelink:link, .b-header-block .b-header-block_b-sharelink:visited, .b-header-block .b-header-block_b-sharelink:hover, .b-post-added-notify, .b-blog-form .b-blog-form_b-error, .b-board-form .b-board-form_b-error, .b-board-post-added-notify, .b-live_b-error, .b-chat-added-notify {
	    background: #E56363;
	}

	a:hover, .b-blog-entry .b-blog-entry_b-header a:link, .b-blog-entry .b-blog-entry_b-header a:visited, .b-paginator ul li u, .b-live-entry a.b-live-entry_b-description:link, .b-live-entry a.b-live-entry_b-description:visited, .b-chat-rooms_b-room_b-header a:link, .b-chat-rooms_b-room_b-header a:visited, .b-chat-rooms_b-room_b-header a:hover {
	    color: #DA3535;
	}

	.g-input-error {
		background: #FDB5B5;
	}
}
