css:
@-moz-document domain("diskusjon.no"){

/***** colors used *******************************************
Background 1:		#000000
Background 2:		#1a1a1a
Background 3:		#202020
Background 4:		#282828
Borders:		#323232
Font-color:		#ccc
Link-color:		#2f8cb7
*************************************************************/

/***** misc and backgrounds **********************************/

.maintitle {
  background: #202020 url(http://i.imgur.com/IsFpff1.png) repeat-x top;
  -webkit-box-shadow: inset 0px 1px 0 #222;
	-moz-box-shadow: inset 0px 1px 0 #222;
	box-shadow: inset 0px 1px 0 #222;
	border-color: #222;}

.input_submit.alt {
	background: #212121 url(https://www.diskusjon.no/public/style_images/master/topic_button.png) repeat-x top;
	color: #fff;
	-moz-border-radius: 3px;
	-webkit-border-radius: 3px;
	border-radius: 3px;
	-moz-box-shadow: inset 0 1px 0 0 #5c5c5c, 0px 2px 3px rgba(0,0,0,0.2);
	-webkit-box-shadow: inset 0 1px 0 0 #5c5c5c, 0px 2px 3px rgba(0,0,0,0.2);
	box-shadow: inset 0 1px 0 0 #5c5c5c, 0px 2px 3px rgba(0,0,0,0.2);
	border-color: #323232;}

.ipsBox_container {
 	background: #191919;
	border: 0px;}

.ipsButton_secondary {
	background: #212121; 
	border: 1px solid #4b4b4b; 
	box-shadow: none;
  	color: #ccc;}

#board_index .maintitle {
  background: #292929 url(http://i.imgur.com/BXTBhES.png) repeat-x top;}

#logo h1 a span {
  background: #292929 url(http://i.imgur.com/cJjtd9W.png) no-repeat 50%; /* endrer logoen til en mørkere utgave */}

#community_app_menu>li.active>a {
	color: #fff; /* endrer tekstfarge på aktiv meny */
	background: #2c5687; /* endrer bakgrunnsfarge på aktiv meny */}

#jobsFeed {
	display: none;}

.cke_skin_ips .cke_wrapper.minimized {
	opacity: 0.05 !important;}

/** fix hwskin **/
#wrapper {
        background: #202020 !important;}

.tek-nav[role=navigation] li > a {
        color: #ccc !important;}
   
/** fix shadow **/
#user_link_menucontent #links li,
.ipsHeaderMenu {
	text-shadow: none;}
  
.popupInner h3 {
	box-shadow: none;}

/** fix borders **/ 
#header input[type=search],
footer[role=contentinfo],
select, 
textarea, 
input[type="text"], 
input[type="password"], 
input[type="datetime"], 
input[type="datetime-local"], 
input[type="date"], 
input[type="month"], 
input[type="time"], 
input[type="week"], 
input[type="number"], 
input[type="email"], 
input[type="url"], 
input[type="search"], 
input[type="tel"], 
input[type="color"],
isindex, #search_wrap,
table.ipb_table td,
blockquote.ipsBlockquote,
p.citation,
footer[role=contentinfo],
.message,
.statistics,
.post_block,
.signature,
.message.unspecific,
.message.error,
.ipbmenu_content, 
.ipb_autocomplete,
.ipbmenu_content li, 
.ipb_autocomplete li,
.popupInner h3,
.popupInner,
.input_text, 
.ipsTagBox_wrapper,
.ipsVerticalTabbed_tabs li,
.ipsVerticalTabbed_tabs li.active a,
.ipsVerticalTabbed_tabs>ul,
.ipsSideMenu ul li,
.ipsSettings_section,
.progress_bar.topic_poll {
	border-color: #323232;}
 
blockquote.ipsBlockquote,
p.citation {
	border: 1px solid #323232;
	border-left: 1px solid #323232;}

/** #000 **/
.ipsUserPhoto {
	border: none; 
	background: #000;}
  
/** #121212 **/
#content {
	background: #121212;}

/** #1a1a1a **/
p.rep_highlight,
p.citation,
.unread .altrow,
.header,
.ipbmenu_content a:hover,
.post_wrap,
.post_block,
.popupInner h3,
.input_text, 
.ipsTagBox_wrapper,
.surround {
	background: #1a1a1a;}

/** #202020 **/
blockquote.ipsBlockquote,
#forumList li ol,
#forumList li div,
#primary_nav.feedBox,
.ipsBadge_green,
.row2, 
.post_block.row2,
.ipbmenu_content, 
.ipb_autocomplete,
.unread,
.popupInner,
.ipsHeaderMenu,
.general_box,
.ipsVerticalTabbed_tabs li a,
.ipsSideBlock,
.no_messages,
.progress_bar,
.poll_question ol {
	background: #202020;}

/** #282828 **/
html, 
body,
select, 
input, 
input[type="password"], 
input[type="search"], 
isindex, #search_wrap,
body#ipboard_body fieldset.submit, 
body#ipboard_body p.submit,
#folder_list .total,
#user_latest_status>div,
.ipsVerticalTabbed_tabs li.active a,
.general_box h3,
.cke_skin_ips .cke_contents iframe,
.feedBox, 
.message,
.ipsFilterbar li.active a,
.pagination .back a, 
.pagination .forward a,
.row1, 
.post_block.row1,
.awesome-forum-button a,
.post_block h3,
.message.unspecific,
.ipsSideBlock h3,
.ipsVerticalTabbed_tabs li,
.poll_question h4,
.post_ignore {
	background: #282828;}

/** #6f3636 **/
body .moderated td, 
body td.moderated, 
body td.moderated,
.moderated, 
.moderated td.altrow, 
.post_block.moderated,
.moderated .row2,
.message.error {
    background-color: #6f3636;}

/** #d27777 **/
.moderated, .moderated a {
    color: #d27777;}

/** transparent **/
#header input[type=search],
#search_options,
#portalWrapper,
.miInnstillinger,
.ipsBox {
	background: transparent;}

/***** font color ******************************************/

/** #ccc **/
html, 
body,
p.rep_highlight,
#community_app_menu>li>a:hover,
#forumList a:hover,
#user_latest_status>div,
.ipsList_data .row_title, 
.ipsList_data .ft,
.ipsList_data .row_data,
.general_box h3,
.awesome-forum-button a,
.ipsType_pagetitle, 
.ipsType_subtitle,
.sites li a:hover,
.col_f_content h4 a,
.header,
.row1 a, 
.post_block.row1 a,
.row2 a, 
.post_body .post,
.post_block.row2 a,
.topic_list td h4 a,
.ipb_table td h4 a,
.message.error,
.ipsVerticalTabbed_tabs li.active a,
.ipsSideMenu h4,
.ipsSideBlock h3,
.ipsSettings_sectiontitle,
.no_messages,
.ipsType_textblock,
.forums-awesome-icons h3 a span,
.info dt,
.poll_question h4,
.poll_question li {
	color: #ccc;}

div > div.post_body > div.post.entry-content > blockquote > span {
        color: #ccc !important;}

/** #b2b2b2 **/
#forumList a,
#community_app_menu>li>a,
.message,
.bbc_url,
.sites li a,
.last_post span, 
.last_post a {
	color: #b2b2b2;}

/** #777 **/
select, 
input, 
input[type="password"], 
input[type="search"], 
isindex, #search_wrap,
#forumTopStories li a,
.desc.lighter, 
.desc.lighter.blend_links a,
.topic h4 a, 
.input_submit,
.message.unspecific {
	color: #777;}
	
#copyright, 
#ipsDebug_footer {
	text-shadow: none; 
	color: #777;}

/** #2f8cb7 **/
ul.post_controls a,
.col_f_content h4 a:hover,
.short a,
.topic_list td h4 a:hover,
.ipb_table td h4 a:hover,
.input_submit.alt:hover,
.ipsButton_secondary:hover {
	color: #2f8cb7;}

ul.mini_pagination li a { 
	background: none; 
	border: 0px; 
	color: #4e7da8;}

body.network-tek {
    background-color: #282828;
}
.network-tek .row2, .network-tek .post_block.row2, .network-tek .post_block h3 {
    background: #282828;
}
.network-tek .ipsBox {
    background: 000000;
}
.network-tek .ipsBox_container {
    border: 1px solid #282828;
}
.network-tek table.ipb_table td, .network-tek .post_block {
    border-bottom: 1px solid #282828;
}
.network-tek a {
    color: #ccc;
}

.ad-placeholder.ad-top-ad {
    height: 0px;
}}
