css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("8ch.net") {
body {
  background:#1d1f21 none;
  color:#C5C8C6
}
h1,div.subtitle {
  color:#C5C8C6!important
}
a:link,a:visited,p.intro a.email span.name {
  color:#81a2be
}
a:link:hover {
  color:#5F89AC
}
a.post_no {
  color:#C5C8C6
}
a.post_no:hover {
  color:#5F89AC!important
}
div.banner {
  background-color:#1d1f21
}
div.post.reply {
  background-color:#282a2e;
  border:1px solid #282a2e;
  margin-bottom:2px;
  margin-left:16px;
  margin-top:2px
}
div.post.reply.highlighted {
  background-color:#1d1d21;
  border:1px solid #111
}
div.post.reply div.body a {
  color:#81a2be
}
div.post.reply div.body a:hover {
  color:#5F89AC
}
div.post-hover {
  border:1px solid #000!important;
  box-shadow:none!important
}
.theme-catalog div.thread:hover {
  background-color:#1d1f21;
  border-color:#111
}
p.intro span.subject {
  color:#b294bb
}
p.intro span.name {
  color:#C5C8C6
}
span.quote {
  color:#adbd68
}
span.heading {
  color:#F20
}
form table tr th {
  background:#282a2e;
  border:1px solid #111;
  color:#C5C8C6
}
div.ban h2 {
  background:#FCA;
  color:inherit
}
div.ban {
  border-color:#800
}
div.ban p {
  color:#000
}
div.pages {
  background:#1d1f21;
  border-color:#1d1f21
}
div.pages a.selected {
  color:#81a2be;
  font-weight:700
}
div.boardlist {
  background-color:#282a2e!important;
  color:#C5C8C6
}
div.boardlist:nth-of-type(1) {
  border-bottom:1px solid #111!important;
  box-shadow:0 0 3px 0 #111
}
div.boardlist a {
  color:#81a2be
}
hr {
  background-color:#282a2e;
  border:0;
  height:1px
}
div#options_div {
  background-color:#282a2e
}
div.options_tab_icon {
  color:#AAA
}
div.options_tab_icon:hover {
  background-color:#111
}
div.options_tab_icon.active {
  color:#F20
}
div.blotter {
  color:#F20
}
span.omitted {
  color:#707070
}
p.intro a,span.omitted a {
  text-decoration:none
}
form#quick-reply {
  padding-right:1px;
  border: 1px solid #111
}
span.capcode {
  background-color: #000;
  padding:2px 5px;
  border-radius: 10px
}
div#watchlist,div#alert_div {
  border:1px solid #111;
  background-color:#282a2e
}
div#watchlist a,a.watchThread {
  color:#81a2be;
  text-decoration:none
}
div#watchlist a:hover,a.watchThread:hover {
  color:#5F89AC
}

/* Remove the following parts if those annoy you */
/* Keep small thumbnails in replies */




/*=========================================================











============================================================*/

a > img[src="/static/spoiler.png"].post-image {
  background: none !important;
  background-color: rgba(22, 24, 30, .6) !important;
  }

div[id^="post-hover-"].post.reply.post-hover {
  background: #111317 !important;
  background-color: #111317 !important;
  min-width: 200px !important;
  width: auto !important;
}


div.post.reply {

/*[[MinPostSize]]*/

/*
  min-width: 50% !important;
  max-width: 62% !important;
*/
}



span.spoiler:hover {
  color: #cacaca !important;
  background-color: transparent !important;
}

span.spoiler {
  color: #acacac !important;
}

div.mix > div.thread:hover {
  background: none !important;
  background-color: #111317 !important;
  border-color: #8f8f8f !important;
}

.grid-size-vsmall {
  max-width: 134px !important;
  max-height: 196px !important;
}


td > input {
  background-color: #282a2e !important;
  color: #cacaca !important;
}

td > textarea#body {
  background-color: #282a2e !important;
  color: #cacaca !important;
}

td > select#user_flag {
  background-color: #282a2e !important;
  color: #cacaca !important;
}

#pRep {
  margin-top: 136px !important;
  margin-bottom: 90px !important;
}

#pRep > div.post.reply {
  display: inline-block !important;
}


#pRep:first-child div.post.reply {
  margin-left: 128px !important;
  
}

#expand-all-images {
  text-align:left !important;
}

.hideme {
  font-size: 8px !important;
  text-decoration: none !important;
  color: rgba(10,12,18,0.5) !important;
}

div.boardlist:nth-child(1) {
  position: absolute !important;
}

::selection {
    color: #acacac !important;
    background: #22282c !important;
}


#options_div {
  min-height: 80% !important;
  min-width: 80% !important;
  max-height: 80% !important;
  max-width: 90% !important;

}

div.options_tab:nth-child(6) > textarea:nth-child(2), div.options_tab:nth-child(5) > textarea:nth-child(2) {
  min-height: 75% !important;
  min-width: 75% !important;
  background-color: #24272d !important;
  color: #8f8f8f !important;
}


.post-hide-link {
  display: none !important;
}
code > pre.prettyprint {
  width: unset !important;
  height: unset !important;
  display: inline-block !important;
  min-width: 280px !important;
  min-height: 120px !important;
  max-height: 720px !important;
  max-width: 75% !important;
  background-color: #222 !important;
  
}

.grid-li > a:link > img {
  border-bottom: 3px dashed #000 !important;
  
  
}

.grid-li > a:visited > img  {
  border-bottom: 3px solid #f00 !important;
  
  /*
  padding-top: 4px !important;
  */
}


.post-hover {
  background-color: #111317 !important;
  }

div.post.reply {

  border-color: #24262a !important;
  background-color: #111317 !important;

  background: linear-gradient(to top, #17191a, #080a0e, #080a0e 48px, transparent), linear-gradient(to bottom, #17191a, #080a0e, #080a0e 48px, transparent);
  box-shadow: 1px 1px 1px rgba(02,04,10,.5);
}

div.post.reply.highlighted {
  margin-left: 7.5% !important;
  padding-left: 18px !important;
  padding-right: 18px !important;
  border-color: #34363d !important;
  border-left-color: #3a3c3f !important;
  border-radius: 0px 13px 13px 0px !important;
  background-color: #111317 !important;
  background: linear-gradient(to left, #1a1c1f, #080a0e, #080a0e 48px, transparent 50%), linear-gradient(to right, #27292f, #080a0e 18px, transparent 50%) !important;
  box-shadow: 1px 1px 2px rgba(02,04,10,.5);

}
input {
  opacity: .5 !important;
}
.desktop-style {
  background-color: #111317 !important;
  background: #111317 !important;
 
}



 .boardlist.bottom {
  background-color: #111317 !important;
}


div.boardlist {
 position: absolute !important;
}

div.styles {
  margin-top: 400px !important;
  background: none !important;
  background-color: transparent !important;
}
  
div#nullreply.post {
  margin: 2px !important;
}

div.post.reply:hover {
  border-color: #6f7177 !important;
}

.post.reply a:not([data-expanded="true"]) .post-image {
  width: auto!important;
  height: auto!important;
  max-width: 600px;
  max-height: 300px;
}


div[style="float:left;width:300px;height:250px"] {
  display: none !important;
}


.thread, body {
 background: #111317 !important; 
 background-color: #111317 !important;
}

#quick-reply {
  background: none !important;
  background-color: rgba(1,2,7,0.5) !important;
}

.post.reply.post-hover {
    background: #111317 !important;
    background-color: #111317 !important;
}

}
