css:
@-moz-document domain(theanarchistlibrary.org) {
  body{background:rgb(25,25,25)}
  body, .dropdown-menu > li > a{color:rgb(90,90,90)}
  .navbar-default, div#footer, .modal-content, .dropdown-menu, .navbar-default .navbar-nav > .open > a{background:rgb(40,40,40); border-color:rgb(30,30,30)!important}
  .well, .list-group-item, .form-control, .pagination > li > a, .btn-default{background:rgb(35,35,35); border-color:rgb(30,30,30)}
  .btn-default{color:rgb(90,90,90)!important; text-shadow:none!important;}
  .dropdown-menu > li > a:hover, .navbar-default .navbar-nav > .open > a:focus,
  .list-group-item.clearfix:hover, .list-group-item.clearfix:focus,
  a.list-group-item:hover, a.list-group-item:focus,
  .pagination > li > a:hover, .pagination > li > a:focus,
  .btn-default:hover, .btn-default:active, .btn-default:focus, .footer
  {
    background:rgb(55,55,55);
    background-color:rgb(55,55,55);
    border-color:rgb(44,44,44);
  }
}
